INSERT INTO `tt_tintuc` (`Ma`, `Ten`, `MaDanhMuc`, `KeyWord`, `Descr`, `NgayTao`, `TrangThai`, `NoiDung`, `Hinh`, `Alias`, `TacGia`) VALUES
(1, 'Du học châu Mỹ', '7', '', '', '0000-00-00', 1, '<div style=\"text-align: center;\"><span style=\"font-size:36px;\"><span style=\"color:#FF0000;\"><u><em><strong>10 TRƯỜNG PHỔ THÔNG TỐT NHẤT Ở MỸ :</strong> </em> </u> </span> </span></div>\r\n\r\n<p></p>\r\n<br />\r\n<br />\r\n \r\n<h4><span style=\"color:#0000FF;\"><strong>1. <span style=\"font-size:20px;\">Edgemont Union Free School District, Greenburgh, New York.</span> </strong> </span></h4>\r\n\r\n<p><img alt=\"EDGEMONT_UNION_FREE_SCHOOL_DISTRICT\" src=\"public/Images/EDGEMONT_UNION_FREE_SCHOOL_DISTRICT.png\" /><br />\r\n </p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Một trong những trường xếp vào hạng top về chất lượng đào tạo.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Học sinh có thể tham gia vào các câu lạc bộ âm nhạc, nghệ thuật, nhóm cộng đồng…</span></li>\r\n</ul>\r\n<span style=\"font-size:18px;\">​​​​​​​</span>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Các chương trình thể thao được đầu tư, học sinh có thể tham gia vào tất cả các môn yêu thích như bóng chuyền, bóng đá…</span></li>\r\n</ul>\r\n\r\n<p></p>\r\n\r\n<h4><span style=\"color:#0000FF;\"><span style=\"font-size:20px;\"><strong>2. Jericho Union Free School District, Oyster Bay Town, New York</strong> </span> </span></h4>\r\n\r\n<p><img alt=\"JERICHO_UNION_FREE_SCHOOL_DISTRICT\" src=\"public/Images/JERICHO_UNION_FREE_SCHOOL_DISTRICT.png\" /><br />\r\n </p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Theo bảng kết quả khảo sát phụ huynh và học sinh thì trường được xếp thứ hạng 4.4 đến trên 5.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Chất lượng giáo dục được đầu tư kỹ lưỡng để học sinh sau khi tốt nghiệp có được nền tảng vững vàng trước khi vào đại học hoặc chuẩn bị đi làm.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Có nhiều hoạt động trong trường dành cho học sinh và phụ huynh..</span></li>\r\n</ul>\r\n \r\n\r\n<h4><span style=\"color:#0000FF;\"><span style=\"font-size:20px;\"><strong>3. Tredyffrin-Easttown School District, Tredyffrin Township, Pennsylvania</strong> </span> </span></h4>\r\n \r\n\r\n<p><img alt=\"TREDYFFRIN-EASTTOWN_SCHOOL_DISTRICT\" src=\"public/Images/TREDYFFRIN-EASTTOWN_SCHOOL_DISTRICT.png\" /><br />\r\n </p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Trường này được xếp thứ hạng từ 4.3 đến trên 5.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Các giáo viên tại đây là một trong những người tốt nhất cả nước và nhận đánh giá rất cao từ học sinh và và phụ huynh.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Hoạt động ngoại khóa tại trường cũng được khuyến khích đầu tư.</span></li>\r\n</ul>\r\n\r\n<p></p>\r\n\r\n<h4><span style=\"color:#0000FF;\"><span style=\"font-size:20px;\"><strong>4. Lower Merion School District, Lower Merion, Pennsylvania</strong> </span> </span></h4>\r\n\r\n<p><img alt=\"LOWER_MERION_SCHOOL_DISTRICT\" src=\"public/Images/LOWER_MERION_SCHOOL_DISTRICT.png\" /><br />\r\n<br />\r\n </p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Nếu bạn đang tìm kiếm một ngôi trường có nhiều nền văn hóa đa dạng, trường này là một lựa chọn tối ưu.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Trường được trang bị cơ sở vật chất và nguồn tài liệu đầy đủ để học sinh và phụ huynh có thể tham khảo.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Sức khỏe và sự an toàn của học sinh là sự ưu tiên hàng đầu của trường.</span></li>\r\n</ul>\r\n\r\n<p></p>\r\n\r\n<h4><span style=\"color:#0000FF;\"><span style=\"font-size:18px;\"><strong>5. Scarsdale Union Free School District, Scarsdale, New York</strong> </span> </span></h4>\r\n \r\n\r\n<p><img alt=\"SCARSDALE_UNION_FREE_SCHOOL\" src=\"public/Images/SCARSDALE_UNION FREE_SCHOOL_DISTRICT.png\" /><br />\r\n </p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"font-size:18px;\">Tại trường này học sinh luôn được nhận nền giáo dục tốt nhất.</span><br />\r\n	 </p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-size:18px;\">Có nhiều sự hỗ trợ cần thiết cho học sinh như: dạy kèm, tư vấn và các dịch vụ khác.</span><br />\r\n	 </p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-size:18px;\">Trường này không phải là trường tốt nhất cho việc hoạt động các môn thể thao nhưng sẽ có nhiều hoạt động ngoại khóa cho học sinh tham gia.</span><br />\r\n	<br />\r\n	 </p>\r\n	</li>\r\n</ul>\r\n\r\n<h4><span style=\"color:#0000FF;\"><span style=\"font-size:20px;\"><strong>6. Great Neck Union Free School District, North Hempstead Town, New York</strong> </span> </span></h4>\r\n\r\n<p><img alt=\"GREAT_NECK_UNION_FREE_SCOOL\" src=\"public/Images/GREAT_NECK_UNION FREE_SCHOOL_DISTRICT.png\" /><br />\r\n </p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Các giáo viên tại trường được đánh giá cao về mặt kiến thức, sự nhạy cảm cần thiết để giao tiếp với học sinh của họ.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Hoạt động ngoại khóa được đánh giá cao.</span></li>\r\n</ul>\r\n\r\n<p></p>\r\n\r\n<h4><span style=\"color:#0000FF;\"><span style=\"font-size:20px;\"><strong>7. Pittsford Central School District, Pittsford Town, New York</strong> </span> </span></h4>\r\n\r\n<p><img alt=\"PITTSFORD_CENTRAL_SCHOOL_DISTRICT\" src=\"public/Images/PITTSFORD_CENTRAL_SCHOOL_DISTRICT.png\" /><br />\r\n </p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Thứ hạng của trường được đánh giá ở mức 4.4 -5.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Học sinh vào trường sẽ được trang bị một nền tảng giáo dục tốt để có cơ hội vào các trường đại học tốt nhất nước Mỹ và những trường khác trên thế giới.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Trường cũng khuyến khích sự đa dạng văn hóa .</span></li>\r\n</ul>\r\n\r\n<p></p>\r\n\r\n<h4><span style=\"font-size:20px;\"><span style=\"color:#0000FF;\"><strong>8. Rye City School District, Rye, New York</strong> </span> </span></h4>\r\n\r\n<h4><img alt=\"RYE CITY_SCHOOL_DISTRICT\" src=\"public/Images/RYE CITY_SCHOOL_DISTRICT.png\" /></h4>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Thứ hạng được đánh giá của trường là 4.6 – 5.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Rất nhiều nguồn lực để phụ vụ nhu cầu học sinh: tài liệu học tập, các thiết bị thể thao, vui chơi giải trí…</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">So với các trường khác, trường này không có nhiều sự đa dạng văn hóa.</span></li>\r\n</ul>\r\n\r\n<p><br />\r\n </p>\r\n\r\n<h4><span style=\"color:#0000FF;\"><span style=\"font-size:20px;\"><strong>9. North Allegheny School District, McCandless Township, Pennsylvania</strong> </span> </span></h4>\r\n \r\n\r\n<p><img alt=\"NORTH_ALLEGHENY_SCHOOL_DISTRICT\" src=\"public/Images/NORTH_ALLEGHENY_SCHOOL_DISTRICT.png\" /><br />\r\n </p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Xếp hạng từ 4.3 – 5.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Trường này được nhận toàn bộ điểm A+ từ chất lượng giáo dục cho đến các hoạt động ngoại khóa.</span></li>\r\n</ul>\r\n\r\n<p></p>\r\n\r\n<h4><span style=\"color:#0000FF;\"><span style=\"font-size:20px;\"><strong>10. Chappaqua Central School District, New Castle Town, New York</strong> </span> </span></h4>\r\n \r\n\r\n<p><img alt=\"CHAPPAQUA_CENTRAL_SCHOOL_DISTRICT\" src=\"public/Images/CHAPPAQUA_CENTRAL_SCHOOL_DISTRICT.png\" /><br />\r\n </p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Học sinh được chuẩn bị đầy đủ cho kỳ thi SAT, học sinh ở đây thường có điểm số cao trong kỳ thi này.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Hoạt động ngoại khóa cũng luôn được chú trọng trong trường.</span></li>\r\n</ul>\r\n\r\n<p></p>\r\n\r\n<h4><span style=\"font-size:20px;\"><span style=\"color:#0000FF;\"><strong>11. Dresden School District, Hanover Town, New Hampshire</strong> </span> </span></h4>\r\n\r\n<p><img alt=\"DRESDEN_SCHOOL_DISTRICT\" src=\"public/Images/DRESDEN_SCHOOL_DISTRICT.png\" /><br />\r\n </p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Không những cung cấp chất lượng giáo dục chuẩn quốc tế mà trường còn là một trong những địa điểm đẹp nhất tại Mỹ.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Phụ huynh và học sinh có thể mong đợi tìm thấy những người giáo viên xem trọng sự thành công của học trò.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Các hoạt động ngoài trời luôn được chú trọng: trượt tuyết vào mùa đông, bơi lội vào mùa hè rất phổ biến, New England được cho là cụm trường thể thao ấn tượng tại Mỹ.</span></li>\r\n</ul>\r\n \r\n\r\n<h4><span style=\"color:#0000FF;\"><span style=\"font-size:20px;\"><strong>12. Adlai E. Stevenson High School District No. 125, Vernon Township, Illinois</strong> </span> </span></h4>\r\n\r\n<p><img alt=\"ADLAI E._STEVENSON_HIGH_SCHOOL_DISTRICT\" src=\"public/Images/ADLAI E._STEVENSON_HIGH_SCHOOL_DISTRICT.png\" /><br />\r\n<br />\r\n </p>\r\n\r\n<ul>\r\n	<li>\r\n	<h4><span style=\"font-size:18px;\">Trường này có sự kết hợp tuyệt vời giữa giáo dục và hoạt động ngoại khóa để học sinh có một nền tảng kiến thức đủ vững và kỹ năng đủ tốt trước khi ra trường.</span></h4>\r\n	</li>\r\n</ul>\r\n\r\n<p></p>\r\n\r\n<h4><span style=\"color:#0000FF;\"><span style=\"font-size:20px;\"><strong>13. Manhasset Union Free School District, North Hamstead Town, New York</strong> </span> </span></h4>\r\n\r\n<p><img alt=\"MANHASSET_UNION FREE_SCHOOL_DISTRICT.png\" src=\"public/Images/MANHASSET_UNION FREE_SCHOOL_DISTRICT.png\" /><br />\r\n </p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Giáo viên luôn chú trọng đến công tác giảng dạy, trường luôn đầu tư vào chất lượng giáo dục phục vụ nhu cầu học sinh như công tác dạy kèm, tư vấn.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Trường có sự đa dạng văn hóa, trường chấp nhận mọi nền văn hóa và tôn giáo.</span></li>\r\n</ul>\r\n\r\n<p></p>\r\n\r\n<h4><span style=\"color:#0000FF;\"><span style=\"font-size:20px;\"><strong>14. San Marino Unified School District, San Marino, California</strong> </span> </span></h4>\r\n \r\n\r\n<p><img alt=\"SAN_MARINO_UNIFIED_SCHOOL_DISTRICT\" src=\"public/Images/SAN_MARINO_UNIFIED_SCHOOL_DISTRICT.png\" /><br />\r\n </p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Trường được đầu tư về giáo dục lẫn hoạt động ngoại khóa.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Nếu bạn đang lo lắng khi gửi con của mình đến các trường học tại California, bạn nên suy nghĩ lại vì trường này cung cấp các khu vực an toàn để sinh sống và học tập.</span></li>\r\n</ul>\r\n\r\n<p></p>\r\n\r\n<h4><span style=\"color:#0000FF;\"><span style=\"font-size:20px;\"><strong>15. Unionville-Chadds Ford School District, West Marlborough Township, Pennsylvania</strong> </span> </span></h4>\r\n \r\n\r\n<p><img alt=\"UNIONVILLE-CHADDS_FORD_SCHOOL_DISTRICT\" src=\"public/Images/UNIONVILLE-CHADDS_FORD_SCHOOL_DISTRICT.png\" /><br />\r\n </p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Thứ hạng được xếp của trường này là 4.2 – 5.</span></li>\r\n</ul>\r\n<span style=\"font-size:18px;\">​​​​​​​</span>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Tài nguyên phong phú được cung cấp đến các học sinh, các buổi học, tư vấn tâm lý đời sống, tư vấn nghề nghiệp và nhiều hoạt động khác cũng được đầu tư chú trọng.</span></li>\r\n</ul>\r\n\r\n<p></p>\r\n\r\n<p></p>\r\n', '', 'du-hoc-chau-my', NULL),
(2, 'Châu Âu', '8', '', '', '0000-00-00', 1, '<h2 style=\"text-align: center;\"></h2>\r\n\r\n<h2><span style=\"color:#FF0000;\"><span style=\"font-size:36px;\"><em><u><strong>6 NƯỚC DU HỌC LÝ TƯỞNG Ở CHÂU ÂU</strong> </u> </em> </span> </span></h2>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Du học là một trong những cách thú vị để giúp một người trưởng thành. Bạn có thể vừa học, vừa du lịch, biết thêm một ngôn ngữ mới và thấy được thế giới rộng lớn thế nào. Châu Âu chắc chắn là một lựa chọn lý tưởng.</span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Sau đây là 6 quốc gia ở châu Âu lý tưởng để du học. Học phí và các khoản sinh hoạt cũng phải chăng so với mặt bằng chung của châu Âu.</span></li>\r\n</ul>\r\n\r\n<p></p>\r\n\r\n<p></p>\r\n\r\n<h4><span style=\"color:#0000FF;\"><span style=\"font-size:22px;\"><strong>Hà Lan</strong> </span> </span></h4>\r\n\r\n<p><img alt=\"du-hoc-ha-lan\" src=\"public/Images/du-hoc-ha-lan.jpg\" /><br />\r\n<br />\r\n<span style=\"font-size:18px;\"> Hà Lan là đất nước xinh đẹp nằm dưới mức nước biển, do vậy xuyên suốt khắp các thành phố là hệ thống kênh rạch dày đặc tạo nên khung cảnh đặc trưng của nơi đây. Hà Lan là quê hương của hoa Tuy-líp, nổi tiếng với những cành đồng hoa và cối xay gió. Xe đạp là phương tiện di chuyển chính mang lại cho đất nước này một bầu không khí trong lành hiếm có. Đặc biệt thủ đô Amsterdam được xếp hạng là một trong 5 thành phố xanh đáng sống nhất thế giới.</span><br />\r\n<br />\r\n<strong><span style=\"color:#FF0000;\"><span style=\"font-size:20px;\">Thế mạnh khi du học tại Hà Lan</span> </span> </strong></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><span style=\"color:#A52A2A;\"><strong>Về chất lượng giáo dục:</strong> </span> Hệ thống giáo dục bậc cao ở Hà Lan đứng thứ 7 thế giới theo bình chọn của Universitas 21. Du học tại đất nước này sinh viên có thể lựa chọn học tại các trường ranking cao nằm trong top 100 thế giới.</span></li>\r\n	<br />\r\n	<li><span style=\"font-size:18px;\"><span style=\"color:#B22222;\"><strong> Về học phí</strong> <strong>:</strong> </span> Chi phí học tập và sinh hoạt tại Hà Lan chỉ rẻ bằng một nửa hoặc 1/3 so với các trường có cùng thứ hạng ở Anh, Mỹ hoặc Úc.  Học phí mỗi năm: hơn 2.100 USD (khoảng 48 triệu đồng).</span></li>\r\n	<br />\r\n	<li><span style=\"font-size:18px;\"><span style=\"color:#B22222;\"><strong>Sinh hoạt ở Hà Lan :</strong> </span> cũng khá thấp so với mặt bằng chung châu Âu. Đất nước này nổi tiếng với âm nhạc, phong cảnh đẹp và văn hóa đi xe đạp.</span></li>\r\n	<br />\r\n	<li><span style=\"font-size:18px;\"><span style=\"color:#B22222;\"><strong>Về thủ tục visa</strong> </span> <strong> <span style=\"color:#B22222;\">:</span> </strong>Du học Hà Lan không cần chứng minh nguồn thu nhập ngoại trừ một số tiết kiệm khoảng 10.000 Euro. Không có visa trượt do trường là đơn vị đứng ra làm các thủ tục. </span></li>\r\n	<br />\r\n	<li><span style=\"font-size:18px;\"><span style=\"color:#B22222;\"><strong>Về chế độ định cư</strong> </span> <strong> <span style=\"color:#B22222;\">:</span> </strong>Sau khi kết thúc khóa học , sinh viên được tự động gia hạn visa 12 tháng để tìm việc làm. Nếu tìm được công việc thu nhập khoảng 27.000 Euro/ năm,  sinh viên sẽ được cấp visa 5 năm ở lại làm việc và định cư hợp pháp tại đất nước này.</span></li>\r\n</ul>\r\n\r\n<p></p>\r\n\r\n<h4><span style=\"color:#0000FF;\"><span style=\"font-size:22px;\"><strong>Đức</strong> </span> </span></h4>\r\n\r\n<p><img alt=\"du-hoc-duc\" src=\"public/Images/du-hoc-duc.jpg\" /><br />\r\n<br />\r\n<span style=\"font-size:18px;\">Điều gì khiến nước Đức trở thành một nơi du học tuyệt vời? Tọa lạc tại trung tâm Châu Âu và hoàn toàn miễn tiền học phí, nước Đức hứa hẹn sẽ là một điểm đến du học lí tưởng cho những ai đang có kế hoạch đi học ở xứ người.</span><br />\r\n<br />\r\n<span style=\"color:#FF0000;\"><span style=\"font-size:20px;\"><strong>Thế mạnh khi du học Đức</strong> </span> </span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><span style=\"color:#B22222;\"><strong>Miễn phí học phí:</strong> </span>  Phần lớn các trường đại học ở Đức miễn học phí cho sinh viên, kể cả sinh viên quốc tế. Đây là nơi lý tưởng để các bạn trẻ muốn du học với chi phí thấp.</span></li>\r\n	<br />\r\n	<li><span style=\"font-size:18px;\"><strong><span style=\"color:#B22222;\">Cơ hội nghề nghiệp:</span> </strong>Sau khi tốt nghiệp, sinh viên quốc tế được ở lại Đức lên đến 18 tháng để tìm việc làm.  </span></li>\r\n	<br />\r\n	<li><span style=\"font-size:18px;\"><strong><span style=\"color:#B22222;\">Cơ sở đào tạo chất lượng cao:</span> </strong> Theo bảng khảo sát Times Higher Rankings, <a href=\"https://www.hotcourses.vn/study/germany/school-college-university/ludwig-maximilian-university-of-munich/417546/international.html\" target=\"_blank\">trường LMU Munich</a> được xếp hạng thứ 29 trên toàn thế giới; <a href=\"https://www.hotcourses.vn/study/germany/school-college-university/university-of-heidelberg/417654/international.html\" target=\"_blank\">Heideberg University</a> xếp hạng 37 và Humboldt University of Berlin xếp thứ 49 cùng nhiều cơ sở đào tạo khác cũng được vinh danh trong tốp 100 trường đại học tốt nhất thế giới. </span></li>\r\n	<br />\r\n	<li><span style=\"font-size:18px;\"><strong><span style=\"color:#B22222;\">Chất lượng cuộc sống:</span>  </strong>Với nền kinh tế phát triển và cơ sở hạ tầng hiện đại, phương tiện đi lại tại Đức được đầu tư xây dựng với chất lượng cao, tức luôn an toàn và nhanh chóng. </span></li>\r\n</ul>\r\n\r\n<p></p>\r\n\r\n<p></p>\r\n\r\n<p><span style=\"color:#0000FF;\"><span style=\"font-size:22px;\"><strong>Tây Ban Nha</strong> </span> </span></p>\r\n\r\n<p><img alt=\"du-hoc-tay-ban-nha\" src=\"public/Images/du-hoc-tay-ban-nha.jpg\" /><br />\r\n<br />\r\n<span style=\"font-size:18px;\">Du học đối với các bạn sinh viên không chỉ là việc học tập và mang về một bằng cấp, đó còn là sự trải nghiệm về văn hóa, ẩm thực, là cơ hội sinh viên thiết lập mạng lưới quan hệ cho thời gian sau khi đi làm. Với 3000 năm lịch sử, Tây Ban Nha sở hữu nền văn minh mà không phải đâu cũng có thể tìm thấy, cung điện hoàng gia, bảo tàng lịch sử, các lâu đài tráng lệ cùng những tác phẩm văn học đồ sộ của mọi thời đại sẽ cuốn hút các bạn ngay từ ngày đầu tiên đến Tây Ban Nha.</span><br />\r\n<br />\r\n<strong><span style=\"color:#FF0000;\"><span style=\"font-size:20px;\">Thế mạnh du học Tây Ban Nha</span> </span> </strong></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><span style=\"color:#B22222;\"><strong>Chất lượng giáo dục cao theo tiêu chuẩn Châu Âu:</strong> </span> Bằng cấp <a href=\"http://eduvietglobal.vn/du-hoc-chau-au/du-hoc-tay-ban-nha\">du học Tây Ban Nha</a> được công nhận trên toàn cầu, được đánh giá cao bởi nội dung giảng dạy phong phú nhưng không kém phần chuyên sâu, cơ sở vật chất hiện đại, bắt kịp theo yêu cầu thực tiễn.Chính phủ Tây Ban Nha cho phép du học sinh được làm việc tới 80h trong 1 tháng với rất nhiều công việc làm thêm khác nhau kèm mức lương từ 10 - 12€/h. </span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><span style=\"color:#B22222;\"><strong>Chi phí sinh hoạt dễ chịu :</strong> </span> Chỉ trên dưới 250€ tại những vùng ngoại ô và tầm 500€ tại những thành phố lớn như Barcelona, Madird là bạn đã yên tâm sống với đầy đủ nhu cầu thiết yếu: Ăn uống, thuê nhà, đi lại, điện thoại và internet.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><span style=\"color:#B22222;\"><strong>Nền chính trị, xã hội ổn định, kinh tế phát triển bền vững : </strong> </span>Tại Châu Âu, Tây Ban Nha là một quốc gia EU có tốc độ tăng trưởng thương mại mạnh mẽ, đi kèm với đó là nền chính trị ổn định, không gặp bất ổn hay nội chiến dù có bất đồng quan điểm giữa các đảng phái chính trị. </span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><strong><span style=\"color:#B22222;\">Chính sách làm thêm cởi mở :</span> </strong> Chính phủ Tây Ban Nha cho phép du học sinh được làm việc tới 80h trong 1 tháng với rất nhiều công việc làm thêm khác nhau kèm mức lương từ 10 - 12€/h. </span></li>\r\n</ul>\r\n\r\n<p></p>\r\n\r\n<h4><span style=\"color:#0000FF;\"><span style=\"font-size:22px;\"><strong>Ba Lan</strong> </span> </span></h4>\r\n\r\n<p><img alt=\"du-hoc-ba-lan\" src=\"public/Images/du-hoc-ba-lan.jpg\" /><br />\r\n<br />\r\n<span style=\"font-size:18px;\">Lựa chọn du học tại Ba Lan, ngoài việc sẽ được học tập trong 1 hệ thống giáo dục chất lượng cao, chi phí sinh hoạt tương đối thấp, các bạn du học sinh còn được hưởng rất là nhiều ưu đãi khi học tập tại Ba Lan.</span><br />\r\n<br />\r\n<strong><span style=\"color:#FF0000;\"><span style=\"font-size:20px;\">Thế mạnh du học Tây Ban Nha</span> </span> </strong></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><span style=\"color:#B22222;\"><strong>Chi phí học tập và sinh hoạt tương đối thấp :</strong> </span> So với mặt bằng chung các nước khác thì bạn chỉ cần khoảng 200 USD – 400 USD là có thể trang trải sinh hoạt tại đất nước Ba Lan. Bạn chỉ cần nộp khoảng từ 1.300 Euro – 2.500 Euro cho mỗi năm học với chương trình học tiếng Anh.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><strong><span style=\"color:#B22222;\">Vấn đề an ninh được đảm bảo an toàn :</span> </strong> Ba Lan là nước hàng năm có hàng triệu lượt khách đến du lịch, tình hình an ninh an toàn và ổn định hơn các nước khác trong khối liên minh Châu Âu EU, tỉ lệ tội phạm ở đây rất thấp, an ninh đảm bảo cho các bạn du học sinh tại Ba Lan.</span></li>\r\n</ul>\r\n\r\n<h2></h2>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><strong><span style=\"color:#B22222;\">Bảo hiểm sức khỏe cho du học sinh tại Ba Lan :</span> </strong> Du học sinh được phép truy cập thằng vào hệ thống y tế của Ba Lan và sử dụng thẻ bảo hiểm y tế Châu Âu, miễn là bạn sử dụng tại các trung tâm y tế công cộng hơn là các trung tâm chăm sóc sức khỏe tư nhân.</span></li>\r\n</ul>\r\n<span style=\"font-size:18px;\">​​​​​​​​​​​​​​</span>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><span style=\"color:#B22222;\"><strong>Đào tạo giáo dục chất lượng cao :</strong> </span> Bằng cấp các trường đại học tại Ba Lan được nhiều nước trên thế giới công nhận, giữa Ba Lan và các nước khác có sự liên kết thỏa thuận công nhận bằng cấp.</span></li>\r\n</ul>\r\n\r\n<p></p>\r\n\r\n<p><span style=\"color:#0000FF;\"><span style=\"font-size:22px;\"><strong>Pháp</strong> </span> </span></p>\r\n\r\n<p><img alt=\"du-hoc-phap\" src=\"public/Images/du-hoc-phap.jpg\" /><br />\r\n<span style=\"font-size:18px;\">Pháp là cường quốc hùng mạnh trên thế giới về nhiều lĩnh vực khoa học, công nghệ, xây dựng, y dược,… Được đặt chân đến nước Pháp để sống và học tập là niềm mơ ước của nhiều học sinh và sinh viên trên thế giới.</span><br />\r\n<br />\r\n<strong><span style=\"color:#FF0000;\"><span style=\"font-size:20px;\">Thế mạnh du học Tây Ban Nha</span> </span> </strong></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><strong><span style=\"color:#B22222;\">Chất lượng giáo dục tuyệt vời :</span> </strong>Pháp có hệ thống trường đại học xếp hạng tốt nhất trên thế giới, với các chương trình đào tạo xuất sắc trong nhiều ngành nổi bật như kiến trúc, mỹ thuật, thời trang, kinh tế,… Chất lượng giáo dục trong các trường đại học Pháp đã được rất nhiều các tờ báo và tạp chí chuyên ngành trên thế giới công nhận.</span></li>\r\n</ul>\r\n<span style=\"font-size:18px;\">​​​​​​​</span>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><strong><span style=\"color:#B22222;\">Chất lượng cuộc sống ở Pháp cao :</span> </strong>Pháp có nền kinh tế phát triển với cơ sở hạ tầng tiên tiến và hiện đại. Chất lượng cuộc sống của người dân nơi đây rất cao, nhân quyền luôn được đảm bảo. Với nhiều chính sách tốt dành cho sinh viên, bạn sẽ dễ dàng có dịp tiếp xúc với vô số các hoạt động thể thao, du lịch hoặc văn hóa.</span></li>\r\n</ul>\r\n<span style=\"font-size:18px;\"> <strong>​​​​​​​</strong> </span>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><strong><span style=\"color:#B22222;\">Cơ hội việc làm :</span> </strong>Chi phí học tập và sinh hoạt luôn là một vấn đề quan trọng đối với các bạn du học sinh. Trong khi một số nước có nền giáo dục phát triển đang không ngừng tăng cao mức học phí, cộng thêm chi phí sinh hoạt đắt đỏ, Pháp luôn nỗ lực giảm thiểu các mức học phí cũng như chi phí trong quá trình học tập cho các du học sinh.</span></li>\r\n</ul>\r\n<span style=\"font-size:18px;\"> <strong>​​​​​​​</strong> </span>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><em><strong><span style=\"color:#B22222;\">Cộng đồng sinh viên Việt Nam đông đúc :</span> </strong>Mạng lưới sinh viên Việt Nam ở Pháp vững mạnh với nhiều hoạt động phong phú gắn kết các thế hệ du học sinh và hỗ trợ tân sinh viên làm quen cuộc sống mới. </em>Cộng đồng sinh viên Việt Nam thường trao đổi thông tin về nhà ở để giúp đỡ nhau. </span></li>\r\n</ul>\r\n\r\n<h4><span style=\"color:#0000FF;\"><span style=\"font-size:36px;\">Na Uy</span> </span></h4>\r\n\r\n<p><img alt=\"du-hoc-na-uy\" src=\"public/Images/du-hoc-na-uy.jpg\" /><br />\r\n<br />\r\n<span style=\"font-size:18px;\">Tuy nhiên, để quyết định chọn một đất nước nào đó cho việc du học của mình, các bạn luôn có nhiều câu hỏi và thắc mắc cần một sự giải đáp chính xác nhất. Vậy hãy cùng tham khảo qua những điều phổ biến mà các bạn đang quan tâm khi dự định đi <em>du học Na Uy </em>nhé.</span><br />\r\n<br />\r\n<strong><span style=\"color:#FF0000;\"><span style=\"font-size:20px;\">Thế mạnh du học Tây Ban Nha</span> </span> </strong></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><span style=\"color:#B22222;\"><strong>Miễn học phí :</strong> </span> Na Uy miễn học phí cho sinh viên, kể cả sinh viên quốc tế. Chỉ một số rất ít chương trình chuyên ngành mới thu học phí của sinh viên.</span></li>\r\n</ul>\r\n<span style=\"font-size:18px;\">​​​​​​​</span>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><strong><span style=\"color:#B22222;\">Chất lượng cuộc sống cao :</span> </strong> Dù chi phí sinh hoạt ở Na Uy cao hơn nhiều nước châu Âu nhưng đổi lại nước này có cảnh quan đẹp, rất yên bình và tỷ lệ tội phạm thấp.</span></li>\r\n</ul>\r\n<span style=\"font-size:18px;\">​​​​​​​​​​​​​​</span>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><strong><span style=\"color:#B22222;\">Đa dạng ngành học :</span> </strong> Có khoảng 250 chương trình thạc sĩ và 250 chương trình cử nhân ở Na Uy giảng dạy bằng tiếng Anh.</span></li>\r\n</ul>\r\n', '', 'chau-au', NULL),
(3, 'Châu Úc', '9', '', '', '0000-00-00', 1, '<h2 style=\"text-align: center;\"><span style=\"font-size:36px;\"><span style=\"color:#FF0000;\">Danh sách 8 trường đại học danh tiếng hàng đầu Australia (Úc)</span> </span></h2>\r\n<br />\r\n<br />\r\n<br />\r\n \r\n<h3><span style=\"font-size:24px;\"><strong><span style=\"color:#0000FF;\">Đại học quốc gia Australia</span> <span style=\"color:#0000FF;\"> (Australia National Univesity)</span> </strong> </span></h3>\r\n \r\n\r\n<p><strong><img alt=\"Đại học quốc gia Australia (ANU)\" height=\"350\" src=\"public/Images/dai-hoc-quoc-gia-uc.jpg\" title=\"Đại học quốc gia Australia (ANU)\" /> </strong></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><strong>Australia National Univesity (ANU)</strong> là trường đại học duy nhất của Úc được thành lập bởi Đạo luật Nghị viện liên bang năm 1946, là một trong những trung tâm nghiên cứu hàng đầu tại Úc. Học viện chính của trường nằm tại thủ đô Canberra với diện tích 145 héc-ta bao gồm ký túc xá, phòng thí nghiệm, giảng đường…</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Với nhiều ngành nghề đào tạo phong phú và đa dạng, các bạn SV có thể chọn cho mình như: Kinh doanh, Kinh tế, Luật, Công nghệ thông tin, Khoa học, Nghiên Cứu Châu Á & Thái Bình Dương, Khoa Học Máy Tính & Cơ Khí, Nghệ Thuật & Khoa Học Xã Hội…</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><em>Điều kiện đầu vào:</em> Học sinh Việt Nam tốt nghiệp trung học phổ thông với học lực giỏi trở lên, có IELTS từ 6.5 trở lên (không có kỹ năng nào dưới 6.0)</span></li>\r\n</ul>\r\n\r\n<ol>\r\n</ol>\r\n\r\n<p></p>\r\n\r\n<h3><span style=\"font-size:24px;\"><strong><span style=\"color:#0000FF;\">Đại học Monash</span> <span style=\"color:#0000FF;\"> (Monash University)</span> </strong> </span></h3>\r\n\r\n<ol>\r\n</ol>\r\n\r\n<p><img alt=\"đại học Monash\" src=\"public/Images/Monash-University.jpeg\" title=\"đại học Monash\" /></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><em><strong>Trường đại học Monash </strong>được thành lập vào năm 1958</em> với hơn 50.000 sinh viên đăng ký. Trường hoạt động 6 địa diểm khác nhau ở victoria với các cơ sở tại: <em>Berwick, Caulfield, Peninsula, Clayton, Gippsland  và Parkville </em>được đầu tư trang thiết bị hiện đại, đạt tiêu chuẩn quốc tế.</span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><strong>Monash </strong>giảng dạy rất nhiều chuyên ngành tương thích với sự đổi mới của thời đại qua 10 khoa của trường đó là: Nghệ thuật và thiết kế, Công nghệ thông tin, Văn chương, Luật, Kinh tế và quản trị kinh doanh, Khoa học sức khỏe – điều dưỡng và khoa học, Y học, Giáo dục, Dược, Kĩ sư.</span></li>\r\n</ul>\r\n<span style=\"font-size:18px;\">​​​​​​​</span>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><em>Điều kiện đầu vào:</em> Tốt nghiệp PTTH với điểm trung bình cộng của môn thi tốt nghiệp ít nhất là 8 và 8,5 hoặc 9. Có IELTS từ 6.5, TOEFLiBT 79 (L12, R13, W21, S18), PTE Academic 58 (không có kỹ năng giao tiếp PTE nào dưới 50).</span></li>\r\n</ul>\r\n\r\n<p></p>\r\n\r\n<h3><span style=\"font-size:24px;\"><strong><span style=\"color:#0000FF;\">Đại học Melbourne</span> <span style=\"color:#0000FF;\"> (The University of Melbourne)</span> </strong> </span></h3>\r\n\r\n<p><strong><img alt=\"Đại học Melbourne\" src=\"public/Images/University_of_Melbourne.jpg\" title=\"Đại học Melbourne\" /> </strong></p>\r\n\r\n<ol>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><em><strong>Đại học Melbourne</strong> là một viện đại học công lập ở Melbourne, Victoria,</em> Úc. Được thành lập vào năm 1853, đây là cơ sở giáo dục đại học lâu đời thứ 2 tại Úc và lâu đời nhất tại tiểu bang Victoria. Bên cạnh khuôn viên chính Parkville ngay phía bắc khu trung tâm thành phố, Melbourne còn có nhiều khu trường sở khác nằm rải rác trên toàn tiểu bang.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><strong>Viện Đại học Melbourne </strong>luôn đứng đầu trong số các trường đại học tốt nhất tại Úc và trong nhóm các trường đại học tốt trên thế giới, đặc biệt là trong các ngành khoa học sinh học, y khoa và công nghệ thông tin.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><strong>Melbourne</strong> có tổng cộng 10 khoa: <em>Khoa Kiến trúc, Xây dựng và Quy hoạch, Văn Khoa, Khoa Kinh tế và Thương mại, Khoa Kinh tế và Thương mại, Khoa Công nghệ, Khoa Luật, Khoa Y -Nha và Khoa học Y tế, Khoa Khoa học tư nhiên, Khoa Thú y và Nông nghiệp, Cao đẳng Nghệ thuật Victoria và Nhạc viện Melbourne.</em> </span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><em>Điều kiện đầu vào:</em> Điểm trung bình 8,5 trở lên và IELTS đạt 6,5 trong đó không có điểm nào dưới 6 hoặc điểm kiểm tra Tiếng anh khác được chấp nhận.</span></li>\r\n</ul>\r\n\r\n<h3><span style=\"font-size:24px;\"><strong><span style=\"color:#0000FF;\">Đại học New South Wales</span> <span style=\"color:#0000FF;\"> (New South Wales University)</span> </strong> </span></h3>\r\n\r\n<p><strong><img alt=\"Đại học New South Wales\" src=\"public/Images/New-South-Wales-University.jpg\" title=\"Đại học New South Wales\" width=\"595\" /> </strong></p>\r\n\r\n<p></p>\r\n\r\n<ol>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><strong>Đại học New South Wales (UNSW)</strong> được thành lập vào năm 1949. Trường có trụ sở đặt tại Kensington, ngoại ô Sydney. Theo bảng xếp hạng học thuật thế giới, UNSW là một trong những trường đại học tốt nhất nước Úc và lọt vào danh sách Top 100 thế giới.</span></li>\r\n</ul>\r\n<span style=\"font-size:18px;\">​​​​​​​</span>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Sinh viên tốt nghiệp của trường được các nhà tuyển dụng săn đón – họ có mức lương khởi điểm trung bình cao nhất so với các đại học khác ở NSW, theo Khảo sát Sinh viên tốt nghiệp Ôxtrâylia 2012. </span></li>\r\n</ul>\r\n<span style=\"font-size:18px;\">​​​​​​​</span>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Trường có tổng cộng 8 khoa: <em>Khoa kinh doanh, Khoa cơ khí, Khoa môi trường xây dựng UNSW, Khoa luật, Khoa Y học, Khoa khoa học, Khoa nghệ thuật và thiết kế, Văn khoa và khoa học xã hội.</em> </span></li>\r\n</ul>\r\n<span style=\"font-size:18px;\"> <em>​​​​​​​</em> </span>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><em>Điều kiện đầu vào:</em> IELTS tối thiểu là 6,5 và TOEFL ITB tối thiểu là 90.</span></li>\r\n</ul>\r\n\r\n<h3><span style=\"font-size:24px;\"><strong><span style=\"color:#0000FF;\">Đại học Queensland</span> <span style=\"color:#0000FF;\"> (Queensland University)</span> </strong> </span></h3>\r\n\r\n<p><strong><img alt=\"Đại học Queensland\" src=\"public/Images/Queensland-University.jpg\" title=\"Đại học Queensland\" /> </strong></p>\r\n\r\n<p></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><strong>Đại học Queensland</strong> là một trường đại học công, được thành lập năm 1909. Trường có số lượng sinh viên vào khoảng 40.000, trong đó có 6.500 sinh viên quốc tế. UQ cũng là thành viên sáng lập của nhóm G8.</span></li>\r\n</ul>\r\n<span style=\"font-size:18px;\">​​​​​​​</span>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Trường hoạt động trong 3 cơ sở: cơ sở Thánh Lucia, cơ sở Gatton nổi tiếng về các khóa học nghiên cứu và học thuật về nông nghiệp và các khoa học liên quan và cơ sở tại Ipswich. Ngoại trừ cơ sở tại Ipswich, Đại học Queensland cung cấp chỗ ở cho sinh viên trong cơ sở của trường.</span></li>\r\n</ul>\r\n<span style=\"font-size:18px;\">​​​​​​​</span>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Trường có tổng cộng tất cả là 10 khoa: Khoa xây dựng và hoạch định, Khoa kinh doanh, Khoa sáng tạo – thiết kê và biểu diễn, Khoa giáo dục, Khoa Kỹ thuật, Khoa sức khỏe và cộng đồng, Khoa công nghệ thông tin, Khoa ngôn – Tiếng anh và các chương trình chuyển tiếp, Khoa luật và tòa án, Khoa khoa học và toán học.</span></li>\r\n</ul>\r\n<span style=\"font-size:18px;\">​​​​​​​</span>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><em>Điều kiện đầu vào:</em> IELTS tối thiểu là 6,5, điểm trung bình tối thiểu là 6,0. </span></li>\r\n</ul>\r\n \r\n\r\n<h3><span style=\"font-size:24px;\"><strong><span style=\"color:#0000FF;\">Đại học Sydney</span> <span style=\"color:#0000FF;\"> (University of Sydney)</span> </strong> </span></h3>\r\n\r\n<p><strong><img alt=\"Đại học Sydney\" src=\"public/Images/University-of-Sydney.jpg\" title=\"Đại học Sydney\" width=\"600\" /> </strong></p>\r\n\r\n<ol>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><em><strong>Đại học Sydney </strong>được thành lập vào năm 1850 </em>và là trường đại học lâu đời nhất tại Úc. Đại học Sydney cũng là một trong những trường đại học uy tín nhất thế giới (xếp thứ 25 trong bảng xếp hạng học thuật thế giới) và có một danh tiếng tuyệt vời cho sự xuất sắc trong giáo dục. Đại học Sydney đã có nhiều sinh viên và sinh viên nhận được các giải thưởng Nobel và Crafoord danh giá.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><strong>Đại học Sydney</strong> có các khoa sau đây và bạn có thể chọn học 1-2 chương trình trong các khoa này: <em>khoa Nông nghiệp và môi trường, khoa Kiến trúc, khoa Thiết kế và quy hoạch, Khoa học xã hội và nhân văn, khoa Kinh doanh, khoa Nha khoa, khoa Giáo dục và công tác xã hội, khoa Cơ khí, khoa Khoa học và sức khỏe, khoa Y, khoa Điều dưỡng và hộ sinh, khoa Dược, khoa Khoa học, khoa Nghệ thuật, khoa Thú y.</em> </span></li>\r\n</ul>\r\n<span style=\"font-size:18px;\"> <em>​​​​​​​</em> </span>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><em>Điều kiện đầu vào:</em> Kết thúc lớp 11 hoặc 12, điểm trung bình 6,5 trở lên, chứng chỉ IELTS 5,0 hoặc học tiếng anh tại Australia.</span></li>\r\n</ul>\r\n \r\n\r\n<h3><span style=\"font-size:24px;\"><strong><span style=\"color:#0000FF;\">Đại học Tây Úc</span> <span style=\"color:#0000FF;\"> (Western Australia University)</span> </strong> </span></h3>\r\n\r\n<p><strong><img alt=\"Đại học Tây Úc\" src=\"public/Images/Western-Australia-University.jpg\" title=\"Đại học Tây Úc\" /> </strong><br />\r\n </p>\r\n\r\n<ol>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><strong>Đại học Tây Úc</strong> được thành lập bởi Quốc hội Tây Úc vào năm 1913. Luôn luôn là trường xếp hạng thứ nhất hoặc thứ 2 trong bảng xếp hạng các trường đại học hàng đầu ở Úc và trong top 100 trường đại học có chất lượng đào tạo tốt nhất trên thế giới.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Trường hiện đang liên kết với một số viện nghiên cứu tốt nhất ở Tây Úc và đã là thành viên của nhóm G8 từ lúc mới được thành lập. Hiện có khoảng 25,000 sinh viên đang theo học Đại học Tây Úc, trong đó sinh viên bậc đại học là 19,000 và sau đại học là 6,000.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Trường có tổng cộng tất cả 12 khoa: Khoa Kiến trúc và mỹ thuật ứng dụng, khoa Nghệ thuật, khoa Kinh doanh, khoa Kỹ sư máy tính và phần mềm, khoa Sư phạm, khoa Kỹ sư, khoa Luật, khoa Khoa học, khoa Y học, khoa Âm nhạc, khoa Nông nghiệp, khoa Tâm lý học.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><em>Điều kiện đầu vào:</em> IELTS tối thiểu là 6,5, TOEFL IBT tối thiểu là 82.</span></li>\r\n</ul>\r\n<span style=\"font-size:18px;\">​​​​​​​</span>\r\n\r\n<h3><span style=\"font-size:24px;\"><strong><span style=\"color:#0000FF;\">Đại học Adelaide </span> <span style=\"color:#0000FF;\">(U</span> </strong> <span style=\"color:#0000FF;\"> <strong>niversity Of Adelaide)</strong> </span> </span></h3>\r\n\r\n<p><img alt=\"Đại học Adelaide\" src=\"public/Images/University-Of-Adelaide.jpg\" title=\"Đại học Adelaide\" /></p>\r\n\r\n<p></p>\r\n\r\n<ol>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><em><strong>Đại học Adelaide</strong> là trường đại học công lập ở thành phố Adelaide</em>, được thành lập vào năm 1874 và là trường đại học lâu đời thứ 3 ở Úc. Trường hiện đang có 25,000 sinh viên. Đại học Tây Úc là lựa chọn tuyệt vời cho sinh viên quốc tế bởi chất lượng giảng dạy và dịch vụ tuyệt vời của mình.</span></li>\r\n</ul>\r\n<span style=\"font-size:18px;\">​​​​​​​</span>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><strong>Đại học Adelaide</strong> được đặt tại thành phố Adelaide - thủ phủ của bang Nam Australia, một thành phố thân thiện, an toàn và chi phí sinh hoạt thấp hơn nhiều so với hầu hết các thành phố lớn khác của Australia như Sydney và Melbourne.</span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Trường có rất nhiều khoa để sinh viên có thể thoải mái lựa chọn như: Luật, Khoa học, Kinh tế, Tài chính, Cơ khí, Nghệ thuật, Thương mại, Công nghệ thông tin, Quản lý môi trường, Chính trị, Truyền  thông, Tâm lý học…</span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><em>Điều kiện đầu vào:</em> IELTS 6.0 (viết và nói không dưới 6 và đọc, nghe không dưới 5.5), học lực khá trở lên.</span></li>\r\n</ul>\r\n', '', 'chau-uc', NULL),
(4, 'Châu á', '10', '', '', '0000-00-00', 1, '<p style=\"text-align: center;\"><span style=\"color:#FF0000;\"><span style=\"font-size:48px;\"><u><em><strong>10 đại học hàng đầu châu Á</strong> </em> </u> </span> </span><br />\r\n<br />\r\n </p>\r\n\r\n<p><span style=\"font-size:18px;\"><strong>Đại học quốc gia Singapore đứng đầu châu Á trong bảng xếp hạng mới nhất năm 2016 của Times High Education.</strong></span><br />\r\n </p>\r\n\r\n<h3><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\"><strong>1. Đại học Quốc gia Singapore</strong> </span> </span></h3>\r\n \r\n\r\n<p><span style=\"font-size:18px;\">Xếp hạng 24 thế giới, Đại học Quốc gia Singapore được thành lập vào năm 1905. Đây là viện đại học xưa nhất và lớn nhất về số lượng sinh viên tại Singapore. Đến nay, tổng số sinh viên của trường là hơn 31.000.</span></p>\r\n\r\n<p></p>\r\n\r\n<p><img alt=\"da-hoc-quoc-gia-singapore\" src=\"public/Images/da-hoc-quoc-gia-singapore.jpg\" /><br />\r\n </p>\r\n\r\n<p><span style=\"font-size:24px;\"><span style=\"color:#0000FF;\"><strong>2. Đại học Bắc Kinh (Trung Quốc)</strong></span></span><br />\r\n </p>\r\n\r\n<p><span style=\"font-size:18px;\">Xếp hạng 29 thế giới, Đại học Bắc Kinh được thành lập vào năm 1898, là một trong những trường quốc gia lâu đời nhất và luôn được đánh giá là đại học danh tiếng nhất Trung Quốc. Ngoài học thuật, trường cũng nổi tiếng với vẻ đẹp kiến trúc truyền thống.</span><br />\r\n </p>\r\n\r\n<p><img alt=\"dai-hoc-bac-kinh\" src=\"public/Images/dai-hoc-bac-kinh.jpg\" /><br />\r\n </p>\r\n\r\n<h3><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\"><strong>3. Đại học Thanh Hoa (Trung Quốc)</strong> </span> </span></h3>\r\n \r\n\r\n<p><span style=\"font-size:18px;\">Xếp hạng 35 thế giới, Đại học Thanh Hoa được thành lập vào năm 1911. Trường bao gồm 19 trường chuyên khoa và 55 khoa ngành với khoảng 33.000 sinh viên đang theo học, trong đó có khoảng 2.600 sinh viên quốc tế.</span><br />\r\n </p>\r\n\r\n<p><img alt=\"dai-hoc-thanh-hoa\" src=\"public/Images/dai-hoc-thanh-hoa.jpg\" /><br />\r\n </p>\r\n\r\n<h3><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\"><strong>4. Đại học Tokyo (Nhật Bản)</strong> </span> </span></h3>\r\n \r\n\r\n<p><span style=\"font-size:18px;\">Xếp hạng 39 thế giới, được thành lập năm 1877, Đại học Tokyo là một trong những viện đại học nghiên cứu hàng đầu Nhật Bản. Trường có 10 phân khoa với hơn 30.000 sinh viên. Trường là nơi đào tạo rất nhiều chính khách nổi tiếng của Nhật Bản.</span><br />\r\n </p>\r\n\r\n<p><img alt=\"dai-hoc-tkyo\" src=\"public/Images/dai-hoc-tkyo.jpg\" /><br />\r\n </p>\r\n\r\n<h3><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\"><strong>5. Đại học Hong Kong (Trung Quốc)</strong> </span> </span></h3>\r\n \r\n\r\n<p><span style=\"font-size:18px;\">Xếp hạng 43 thế giới, thành lập năm 1911, đây là trường đại học lâu đời nhất ở Hong Kong và cũng là trường giảng dạy các môn học bằng tiếng Anh tốt nhất châu Á. Trường nổi tiếng có cơ sở vật chất hiện đại và đội ngũ cán bộ giảng viên hùng hậu trên 1.400 người.</span><br />\r\n </p>\r\n\r\n<p><img alt=\"dai-hoc-hong-kong\" src=\"public/Images/dai-hoc-hong-kong.jpg\" /><br />\r\n </p>\r\n\r\n<h2><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\"><strong>6. Đại học Khoa học và Công nghệ Hong Kong (Trung Quốc)</strong> </span> </span></h2>\r\n \r\n\r\n<p><span style=\"font-size:18px;\">Xếp hạng 49 thế giới, được thành lập năm 1991, Đại học Khoa học và Công nghệ Hong Kong là trường đại học trẻ tuổi nhưng đã nhanh chóng tạo được danh tiếng trên toàn thế giới nhờ vào những khác biệt trong chất lượng giảng dạy và nghiên cứu</span>.<br />\r\n </p>\r\n\r\n<p><img alt=\"dai-hoc-cong-nghe-hong-kong\" src=\"public/Images/dai-hoc-cong-nghe-hong-kong.JPG\" /><br />\r\n </p>\r\n\r\n<h3><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\"><strong>7. Đại học Công nghệ Nanyang (Singapore)</strong> </span></span></h3>\r\n \r\n\r\n<p><span style=\"font-size:18px;\">Xếp hạng 54 thế giới, Đại học Công nghệ Nanyang là một trong 3 trường đại học công lập ở Singapore, thuộc hệ thống đại học ASEAN. Đây là trường đại học danh tiếng bậc nhất Đông Nam Á và thường xuyên đứng trong top 100 trường đại học hàng đầu thế giới.</span><br />\r\n </p>\r\n\r\n<p><img alt=\"dai-hoc-nanyang\" src=\"public/Images/dai-hoc-nanyang.jpg\" /><br />\r\n </p>\r\n\r\n<h3><span style=\"font-size:24px;\"><span style=\"color:#0000FF;\"><strong>8. Đại học Quốc gia Seoul (Hàn Quốc)</strong> </span> </span></h3>\r\n \r\n\r\n<p><span style=\"font-size:18px;\">Xếp hạng 72 thế giới, được thành lập năm 1946, đây là trường đại học quốc gia đầu tiên của Hàn Quốc và là kiểu mẫu cho tất cả trường đại học khác ở Hàn Quốc. Đại học Seoul có 24 trường thành viên và khoảng 30.000 sinh viên đang theo học và nghiên cứu.</span><br />\r\n </p>\r\n\r\n<p><img alt=\"dai-hoc-seoul\" src=\"public/Images/dai-hoc-seoul.jpg\" /><br />\r\n </p>\r\n\r\n<h3><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\"><strong>9. Đại học Trung văn Hương Cảng (Trung Quốc)</strong> </span> </span></h3>\r\n \r\n\r\n<p><span style=\"font-size:18px;\">Xếp hạng 76 thế giới, Đại học Trung văn Hương Cảng được thành lập vào năm 1963, là trường đại học lâu đời thứ hai tại Hong Kong. Trường có 9 trường thành viên và 8 chuyên khoa. Tất cả ngành học đều được dạy bằng tiếng Anh và tiếng Trung Quốc.</span><br />\r\n </p>\r\n\r\n<p><img alt=\"dai-hoc-trung-van-huong-can\" src=\"public/Images/dai-hoc-trung-van-huong-can.png\" /><br />\r\n </p>\r\n\r\n<h3><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\"><strong>10. Viện Khoa học và Công nghệ tiên tiến Hàn Quốc - KAIST (Hà Quốc)</strong> </span> </span></h3>\r\n<br />\r\n<img alt=\"KAIST\" src=\"public/Images/KAIST.jpg\" />\r\n<p><span style=\"font-size:24px;\">Xếp hạng 89 thế giới, được thành lập năm 1971, KAIST là viện nghiên cứu khoa học và công nghệ hàng đầu châu Á và thế giới. Trường đào tạo sinh viên ở 4 bậc học, 7 ngành và hơn 20 chuyên ngành.</span><br />\r\n </p>\r\n\r\n<p><span style=\"font-size:24px;\">Nguồn: vnexpress.net</span></p>\r\n', '', 'chau-a', NULL);
INSERT INTO `tt_tintuc` (`Ma`, `Ten`, `MaDanhMuc`, `KeyWord`, `Descr`, `NgayTao`, `TrangThai`, `NoiDung`, `Hinh`, `Alias`, `TacGia`) VALUES
(5, 'Định cư EB5', '11', '', '', '0000-00-00', 1, '<p style=\"text-align: center;\"><span style=\"color:#FF0000;\"><span style=\"font-size:28px;\"><strong>EB5 – CHƯƠNG TRÌNH LẤY THẺ XANH MỸ AN TOÀN  DÀNH CHO NGƯỜI NƯỚC NGOÀI ĐẾN MỸ THEO HÌNH THỨC ĐẦU TƯ.</strong> </span> </span><br />\r\n<br />\r\n </p>\r\n\r\n<p><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\"><strong>EB5 là gì?</strong></span></span><br />\r\n </p>\r\n\r\n<p><span style=\"font-size:18px;\"><strong>EB5 </strong>là chương trình xin thẻ xanh mỹ dành cho người nước ngoài đến Mỹ theo hình thức đầu tư được chính phủ Mỹ thông qua năm 1990 nhằm cải thiện những địa phương có tỷ lệ thất nghiệp cao hoặc kinh thế chậm phát triển. Năm 2003, chính phủ Mỹ cho phép các dự án nằm trong khu vực khuyến khích đầu tư (Regional Centers)  thu hút vốn đầu tư từ nhiều quốc gia trên thế giới.</span></p>\r\n\r\n<p><img alt=\"dinh-cu-eb5-my\" src=\"public/Images/nuoc_my.jpg\" /></p>\r\n\r\n<p></p>\r\n\r\n<p><span style=\"font-size:18px;\">Mỗi năm, chính phủ Mỹ cấp 10.000 visa theo diện EB5, trong đó có đến 3000 visa theo diện ủy thác thông qua Regional Centers với số vốn đầu tư chỉ với $500.000 và số còn lại dành cho tự đầu tư Mỹ.</span><br />\r\n </p>\r\n\r\n<p><span style=\"font-size:18px;\">Vậy, tự đầu tư và đầu tư ủy thác là gì?</span><br />\r\n </p>\r\n\r\n<p><span style=\"font-size:18px;\"><strong><span style=\"color:#B22222;\">ĐẦU TƯ TRỰC TIẾP:</span> </strong>thành lập công ty mới hoàn toàn hoặc mua lại cơ sở kinh doanh đang hoạt động hoặc mở công ty con hay mở thêm chi nhánh kinh doanh.</span><br />\r\n </p>\r\n\r\n<p><span style=\"font-size:18px;\"><span style=\"color:#B22222;\"><strong>Vốn đầu tư:</strong> </span> $1.000.000 và phải tạo ra 10 việc làm toàn thời gian cho công dân hoặc thường trú nhân Mỹ trong 02 năm liên tiếp.</span><br />\r\n </p>\r\n\r\n<p><span style=\"font-size:18px;\"><span style=\"color:#B22222;\"><strong>Đầu tư ủy thác (Đầu tư gián tiếp): </strong> </span>đầu tư thông qua Regional Centers hoặc đầu tư dự án được sự chấp thuận của chính phủ Mỹ.</span><br />\r\n </p>\r\n\r\n<p><span style=\"font-size:18px;\"><span style=\"color:#B22222;\"><strong>Vốn đầu tư:</strong> </span> $500.000.</span><br />\r\n<br />\r\n </p>\r\n\r\n<p><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\"><strong>QUYỀN LỢI  :</strong> </span> </span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><span style=\"color:#008000;\"><strong>ĐƯỢC</strong> </span>  thẻ xanh định cư mỹ dưới dạng thường trú nhân cho cả gia đình ( bao gồm vợ/chồng và các con độc thân dưới 21 tuổi).</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><span style=\"color:#008000;\"><strong>ĐƯỢC</strong> </span> hưởng quyền lợi như công dân Mỹ.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><span style=\"color:#008000;\"><strong>ĐƯỢC</strong> </span> <span style=\"color:#B22222;\"> <strong> </strong> </span>ra vào Mỹ không cần visa.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><span style=\"color:#008000;\"><strong>ĐƯỢC</strong> </span> <span style=\"color:#B22222;\"> <strong> </strong> </span>bão lãnh người thân, xin nhập quốc tịch, …</span></li>\r\n</ul>\r\n \r\n\r\n<p><img alt=\"dinh-cu-my-eb5\" src=\"public/Images/eb5.png\" /><br />\r\n </p>\r\n\r\n<p><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\"><strong>ĐỐI TƯỢNG ĐẦU TƯ</strong></span></span><br />\r\n </p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><strong><span style=\"color:#008000;\">KHÔNG </span> </strong>giới hạn về độ tuổi.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><strong><span style=\"color:#008000;\">KHÔNG</span> </strong>giới hạn về xuất thân, bao gồm tất cả những nhà đầu tư có khả năng về tài chính và có nhu cầu định cư Mỹ.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><strong><span style=\"color:#008000;\">KHÔNG</span> </strong>yêu cầu về ngoại ngữ.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\"><span style=\"color:#008000;\"><strong>KHÔNG</strong> </span> yêu cầu có công ty kinh doanh hoặc kinh nghiệm quản lý doanh nghiệp.</span></li>\r\n</ul>\r\n \r\n\r\n<p><img alt=\"dinh-cu-eb5-nuoc-my\" src=\"public/Images/dinh-cu-eb5.jpg\" /></p>\r\n\r\n<p><span style=\"font-size:18px;\"><a name=\"_gjdgxs\"></a>Để được tư vấn kĩ hơn về chương trình, hãy đến với </span> STAN STANART <span style=\"font-size:18px;\"> để nhận được sự tư vấn chuyên nghiệp và tận tình từ đội ngũ chuyên viên và luật sư với nhiều năm kinh nghiệm trong lĩnh vực đầu tư định cư Mỹ.</span></p>\r\n', '', 'dinh-cu-eb', NULL),
(6, 'Dự án định cư EB5', '12', '', '', '0000-00-00', 1, '<span style=\"font-size:18px;\"><strong>Về dự án hiện tại mà FirstPathway Partners mong muốn được hợp tác với quý đại lý là khách sạn JW Marriott, một dự án sáng giá, và đầy tiềm năng vì những lý do sau:</strong><br />\r\n<br />\r\n<strong> 1.</strong>     <strong><span style=\"color:#B22222;\">Tọa lạc tại Southern California:</span> </strong> Dự án của chúng tôi tọa lạc tại quận Cam (Orange County), nơi thu hút hơn 47 triệu du khách vào năm ngoái, trở thành đểm du lịch lớn thứ tư trên toàn nước Mỹ. Số lượt du khách nơi này còn cao hơn cả San Francisco, Las Vegas, San Diego and Miami.<br />\r\n <br />\r\n <br />\r\n<br />\r\n<strong>2.    <span style=\"color:#B22222;\">Ngay cạnh bên Disneyland: </span></strong> Dự án của chúng nằm ngay đối diện với nơi thu hút khách du lịch lớn nhất và ổn định nhất Hoa Kỳ, công viên Disneyland, nơi thu hút hơn 27 triệu khách du lịch vào năm ngoái. Disneyland là một phần trong lãnh vực vui chơi giải trí, 1 ngành công nghiệp  <br />\r\nKhông chu kỳ. Năm 2008, trong thời kỳ khủng hoảng lớn nhất, khách đến thăm Disneyand chỉ giảm 1% và tăng 9% trong năm tiếp theo. Theo so sánh, chúng tôi nhận thấy sự xuống dốc của lãnh vực bất động sản và sản xuất là hơn 40% trong 2 năm.  <br />\r\n <br />\r\n<strong>3.    <span style=\"color:#B22222;\">Dự án không mắc phải sự cạnh tranh nào trong phân khúc cao cấp:</span> </strong>Khách sạn cũa chúng tôn sẽ là khách sạn hạng sanh DUY NHẤT nằm liển kề bên ngoài công viên Disneyland. Đây là điểm khác biệt lớn nhất của dự án chúng tôi với các dự án khách sạn khác tại các điểm du lịch lới khác như Las Vegas, New  York, Chicago, Orlando, San Francisco and Los Angeles. Tại các thành phố này, sự cạnh trang trong thị trường khách sạn cao cấp rất mãnh liệt. Theo Expedia, Las Vegas hiện có ít nhất 59 khách sạn cao cấp trong khi Orlando có ít nhất 29.<br />\r\n <br />\r\n<strong>4.  <span style=\"color:#B22222;\">Tài nguyên đất đai vô cùng khan hiếm bên ngoài Disneyland: </span></strong>  Nhà phát triển dự án phải mất 10 năm để mua hết những khu đất nhỏ để tạo nên dự án của chúng ta bởi bì khu vực cần ngoài Disneyland có rất ít những khu đất lớn. Vì vậy, không những dự án của chúng tôi hiện nay không có bất kỳ đối thủ cạnh tranh đáng kể nào, mà trong tương lai sẽ rất hiếm có một sự cạnh tranh đáng kể nào cho dự án của chúng ta khi số tiền EB5 nằm trong dự án<br />\r\n <br />\r\n<strong>5.    <span style=\"color:#B22222;\">Được hoàn thuế 81 triệu Dollar: </span></strong><span style=\"color:#B22222;\"> </span>Thành phố Anaheim đã đồng ý hoàn thuế cho chúng tôi 81 triệu Dollar. Số hoàn thuế này được tặng cho dự án không những cho them lợi nhuận mà còn phản ánh tầm nhìn của thành phố rằng thị trường đang rất cần một dự án khách sạn cao cấp.<br />\r\n <br />\r\n<strong>6.    <span style=\"color:#B22222;\">Ngay cạnh trung tâm hội nghị lớn nhất bờ Tây: </span></strong> Góc đường ngay cạnh dự án của chúng tôi là trung tâm hội nghị Anaheim. Chính quyền Anaheim vừa mới mở rộng trung tâm này thành trung tâm hội nghị lớn nhất Hoa Kỳ. Chính quyền thành phố muốn biến Aaheim trở thành điểm đến cho những hội nghị cao cấp nhưng nhận ra rằng mục tiêu sẽ không thể đạt được cho đến khi khu vực này có một khách sạn cao cấp. Đó là lý do thành phố đã đề nghị hoàn thuế cho chúng tôi 81 triệu USD.<br />\r\n <br />\r\n<strong>7.   <span style=\"color:#B22222;\"> Disneyland  dự định mở rộng 1 tỷ Dollar vào năm sau:</span> </strong>Disney đang dự định sẽ mở rộng hoạt động với một công viên 1 tỷ dollar, và sự mở rộng này được kỳ vọng sẽ tăng lượng tham quan trong khu vực lên những tầng cao hơn nữa.<br />\r\n <br />\r\n<strong>8.   <span style=\"color:#B22222;\">Lincoln Stone and Paul Sommers:</span> </strong> Những giấy tờ của dự án chúng tôi được chuẩn bị bởi luật sư EB5 ưu việt tại Hoa Kỳ, Lincoln Stone. Bản báo cáo công việc của chúng tôi được chuẩn bị bởi Paul Sommers, một nhà kinh tế học EB5 gạo cội, người đã có uy tín rất lớn trong lãnh vực EB5.<br />\r\n <br />\r\n <br />\r\n9. <span style=\"color:#B22222;\"><strong>Dự án đã có I924 thông qua và hơn 40 đơn I-526 đã được thông qua</strong></span> trong đó có cả những nhà đầu tư Việt Nam</span>', 'public/Images/l-1a.jpg', 'du-an-dinh-cu-eb5', NULL),
(7, 'Giới thiệu đầu tư L-1a', '13', '', '', '0000-00-00', 1, '<p>\r\n                        <span style=\"font-size:18px;\">Visa L-1A  là  hình thức visa dưới dạng không định cư (Non-Immigrant Visa), nằm trong nhóm visa L\r\n                            (visa làm việc) nhằm mục đích thu hút nhân tài là các doanh nhân và giám đốc, quản lý giỏi đến\r\n                            Mỹ để góp phần phát triển tiềm lực kinh tế Mỹ\r\n                            <strong>.</strong>\r\n                            <br />\r\n                            <br /> Người được cấp visa này được quyền thường trú tối đa tại Mỹ 07 năm với vị trí điều hành/ giám\r\n                            đốc, không cần phải có thêm bất kỳ loại giấy phép lao động nào.\r\n                            <br />\r\n                            <br />\r\n                            <p>\r\n                                <img src=\"public/Images/l-1a.jpg\" alt=\"visa l-1a\">\r\n                            </p>\r\n                            <br />\r\n                        </span>\r\n                        <br />\r\n                        <span style=\"color:#0000FF;\">\r\n                            <span style=\"font-size:24px;\">\r\n                                <strong>Đối tượng áp dụng</strong>:</span>\r\n                        </span>\r\n                        <br />\r\n                    </p>\r\n\r\n                    <ul>\r\n                        <li>\r\n                            <span style=\"font-size:18px;\">Đương đơn phải đang làm việc ở vị trí quản lý/điều hành hoặc chuyên gia cho công ty tại Việt\r\n                                Nam ít nhất 1 năm liên tục, trong vòng 3 năm qua tính đến ngày nộp đơn.</span>\r\n                        </li>\r\n                    </ul>\r\n                \r\n\r\n                    <ul>\r\n                        <li>\r\n                            <span style=\"font-size:18px;\">Đương đơn nhập cảnh vào Mỹ để quản lý/điều hành hoặc chuyên gia cho một chi nhánh của cùng doanh\r\n                                nghiệp hoặc một tổ chức có mối liên hệ chặt chẽ (công ty mẹ con, chi nhánh, liên doanh).</span>\r\n                        </li>\r\n                    </ul>\r\n                \r\n\r\n                    <ul>\r\n                        <li>\r\n                            <span style=\"font-size:18px;\">Chứng minh sự liên hệ giữa công ty Việt Nam và công ty Mỹ: kế hoạch kinh doanh & Giấy phép đầu\r\n                                tư, Nguồn vốn thuyên chuyển hợp pháp.</span>\r\n                        </li>\r\n                    </ul>\r\n                \r\n\r\n                    <h3>\r\n                        <span style=\"color:#0000FF;\">\r\n                            <span style=\"font-size:24px;\">\r\n                                <strong> GIẢI PHÁP TỐI ƯU NHẤT CHO THỰC HIỆN HÓA GIẤC MƠ MỸ </strong>\r\n                            </span>\r\n                        </span>\r\n                    </h3>\r\n\r\n                    <ul>\r\n                        <li>\r\n                            <span style=\"font-size:18px;\">Thời gian thực hiện hồ sơ nhanh chóng: 8 - 12 tháng.</span>\r\n                        </li>\r\n                    </ul>\r\n                \r\n\r\n                    <ul>\r\n                        <li>\r\n                            <span style=\"font-size:18px;\">Vợ hoặc chồng cùng con cái chưa đến 21 tuổi, độc thân của đương đơn đều có thể đi cùng.</span>\r\n                        </li>\r\n                    </ul>\r\n                \r\n\r\n                    <ul>\r\n                        <li>\r\n                            <span style=\"font-size:18px;\"> Lấy thẻ xanh vĩnh viễn cho cả gia đình sau thời gian tối thiểu là 30 tháng cùng với đáp ứng\r\n                                một số các điều kiện cụ thể, mà không cần phải qua thẻ xanh có điều kiện (2 năm).</span>\r\n                        </li>\r\n                    </ul>\r\n                \r\n\r\n                    <ul>\r\n                        <li>\r\n                            <span style=\"font-size:18px;\">Được mở rộng kênh đầu tư, quy mô kinh doanh tại bất cứ địa điểm phát triển nào trên nước Mỹ.</span>\r\n                        </li>\r\n                    </ul>\r\n                \r\n\r\n                    <ul>\r\n                        <li>\r\n                            <span style=\"font-size:18px;\">Không có hạn chế nào về loại hình của doanh nghiệp khi đầu tư sang Mỹ. Điều này giúp đa dạng\r\n                                hóa các kênh đầu tư để tăng lợi nhuận, đồng thời, chia nhỏ rủi ro một cách tối ưu.</span>\r\n                        </li>\r\n                    </ul>\r\n                \r\n\r\n                    <h3>\r\n                        <span style=\"color:#0000FF;\">\r\n                            <span style=\"font-size:24px;\">\r\n                                <strong>NHỮNG QUYỀN LỢI, PHÚC LỢI CỦA XÃ HỘI MỸ MÀ DIỆN VISA L1-A SẼ MANG ĐẾN CHO CẢ GIA ĐÌNH BẠN?</strong>\r\n                            </span>\r\n                        </span>\r\n                    </h3>\r\n                    <br />\r\n                    <span style=\"font-size:18px;\">\r\n                        <strong>1. Cả gia đình được hưởng đầy đủ quyền lợi như một công dân Mỹ thực thụ (trừ quyền bầu cử) như sau:</strong>\r\n                    </span>\r\n\r\n                    <ul>\r\n                        <li>\r\n                            <span style=\"font-size:18px;\">Quyền mua nhà cửa, sở hữu tài sản.</span>\r\n                        </li>\r\n                    </ul>\r\n                \r\n\r\n                    <ul>\r\n                        <li>\r\n                            <span style=\"font-size:18px;\">Quyền được cấp số an sinh xã hội, các loại ID, đăng ký thi lấy bằng lái xe.</span>\r\n                        </li>\r\n                    </ul>\r\n                \r\n\r\n                    <ul>\r\n                        <li>\r\n                            <span style=\"font-size:18px;\">Quyền mua các loại bảo hiểm y tế và bảo hiểm xã hội  như một công dân Mỹ.</span>\r\n                        </li>\r\n                    </ul>\r\n                \r\n\r\n                    <ul>\r\n                        <li>\r\n                            <span style=\"font-size:18px;\">Quyền tự do di chuyển, lưu trú tại Việt Nam và Mỹ.</span>\r\n                        </li>\r\n                    </ul>\r\n                \r\n\r\n                    <ul>\r\n                        <li>\r\n                            <span style=\"font-size:18px;\">Quyền được làm việc hợp pháp dưới sự bảo hộ của luật pháp Mỹ với đối tượng là vợ/ chồng hợp pháp\r\n                                của đương đơn.</span>\r\n                        </li>\r\n                    </ul>\r\n                    <span style=\"font-size:18px;\">\r\n                        <strong>2. Những phúc lợi xã hội được hưởng đầy đủ:</strong>\r\n                    </span>\r\n\r\n                    <ul>\r\n                        <li>\r\n                            <span style=\"font-size:18px;\"> Miễn học phí tại trường công và con cái có cơ hội vào những trường đại học có chất lượng tốt\r\n                                (các trường ĐH Mỹ thường chỉ nhận 10% du học sinh).</span>\r\n                        </li>\r\n                    </ul>\r\n                \r\n\r\n                    <ul>\r\n                        <li>\r\n                            <span style=\"font-size:18px;\">Sau khi kết thúc việc học, con của đương đơn diện L1A không bị bắt buộc quay về Việt Nam và được\r\n                                hưởng chế độ lương bổng hấp dẫn khi làm việc hơn du học sinh.</span>\r\n                        </li>\r\n                    </ul>\r\n                \r\n\r\n                    <ul>\r\n                        <li>\r\n                            <span style=\"font-size:18px;\">Được hưởng các trợ cấp y tế (Medicare Benefits), hưu trí (Retirement Benefits), dưỡng lão (Survivor\'s\r\n                                Benefits),…</span>\r\n                        </li>\r\n                    </ul>\r\n                    <span style=\"font-size:18px;\"> </span>\r\n                    <br />', '', 'gioi-thieu-dau-tu-l-1a', NULL),
(8, 'Visa EB3', '15', '', '', '0000-00-00', 1, '<span style=\"font-size:18px;\">Visa EB3 là chương trình định cư tại Mỹ theo diện lao động phổ thông dành cho lao động người nước ngoài. </span><br />\r\n<br />\r\n \r\n<p><span style=\"font-size:18px;\"><img alt=\"visa-eb3\" src=\"public/Images/visa_eb3.jpg\" /> </span></p>\r\n<br />\r\n<strong><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\">Điều kiện của EB3 như thế nào?</span></span></strong><br />\r\n<br />\r\n<span style=\"font-size:18px;\">Vì là chương trình lao động phổ thông nên điều kiện của chương trình này rất đơn giản:</span>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Có sức khỏe tốt</span></li>\r\n	<li><span style=\"font-size:18px;\">Nằm trong độ tuổi lao động</span></li>\r\n</ul>\r\n<br />\r\n<br />\r\n<span style=\"font-size:18px;\"><span style=\"color:#0000FF;\"> </span></span><span style=\"color:#0000FF;\"><strong><span style=\"font-size:24px;\">Visa EB3 được chia ra thành các loại hình lao động như thế nào?</span></strong></span>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Người lao động có tay nghề là người có công việc yêu cầu tối thiểu 2 năm đào tạo hoặc kinh nghiệm làm việc mà không phải là tạm thời hoặc theo mùa.</span></li>\r\n	<li><span style=\"font-size:18px;\">Chuyên gia là thành viên của các ngành nghề mà công việc đòi hỏi ít nhất một bằng cử nhân từ một trường đại học hoặc cao đẳng của Mỹ hoặc trình độ tương đương.</span></li>\r\n	<li><span style=\"font-size:18px;\">Lao động phổ thông là người có khả năng thỏa mãn các vị trí yêu cầu ít hơn hai năm đào tạo hoặc kinh nghiệm mà không phải là tạm thời hoặc theo mùa.</span></li>\r\n</ul>\r\n<br />\r\n<strong><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\"> Làm thế nào để được cấp visa EB3? </span></span></strong>\r\n\r\n<p><span style=\"font-size:18px;\"><img alt=\"visa-eb3\" src=\"public/Images/eb-3.jpg\" /> </span></p>\r\n<br />\r\n<span style=\"font-size:18px;\">1. Nhà tuyển dụng sẽ hoàn tất mẫu đơn PERM (Program Electronic Review Management) và nộp cho Bộ Lao Động Hoa Kỳ (U.S. Department of Labor) dể Bộ Lao Động Hoa Kỳ chứng thực và chấp thuận mẫu đơn PERM mà nhà tuyển dụng đệ trình.<br />\r\n<br />\r\n2. Nộp đơn I-140 xin bảo lãnh nhân công với Sở Di Trú Hoa Kỳ (U.S. Immigration and Citizenship Services). Nhà tuyển dụng cần phải chứng minh rằng:</span>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Đối tượng lao động có những bằng cấp mà vị trí tuyển dụng yêu cầu và những bằng cấp này phải còn hiệu lực trong suốt quá trình xét duyệt mẫu đơn PERM.</span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Nhà tuyển dụng có khả năng chi trả cho đối tượng lao động mức lương được đề xuất trong mẫu đơn PERM và trong bất kỳ trường hợp nào, người lao động cũng không phải hoàn trả lại phần lương của họ cho nhà tuyển dụng.</span></li>\r\n</ul>\r\n<br />\r\n<span style=\"font-size:18px;\">3. Cuối cùng là bước đối tượng lao động tại nước ngoài sẽ nộp đơn xin thẻ xanh / visa định cư. Có 1 điểm quan trọng cần lưu ý là, trường hợp bảo lãnh định cư diện lao động cũng chịu những hạn chế thường niên về việc xin làm thường trú nhân, tùy theo khu vực địa lý. Với công dân Việt Nam, chỉ những người nộp đơn PERM xin EB-3 trước ngày 1 tháng 6 năm 2016 là có thể xin thẻ xanh vào tháng 10, 2016 còn những ưu tiên khác như EB-5 thì thời điểm hiện tại không có hồ sơ tồn đọng với đối tượng công dân Việt Nam nên cũng không hạn chế.</span><br />\r\n<br />\r\n<strong><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\">Những quyền lợi khi làm visa EB3? </span></span></strong>\r\n\r\n<p><span style=\"font-size:18px;\"><img alt=\"visa-eb3\" src=\"public/Images/eb3.png\" /> </span></p>\r\n<br />\r\n<span style=\"font-size:18px;\">Bạn và gia đình bạn sẽ nhận được những lợi ích khi tham gia vào chương trình này:</span>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Thẻ xanh sẽ được cấp sau 3 tháng kể từ khi bạn sang Mỹ</span></li>\r\n	<li><span style=\"font-size:18px;\">Gia đình bạn sẽ nhận được mọi quyền lợi như công dân Mỹ</span></li>\r\n	<li><span style=\"font-size:18px;\">Thời gian nhận được công dân ngắn nhất.</span></li>\r\n</ul>\r\n<br />\r\n<br />\r\n<strong><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\"> Những người có thể cùng đi sang Mỹ theo visa EB3?</span></span></strong><br />\r\n<br />\r\n<span style=\"font-size:18px;\">Vợ/chồng của bạn có thể nhập cư Hoa Kỳ theo diện E34 (vợ/chồng của “lao động có trình độ chuyên môn nghiệp vụ” hoặc “chuyên gia”) hay EW4 (vợ/chồng của “các lao động khác”). Trong quá trình bạn và vợ/chồng của bạn đang nộp đơn xin định cư Hoa Kỳ (xin thẻ xanh), vợ/chồng của bạn sẽ đủ điều kiện để nộp đơn xin Giấy Phép Lao Động (EAD). Con cái chưa thành niên của bạn (dưới 21 tuổi) có thể nhập cư Hoa Kỳ theo diện E35 (con cái của “lao động có tŕnh độ chuyên môn nghiệp vụ” hay “chuyên gia”) hoặc EW5 (con cái của “các lao động khác”).</span>', '', 'visa-eb3', NULL),
(9, 'Giới thiệu chung', '17', '', '', '0000-00-00', 1, '<span style=\"font-size:18px;\">Visa J-1 dành cho các cá nhân tham gia vào các chương trình trao đổi, giao lưu hoặc đào tạo. Chương trình được ban hành để thúc đẩy thiện chí quốc tế qua trao đổi, giao lưu văn hóa. Người có visa J-1 có thể tham gia vào nhiều loại chương trình bao gồm chương trình của chính phủ, học thuật, hoặc khu vực tư nhân.<br />\r\nVí dụ về các chương trình visa J-1:</span><br />\r\n \r\n<p><img alt=\"\" src=\"public/Images/visa-j1.jpg\" /></p>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Giúp việc để học ngoại ngữ và chăm sóc giáo dục</span></li>\r\n	<li><span style=\"font-size:18px;\">Hướng dẫn viên cắm trại</span></li>\r\n	<li><span style=\"font-size:18px;\">Khách chính phủ</span></li>\r\n	<li><span style=\"font-size:18px;\">Thực tập</span></li>\r\n	<li><span style=\"font-size:18px;\">Khách quốc tế (Bộ Ngoại giao sử dụng)</span></li>\r\n	<li><span style=\"font-size:18px;\">Nhà vật lý</span></li>\r\n	<li><span style=\"font-size:18px;\">Giáo sư và học giả nghiên cứu</span></li>\r\n	<li><span style=\"font-size:18px;\">Học giả ngắn hạn</span></li>\r\n	<li><span style=\"font-size:18px;\">Chuyên viên</span></li>\r\n	<li><span style=\"font-size:18px;\">Sinh viên, cao đẳng/đại học</span></li>\r\n	<li><span style=\"font-size:18px;\">Học sinh phổ thông cơ sở</span></li>\r\n	<li><span style=\"font-size:18px;\">Du lịch làm việc hè</span></li>\r\n	<li><span style=\"font-size:18px;\">Giáo viên</span></li>\r\n	<li><span style=\"font-size:18px;\">Đào tạo viên</span></li>\r\n</ul>\r\n\r\n<p><img alt=\"visa-j1-my\" src=\"public/Images/visa-j1-my.jpg\" /></p>\r\n<br />\r\n<span style=\"font-size:18px;\">Có hơn 170.000 khách tham gia vào chương trình visa J-1 hàng tháng. Thời gian tạm trú của người giữ visa J-1 phụ thuộc vào chương trình cụ thể mà họ tham gia. Việc gia hạn visa J-1 để cho phép khách tạm trú được ở lại Mỹ trong thời gian lâu hơn đôi khi được chấp thuận. Một vài người giữ visa J-1 phải tuân theo những yêu cầu về cư trú tại nước sở tại. Những người chịu theo yêu cầu này không thể trở thành thường trú nhân, không thể thay đổi tình trạng của họ ở Mỹ và không thể có visa theo diện gia đình hoặc theo diện việc làm trừ khi họ trở lại đất nước họ trong 2 năm. Họ không thể trở lại bất cứ nước nào ngoài nước mẹ đẻ của họ. Người giữ visa J-1 có thể phải tuân theo yêu cầu cư trú 2 năm nếu:</span>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Chương trình J-1 có vốn tài trợ từ một tổ chức quốc tế, từ chính phủ sở tại của ứng viên, hoặc từ chính phủ Mỹ</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Người có visa J-1 học tập hoặc làm việc trong lĩnh vực chuyên ngành mà đất nước họ cần và được liệt vào Danh sách trình độ chuyên môn của khách trao đổi của Bộ Ngoại giao.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Người có visa J-1 đã tham gia vào chương trình đào tạo y tế của Ủy ban Giáo dục cho Sinh viên Y nước ngoài (ECFMG)</span></li>\r\n</ul>\r\n<span style=\"font-size:18px;\"> </span><br />\r\n<span style=\"color:#0000FF;\"><span style=\"font-size:24px;\"><strong>Những yêu cầu đối với Visa J-1</strong> </span> </span><br />\r\n<br />\r\n<span style=\"font-size:18px;\"><span style=\"color:#B22222;\">Các ứng viên phải đáp ứng một vài yêu cầu để được cấp visa J-1.</span> </span>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Các ứng viên phải tham gia vào chương trình được quản lý bởi một tổ chức tài trợ đã chỉ định. </span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Các nhà tài trợ được chỉ định bao gồm các tổ chức như trường đại học, doanh nghiệp tư nhân, hoặc cơ quan chính phủ có quản lý các chương trình trao đổi cá nhân J-1.  </span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Mỗi chương trình này đều có các tiêu chí cụ thể của riêng nó mà bắt buộc các ứng viên phải thỏa mãn. </span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Tất cả các ứng viên xin visa J-1 phải thành thạo tiếng Anh để đáp ứng các yêu cầu của chương trình mà họ đăng ký. </span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Các ứng viên cũng phải có đầy đủ bảo hiểm y tế và phải nộp đủ phí tham gia chương trình. </span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Những phí này bao gồm phí do chương trình tài trợ cá nhân yêu cầu, phí của Bộ An ninh Nội địa, phí xử lý, cũng như là phí bảo hiểm visa. Khoản phí là khác nhau tùy thuộc vào các chương trình cụ thể mà ứng viên tham gia.</span></li>\r\n</ul>\r\n\r\n<p><img alt=\"visa-j1-my\" src=\"public/Images/visa-my-j1.jpg\" /></p>\r\n<br />\r\n<strong><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\">Quy trình xin visa J-1</span> </span> </strong><br />\r\n<br />\r\n<span style=\"font-size:18px;\"><span style=\"color:#B22222;\">Quy trình xin visa được bắt đầu bằng cách lấy Đơn DS-2019 từ nhà tài trợ chương trình.</span>  </span>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Đơn này sẽ trình bày rõ thời gian chương trình, mục đích của chương trình, các quỹ liên quan đến chương trình và trình bày xem ứng viên đó có phải theo yêu cầu cư trú tại nước sở tại 2 năm không thông qua việc tham gia chương trình. </span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">  Khi có được đơn DS-2019, các ứng viên xin visa J-1 nộp đơn xin visa cho lãnh sự quán Mỹ hoặc đại sứ quán ở nước ngoài để được vào Mỹ. </span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Visa J-1 là visa không định cư được cấp trong một giai đoạn tạm thời.  </span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Người có visa J-1 có thể nộp hồ sơ xin visa khác để có thể thường trú tại Mỹ, nhưng họ không thể đi lại khi thực hiện việc đó trừ khi được cho phép theo hồ sơ của họ hoặc cho đến khi nhận được thẻ xanh. </span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Vơ/chồng và con cái dưới 21 tuổi của người giữ visa J-1 có đủ điều kiện để có được visa phụ thuộc J-2 cho phép họ đi cùng gia đình đến Mỹ và xin giấy phép lao động. </span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Thời gian xử lý cho chương trình visa J-1 phụ thuộc vào nước mẹ đẻ của ứng viên mặc dù thường mất 6 tuần trở xuống.</span></li>\r\n</ul>\r\n<br />\r\n<strong><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\">Tóm tắt về Visa J-1</span></span></strong>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Visa J-1 dành cho các ứng viên tham gia vào chương trình trao đổi, giao lưu.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Visa J-2 dành cho vợ/chồng hoặc con cái dưới 21 tuổi của ứng viên J-1.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Đơn phải nộp là:</span>\r\n\r\n	<ul style=\"list-style-type:circle;\">\r\n		<li><span style=\"font-size:18px;\">DS 2019</span></li>\r\n		<li><span style=\"font-size:18px;\">DS-160</span></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Phỏng vấn tại lãnh sự quán Hoa Kỳ hoặc đại sứ quán được yêu cầu.</span></li>\r\n	<li><span style=\"font-size:18px;\">Phí xử lý là $100.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Quá trình phê duyệt thường mất 6 tuần.</span></li>\r\n</ul>\r\n\r\n<p><img alt=\"\" src=\"public/Images/visa-my-j-1.jpg\" /></p>\r\n<span style=\"color:#FF0000;\"> <span style=\"font-size:24px;\"> <strong>Quan trọng</strong> </span> </span><br />\r\n<br />\r\n<span style=\"font-size:18px;\">Bạn phải có mẫu DS-2019 khi đến phỏng vấn theo hẹn.  Chúng tôi không thể cấp visa cho tới khi bạn có thể nộp mẫu DS-2019.<br />\r\nMẫu DS-2019 không phải là visa. Nếu bạn đến Hoa Kỳ với mẫu DS-2019 mà không có visa, bạn sẽ không được phép nhập cảnh vào Hoa Kỳ mà sẽ phải trở lại nước mà bạn đã xuất phát.<br />\r\nĐể xin được loại visa này, bạn cần phải chứng minh:</span>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Có nguồn tài chính đầy đủ để chi trả cho mọi chi phí của thời gian lưu trú và chuyến quay trở về. </span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Có khả năng tiếng Anh phù hợp.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Có ràng buộc chặt chẽ với nước nơi mình nộp đơn xin visa đi Hoa Kỳ.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Có bằng chứng chứng tỏ rằng mình sẽ rời khỏi Hoa Kỳ khi kết thúc chương trình.</span></li>\r\n</ul>\r\n<span style=\"font-size:18px;\"> </span><br />\r\n<strong><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\"><a name=\"docr\"></a>Giấy tờ cần có</span> </span> </strong><br />\r\n<br />\r\n<span style=\"font-size:18px;\"><span style=\"color:#B22222;\"><strong>Mỗi đương đơn cần phải nộp:</strong> </span> </span>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Hộ chiếu còn hạn sử dụng.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Trang xác nhận của Đơn xin visa điện tử (DS-160).</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Ảnh cỡ 5cm x 5cm nền màu trắng mới chụp trong vòng sáu tháng.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Bằng chứng nộp phí xét đơn; Đương đơn có học bổng Fulbright, học bổng của Quỹ giáo dục Việt Nam (VEF) và thân nhân của họ không phải nộp phí xét đơn.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Bằng chứng nộp phí SEVIS; Đương đơn có học bổng Fulbright, học bổng của Quỹ giáo dục Việt Nam (VEF) không phải nộp phí xét SEVIS.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Mẫu DS-2019 do tổ chức bảo trợ đã được Chính phủ Hoa Kỳ phê chuẩn cấp cho từng đương đơn.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Giấy tờ hậu thuẫn cho đơn xin visa của bạn.</span></li>\r\n</ul>\r\n<br />\r\n<span style=\"font-size:18px;\"><span style=\"color:#B22222;\"><strong>Những người phụ thuộc xin visa không cùng lúc với đương đơn chính cần phải có giấy tờ sau:</strong> </span> </span>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Bản sao mẫu DS-2019 của đương đơn chính mang visa J1.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Bản sao visa J1 của đương đơn chính.</span></li>\r\n</ul>\r\n \r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Hồ sơ kết hôn hoặc khai sinh để chứng tỏ mối quan hệ với đương đơn chính.</span></li>\r\n</ul>\r\n<br />\r\n<span style=\"font-size:18px;\"><span style=\"color:#B22222;\"><strong>Thực tập sinh, những người tham gia khóa đào tạo và sinh viên thực tập cần cung cấp:</strong> </span> </span>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Mẫu DS-7002 – Kế hoạch sắp xếp việc Đào tạo/Thực tập.</span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Kế hoạch đào tạo cụ thể.</span></li>\r\n</ul>\r\n<span style=\"font-size:18px;\"> </span>', 'public/Images/visa-my-j1-xuat-khau-lao-dong.jpg', 'gioi-thieu-chung', 'Hoàng Hương Lan'),
(15, 'ĐẤT NÔNG NGHIỆP', '14', '', '', '2018-05-15', 1, '<img alt=\"\" src=\"public/images/dat-nong-nghiep-1.jpg\" style=\"width: 1365px; height: 1024px;\" /> <img alt=\"\" src=\"public/images/dat-nong-nghiep-2.jpg\" style=\"width: 1365px; height: 1024px;\" /> <img alt=\"\" src=\"public/images/dat-nong-nghiep-3.jpg\" style=\"width: 1365px; height: 1024px;\" /> <img alt=\"\" src=\"public/images/dat-nong-nghiep-4.jpg\" style=\"width: 1365px; height: 1024px;\" /> <img alt=\"\" src=\"public/images/dat-nong-nghiep-6.jpg\" style=\"width: 1365px; height: 1024px;\" />\r\n<div class=\"alert alert-success\">TẠI SAO LÀ ĐỊA CHỈ UY TÍN, ĐÁNG TIN CẬY CHO CẢ GIA ĐÌNH BẠN?\r\n<p>Đến với STAN STANART VIETNAM, khách hàng có thể hoàn toàn an tâm với đội ngũ chuyên viên tư vấn và luật sư với nhiều năm kinh nghiệm trong lĩnh vực định cư – đầu tư Mỹ đi kèm với tôn chỉ MINH BẠCH – THÔNG SUỐT – HIỆU QUẢ.</p>\r\n\r\n<p>Nhằm đáp ứng 02 nhu cầu quan trọng của khách hàng đó là THẺ XANH và BẢO TOÀN VỐN, SOUTHWESTGATE VIETNAM tập trung vào các dự án có các yếu tố sau:</p>\r\n\r\n<ul>\r\n	<li>1. Đơn vị phân phối dự án độc quyền.</li>\r\n	<li>2. Cam kết hoàn vốn.</li>\r\n	<li>3. Đầu tư có lợi nhuận.</li>\r\n	<li>4. Chuyên gia định hướng đầu tư kinh nghiệm.</li>\r\n	<li>5. Luật sư di trú Mỹ chuyên nghiệp.</li>\r\n</ul>\r\n\r\n<p>Bởi lẽ đó, hãy đến với STAN STANART VIETNAM để được tư vấn lựa chọn và hoàn thiện dự án đầu tư Mỹ một cách an toàn, thuận tiện và hiệu quả.</p>\r\nHÃY AN TÂM ĐẶT TRỌN SỰ TIN TƯỞNG Ở STAN STANART VIETNAM !</div>\r\n', 'public/Images/dat_nong_nghiep.jpg', 'dat-nong-nghiep', 'Hoàng Hương Lan'),
(16, 'DỰ ÁN ĐẦU TƯ - VIỆN DƯỠNG LÃO', '14', '', '', '2018-05-15', 1, '<img alt=\"\" src=\"public/images/dau-tu-l1a.jpg\" style=\"width: 1200px; height: 300px;\" /><br />\r\n<img alt=\"\" src=\"public/images/dau-tu-l1a_1.jpg\" style=\"width: 1365px; height: 1024px;\" /><br />\r\n<img alt=\"\" src=\"public/images/dau-tu-l1a_2.jpg\" style=\"width: 1365px; height: 1024px;\" /><br />\r\n<img alt=\"\" src=\"public/images/dau-tu-l1a_.jpg\" style=\"width: 1365px; height: 1024px;\" /><br />\r\n<img alt=\"\" src=\"public/images/dau-tu-l1a_3.jpg\" style=\"width: 1365px; height: 1024px;\" /><br />\r\n<img alt=\"\" src=\"public/images/dau-tu-l1a_5.jpg\" style=\"width: 1365px; height: 1024px;\" /><br />\r\n<img alt=\"\" src=\"public/images/dau-tu-l1a_5.jpg\" style=\"width: 1365px; height: 1024px;\" />\r\n<div class=\"alert alert-success\">TẠI SAO STAN STANART LÀ ĐỊA CHỈ UY TÍN, ĐÁNG TIN CẬY CHO CẢ GIA ĐÌNH BẠN?\r\n<p>Đến với STAN STANART VIETNAM, khách hàng có thể hoàn toàn an tâm với đội ngũ chuyên viên tư vấn và luật sư với nhiều năm kinh nghiệm trong lĩnh vực định cư – đầu tư Mỹ đi kèm với tôn chỉ MINH BẠCH – THÔNG SUỐT – HIỆU QUẢ.</p>\r\n\r\n<p>Nhằm đáp ứng 02 nhu cầu quan trọng của khách hàng đó là THẺ XANH và BẢO TOÀN VỐN, STAN STANART VIETNAM tập trung vào các dự án có các yếu tố sau:</p>\r\n\r\n<ul>\r\n	<li>1. Đơn vị phân phối dự án độc quyền.</li>\r\n	<li>2. Cam kết hoàn vốn.</li>\r\n	<li>3. Đầu tư có lợi nhuận.</li>\r\n	<li>4. Chuyên gia định hướng đầu tư kinh nghiệm.</li>\r\n	<li>5. Luật sư di trú Mỹ chuyên nghiệp.</li>\r\n</ul>\r\n\r\n<p>Bởi lẽ đó, hãy đến với STAN STANART VIETNAM để được tư vấn lựa chọn và hoàn thiện dự án đầu tư Mỹ một cách an toàn, thuận tiện và hiệu quả.</p>\r\nHÃY AN TÂM ĐẶT TRỌN SỰ TIN TƯỞNG Ở STAN STANART VIETNAM !</div>\r\n', 'public/Images/vien-duong-lao.jpg', 'du-an-dau-tu-vien-duong-lao', 'Hoàng Hương Lan'),
(17, 'ĐẦU TƯ- NHÀ HÀNG THỨC ĂN VIỆT', '14', '', '', '2018-05-16', 1, '<img alt=\"\" src=\"public/images/nha-hang-thuc-an-viet_1.jpg\" style=\"width: 1365px; height: 1024px;\" /> <img alt=\"\" src=\"public/images/nha-hang-thuc-an-viet_2.jpg\" style=\"width: 1365px; height: 1024px;\" /> <img alt=\"\" src=\"public/images/nha-hang-thuc-an-viet_3.jpg\" style=\"width: 1365px; height: 1024px;\" /> <img alt=\"\" src=\"public/images/nha-hang-thuc-an-viet_4.jpg\" style=\"width: 1365px; height: 1024px;\" />\r\n\r\n<div class=\"alert alert-success\">\r\nTẠI SAO STAN STANART LÀ ĐỊA CHỈ UY TÍN, ĐÁNG TIN CẬY CHO CẢ GIA ĐÌNH BẠN?\r\n\r\n<p>Đến với STAN STANART VIETNAM, khách hàng có thể hoàn toàn an tâm với đội ngũ chuyên viên tư vấn và luật sư với nhiều năm kinh nghiệm trong lĩnh vực định cư – đầu tư Mỹ đi kèm với tôn chỉ MINH BẠCH – THÔNG SUỐT – HIỆU QUẢ.</p>\r\n\r\n<p>Nhằm đáp ứng 02 nhu cầu quan trọng của khách hàng đó là THẺ XANH và BẢO TOÀN VỐN, STAN STANART VIETNAM tập trung vào các dự án có các yếu tố sau:</p>\r\n\r\n<ul>\r\n	<li>1. Đơn vị phân phối dự án độc quyền.</li>\r\n	<li>2. Cam kết hoàn vốn.</li>\r\n	<li>3. Đầu tư có lợi nhuận.</li>\r\n	<li>4. Chuyên gia định hướng đầu tư kinh nghiệm.</li>\r\n	<li>5. Luật sư di trú Mỹ chuyên nghiệp.</li>\r\n</ul>\r\n\r\n<p>Bởi lẽ đó, hãy đến với STAN STANART VIETNAM để được tư vấn lựa chọn và hoàn thiện dự án đầu tư Mỹ một cách an toàn, thuận tiện và hiệu quả.</p>\r\n\r\nHÃY AN TÂM ĐẶT TRỌN SỰ TIN TƯỞNG Ở STAN STANART VIETNAM !\r\n</div>', 'public/Images/nha-hang-thuc-an-viet.jpg', 'dau-tu-nha-hang-thuc-an-viet', 'Hoàng Hương Lan'),
(18, 'Tuyển dụng lao động EB3-nhận thẻ xanh Mỹ 10 năm', '16', '', '', '2018-05-16', 1, '<p><span style=\"font-size:18px;\">Hiện nay Westlife cùng Văn phòng luật sư DMV LAW CENTER đang cung cấp dịch vụ tuyển dụng và làm visa EB-3 có thẻ xanh cho lao động phổ thông tại tại Hoa Kỳ. Toàn bộ thủ tục pháp lý hợp pháp 100% theo luật di trú Mỹ. Luôn đảm bảo quyền và lợi ích cao nhất cho người lao động.<br />\r\n<br />\r\n<img alt=\"lao-dong-eb3\" src=\"http://localhost/van/public/images/lao-dong-eb-3.jpg\" style=\"width: 600px; height: 401px;\" /></span><br />\r\n<br />\r\n </p>\r\n\r\n<p><span style=\"font-size:18px;\">Nội dung cụ thể như sau:</span></p>\r\n\r\n<p><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\"><strong>1.  Lương:</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Từ $13-$13.5/giờ, cộng tiền thưởng tuỳ từng vị trí công việc</span></li>\r\n	<li><span style=\"font-size:18px;\">Lương tăng ca: từ $13,5-$15/giờ</span></li>\r\n</ul>\r\n\r\n<p><span style=\"color:#0000FF;\"><span style=\"font-size:18px;\"><strong>2.  Thời gian làm việc:</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Từ thứ hai-thứ sáu, 8 tiếng/ngày</span></p>\r\n\r\n<p><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\"><strong>3.  Vị trí:</strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000;\"><span style=\"font-size:18px;\"><strong>Tại Wisconsin (số lượng 15 người):</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Thời gian giải quyết rất nhanh chóng, khoảng 8-10 tháng đối với người đang ở Mỹ và khoảng 12-16 tháng đối với người bắt đầu từ Việt Nam sang Mỹ</span></li>\r\n	<li><span style=\"font-size:18px;\">Tham gia vào dây chuyền chế biến gia cầm</span></li>\r\n	<li><span style=\"font-size:18px;\">Đóng gói vào các thùng</span></li>\r\n	<li><span style=\"font-size:18px;\">Kiểm tra nhãn hàng và đóng thùng bìa carton</span></li>\r\n	<li><span style=\"font-size:18px;\">Các công việc liên quan đến đóng gói hàng trong dây chuyền chế biến gia cầm.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px;\"><strong>Tại Maryland (số lượng hạn chế dưới 10 người):</strong></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Thời gian giải quyết rất nhanh chóng, khoảng 12-15 tháng đối với người đang ở Mỹ và khoảng 18-24 tháng đối với người bắt đầu từ Việt Nam sang Mỹ</span></li>\r\n	<li><span style=\"font-size:18px;\">Tham gia vào các công việc liên quan đến quảng cáo (phát quảng cáo, sắp xếp quảng cáo, chuẩn bị nội dung, trợ giúp liên quan đến việc huy động tài chính), vv.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px;\"><strong>Tại Virginia (số lượng rất hạn chế dưới 5 người):</strong></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Thời gian giải quyết rất nhanh chóng, khoảng 12-15 tháng đối với người đang ở Mỹ và khoảng 18-24 tháng đối với người bắt đầu từ Việt Nam sang Mỹ</span></li>\r\n	<li><span style=\"font-size:18px;\">Tham gia vào các công việc có liên quan đến vệ sinh các toà nhà của chính phủ, các thư viện, trạm cảnh sát, các trung tâm giải trí, các khách sạn, vv.</span></li>\r\n</ul>\r\n<span style=\"font-size:18px;\"><img alt=\"\" src=\"http://localhost/van/public/images/lao-dong-eb3.jpg\" style=\"width: 800px; height: 522px;\" /></span>\r\n\r\n<p><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\"><strong>4.  Điều kiện:</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Không có tiền án, tiền sự tại Việt Nam</span></li>\r\n	<li><span style=\"font-size:18px;\">Không mắc bệnh truyền nhiễm</span></li>\r\n	<li><span style=\"font-size:18px;\">Không sử dụng chất gây nghiện</span></li>\r\n	<li><span style=\"font-size:18px;\">Người lao động có độ tuổi từ 18-50</span></li>\r\n	<li><span style=\"font-size:18px;\">Đủ sức khoẻ lao động</span></li>\r\n	<li><span style=\"font-size:18px;\">Không yêu cầu bằng cấp, tay nghề</span></li>\r\n	<li><span style=\"font-size:18px;\">Tiếng Anh (căn bản, giao tiếp)</span></li>\r\n	<li><span style=\"font-size:18px;\">Chưa từng vi phạm luật di trú Mỹ</span></li>\r\n</ul>\r\n\r\n<p><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\"><strong>5.  Lợi ích:</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Du học sinh Việt Nam (học Anh văn căn bản) không cần phải duy trì student visa F1 sau khoảng 8 tháng làm hồ sơ di trú;</span></li>\r\n	<li><span style=\"font-size:18px;\">Được bảo trợ thẻ xanh Mỹ 10 năm cho cả gia đình;</span></li>\r\n	<li><span style=\"font-size:18px;\">Được cấp bảo hiểm sức khoẻ, bảo hiểm mắt và bảo hiểm răng cho cả gia đình;</span></li>\r\n	<li><span style=\"font-size:18px;\">Rủi ro thấp vì mọi thủ tục là hợp pháp do tổ hợp luật sư Mỹ thực hiện theo luật pháp Mỹ;</span></li>\r\n	<li><span style=\"font-size:18px;\">Chi phí thấp hơn các diện di dân khác;</span></li>\r\n	<li><span style=\"font-size:18px;\">Có công việc ổn định, lâu dài khi đặt chân tới nước Mỹ;</span></li>\r\n	<li><span style=\"font-size:18px;\">Con cái được học miễn phí từ lớp 1-12 tại trường công lập;</span></li>\r\n	<li><span style=\"font-size:18px;\">Mọi thành viên trong gia đình được học từ cao đẳng trở lên với chi phí chỉ bằng 1/3 so với du học sinh;</span></li>\r\n	<li><span style=\"font-size:18px;\">Được hưởng tất cả các chế độ ưu tiên dành cho một thường trú nhân tại Mỹ;</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:24px;\"><span style=\"color:#0000FF;\"><strong>6.  Bắt đầu nhận hồ sơ :</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Do số lượng hồ sơ diện EB3 dành cho người Việt Nam mỗi năm có hạn, ưu tiên những hồ sơ đến sớm nhất</span></p>\r\n\r\n<p></p>\r\n', 'public/Images/lao-dong-eb-3.jpg', 'tuyen-dung-lao-dong-eb3-nhan-the-xanh-my-10-nam', 'Hoàng Hương Lan'),
(19, 'Visa EB3 Định cư Mỹ theo diện có tay nghề', '16', '', '', '2018-05-16', 1, '<span style=\"font-size:22px;\">Visa EB3 – Định cư Mỹ theo diện có tay nghề, chuyên gia, lao động phổ thông</span>\r\n\r\n<p><br />\r\n<strong><img alt=\"visa-eb3\" src=\"public/images/visa-eb3.jpg\" style=\"width: 612px; height: 407px;\" /><br />\r\n<br />\r\n<span style=\"font-size:18px;\">EB3 là chương trình định cư tại Mỹ theo diện lao động phổ thông dành cho lao động người nước ngoài làm việc tại các nông trại, tòa nhà, nhà hàng, các công ty ngoại thất, …</span></strong></p>\r\n\r\n<p><span style=\"font-size:18px;\">Yêu cầu đương đơn có sức khỏe tốt, trong độ tuổi lao động, không yêu cầu trình độ, không yêu cầu có chứng chỉ tiếng Anh.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Khuyến cáo đây là công việc chân tay với cường độ lớn, yêu cầu cam kết tối thiểu hợp đồng 1 năm, sau 1 năm đương đơn có thể gia hạn hợp đồng hoặc tự do tìm công việc mới tại Mỹ.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\"><strong>Quyền lợi:</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px;\">– Có việc làm ngay sau sang Mỹ.<br />\r\n– Thẻ xanh sẽ được cấp sau 3 tháng khi bạn sang Mỹ.<br />\r\n– Gia đình bạn sẽ nhận được hầu hết quyền lợi như công dân Mỹ.<br />\r\n– Thời gian nhận được công dân ngắn nhất.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\"><strong>Cam kết với chương trình định cư diện EB3:</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px;\">– Thời gian nhận Visa chỉ từ 12-18 tháng kể từ ngày ký hợp đồng.<br />\r\n– Bồi hoàn 100% nếu đương đơn không nhận được Visa.</span></p>\r\n', 'public/Images/visa-eb3.jpg', 'visa-eb3-dinh-cu-my-theo-dien-co-tay-nghe', 'Hoàng Hương Lan');
INSERT INTO `tt_tintuc` (`Ma`, `Ten`, `MaDanhMuc`, `KeyWord`, `Descr`, `NgayTao`, `TrangThai`, `NoiDung`, `Hinh`, `Alias`, `TacGia`) VALUES
(20, 'Chương trình định cư Mỹ theo diện lao động EB3', '16', '', '', '2018-05-16', 1, '<p><span style=\"font-size:18px;\">Chương trình định cư Mỹ theo diện lao động EB-3 do chính phủ Mỹ ban hành, nhằm cung cấp lao động phổ thông nước ngoài cho nước Mỹ tại những nơi lao động bản xứ không đủ số lượng để đáp ứng.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Định cư Mỹ diện EB-3 là hình thức định cư theo diện việc làm bao gồm: lao động có tay nghề, chuyên gia và lao động phổ thông. Người lao động sẽ được cấp thẻ thường trú nhân (thẻ xanh) để nhập cư vào Mỹ làm việc lâu dài, dựa trên sự bảo lãnh của một doanh nghiệp tại Mỹ (hay còn gọi là nhà tuyển dụng tại Mỹ).<br />\r\n<br />\r\n<img alt=\"\" src=\"public/images/visa-eb3-lao-dong-my.jpg\" style=\"width: 1000px; height: 568px;\" /></span></p>\r\n\r\n<p><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\"><strong>Vì sao có chính sách định cư Mỹ EB-3?</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Tại Mỹ, các ngành đặc thù liên quan đến sức khoẻ như dược phẩm, thực phẩm… thì các cơ sở sản xuất buộc phải nằm trong lãnh thổ nước Mỹ để đảm bảo an toàn sức khoẻ cho hơn 300 triệu dân. Những công việc này yêu cầu phần lớn lao động, nhưng do công việc cực nhọc và lương thấp nên rất ít người Mỹ làm, từ đó dẫn đến sự thiếu hụt lao động lớn.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Chính vì vậy Mỹ đưa ra một chương trình để thuê người lao động nước ngoài cho các công việc này. Định cư Mỹ diện eb3 được xây dựng dựa trên cơ sở các bên đều có lợi:</span></p>\r\n\r\n<p></p>\r\n\r\n<p><span style=\"font-size:18px;\">Về phía Mỹ: Người Mỹ tính toán trung bình chi phí nuôi dạy một đứa trẻ từ khi sinh ra đến năm 18 tuổi để lao động đóng thuế là 2 triệu USD, tuy nhiên với hình thức lao động này thì nước Mỹ sẽ có được lực lượng lao động có thể sử dụng ngay và đóng thuế cho đất nước này suốt đời.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Các doanh nghiệp Mỹ: có thể giải quyết được vấn đề thiếu hụt lao động, có được những lao động cho nhiều vị trí thiếu hụt với mức lương tối thiểu giúp tiết kiệm chi phí và hiệu quả kinh tế.</span></p>\r\n\r\n<p></p>\r\n\r\n<p><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\"><strong>Các tiêu chí xin visa EB-3</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Bạn có thể đủ điều kiện xin visa di trú diện ưu tiên này nếu như bạn là lao động có kỹ năng, chuyên gia hoặc các loại khác.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">“Lao động có kỹ năng” là những lao động mà công việc của họ yêu cầu tối thiểu phải trải qua 2 năm đào tạo hoặc kinh nghiệm, và không thuộc nhóm công việc tạm thời hay thời vụ.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">“Chuyên gia” là những người mà công việc của họ đ̣òi hỏi ít nhất phải có bằng đại học Mỹ hoặc bằng cấp nước ngoài tương đương.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Nhóm “các lao động khác” dành cho những lao động làm các công việc phổ thông yêu cầu thời gian đào tạo hay kinh nghiệm dưới 2 năm, và không thuộc nhóm công việc tạm thời hay thời vụ.</span></p>\r\n\r\n<p><img alt=\"\" src=\"public/images/visa-eb3-lao-dong-my-1.png\" style=\"width: 1633px; height: 919px;\" /></p>\r\n\r\n<p><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\"><strong>Lợi ích người định cư mỹ theo diện lao động là gì?</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Người lao động và gia đình gồm chồng/ vợ cùng với các con độc thân dưới 21 tuổi sẽ được cấp Thẻ Xanh VÔ ĐIỀU KIỆN để định cư tại Mỹ ngay khi đặt chân đến Mỹ.</span></li>\r\n	<li><span style=\"font-size:18px;\">Con đương đơn được học MIỄN PHÍ tại Mỹ đến năm lớp 12. Cơ hội vào Đại học danh tiếng cao hơn du học sinh và chi phí chỉ bằng 30% chi phí du học.</span></li>\r\n	<li><span style=\"font-size:18px;\">Có được hợp đồng lao động dài hạn tại Mỹ với lịch làm việc linh động, môi trường làm việc thân thiện và bình đẳng.</span></li>\r\n	<li><span style=\"font-size:18px;\">Đảm bảo việc làm ổn định và có được mức thu nhập cho bản thân (và gia đình) sinh sống lâu dài tại đất nước này.</span></li>\r\n	<li><span style=\"font-size:18px;\">Được mang song tịch.</span></li>\r\n	<li><span style=\"font-size:18px;\">Được hưởng đầy đủ các quyền lợi như một công dân Mỹ thực thụ (trừ quyền bầu cử).</span></li>\r\n	<li><span style=\"font-size:18px;\">Được hưởng đầy đủ các quyền lợi về giáo dục, tài chính, y tế, xã hội.</span></li>\r\n	<li><span style=\"font-size:18px;\">Các thành viên trong gia đình có quyền cư trú, học tập và làm việc tại bất cứ nơi nào trên nước Mỹ.</span></li>\r\n	<li><span style=\"font-size:18px;\">Được nhận bảo hiểm y tế, bảo hiểm lao động tại nơi làm việc theo quy định tại Mỹ.</span></li>\r\n	<li><span style=\"font-size:18px;\">Có điều kiện học hỏi thêm để trau dồi kiến thức, kinh nghiệm sau thời gian làm việc.</span></li>\r\n	<li><span style=\"font-size:18px;\">Được ra vào Mỹ không cần Visa.</span></li>\r\n	<li><span style=\"font-size:18px;\">Được xin nhập quốc tịch, bảo lãnh người thân.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:24px;\"><span style=\"color:#0000FF;\"><strong>Đối tượng tuyển dụng:</strong></span></span><br />\r\n </p>\r\n\r\n<p><span style=\"font-size:18px;\">– Nam, nữ trên 18 tuổi.<br />\r\n– Sức khỏe tốt.<br />\r\n– Lý lịch tư pháp trong sạch.<br />\r\n– Lợi thế cho đối tượng đã có gia đình.</span></p>\r\n', 'public/Images/visa-eb3-lao-dong-my.jpg', 'chuong-trinh-dinh-cu-my-theo-dien-lao-dong-eb3', 'Hoàng Hương Lan'),
(23, 'Visa J1', '18', '', '', '2018-05-16', 1, '<p><span style=\"font-size:18px;\">Visa Khách trao đổi – J1 – phù hợp cho những người đi Hoa Kỳ theo chương trình chính thức đã được các tổ chức giáo dục hoặc các tổ chức phi lợi nhuận khác phê chuẩn và bảo trợ. Các chuơng trình này do Cơ quan Giáo dục và Văn Hoá của Bộ Ngoại giao Hoa Kỳ chỉ định.<br />\r\n<br />\r\n<img alt=\"\" src=\"public/images/visa-j1-lao-dong-my.jpg\" style=\"width: 800px; height: 440px;\" /></span></p>\r\n\r\n<table border=\"1\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<caption>\r\n	<p><strong><span style=\"font-size:24px;\"><span style=\"color:#0000FF;\">Mô tả chi tiết về loại visa</span></span></strong></p>\r\n\r\n	<p></p>\r\n	</caption>\r\n	<tbody>\r\n		<tr align=\"center\" valign=\"top\">\r\n			<td scope=\"col\" valign=\"top\" width=\"115\">\r\n			<p><span style=\"font-size:18px;\"><strong>Miêu tả</strong></span></p>\r\n			</td>\r\n			<td scope=\"col\" valign=\"top\" width=\"54\">\r\n			<p><span style=\"font-size:18px;\"><strong>Loại Visa</strong></span></p>\r\n			</td>\r\n			<td scope=\"col\" valign=\"top\" width=\"216\">\r\n			<p><span style=\"font-size:18px;\"><strong>Mục đích đi Mỹ</strong></span></p>\r\n			</td>\r\n			<td scope=\"col\" valign=\"top\" width=\"192\">\r\n			<p><span style=\"font-size:18px;\"><strong>Hạn chế</strong></span></p>\r\n			</td>\r\n			<td scope=\"col\" valign=\"top\" width=\"90\">\r\n			<p><span style=\"font-size:18px;\"><strong>Thời hạn được phép ở Mỹ</strong></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr valign=\"middle\">\r\n			<td scope=\"row\" valign=\"top\" width=\"115\">\r\n			<p><span style=\"font-size:18px;\">Đương đơn tham gia chương trình làm việc và du lich trong dịp nghỉ hè.</span></p>\r\n			</td>\r\n			<td valign=\"top\" width=\"54\">\r\n			<p><span style=\"font-size:18px;\">J1</span></p>\r\n			</td>\r\n			<td rowspan=\"3\" valign=\"top\" width=\"216\">\r\n			<p><span style=\"font-size:18px;\">Bạn dự định tham gia một trong những chương trình sau:</span></p>\r\n\r\n			<ul>\r\n				<li><span style=\"font-size:18px;\">Các chương trình sau đại học</span></li>\r\n				<li><span style=\"font-size:18px;\">Học nghành y (với tư cách là thường trú dân hay bác sỹ nội trú thực tập)</span></li>\r\n				<li><span style=\"font-size:18px;\">Chương trình đại học ngắn hạn tại các khoa của trường đại học</span></li>\r\n				<li><span style=\"font-size:18px;\">Đào tạo nghề nghiệp</span></li>\r\n				<li><span style=\"font-size:18px;\">Các chương trình làm việc trong mùa hè</span></li>\r\n				<li><span style=\"font-size:18px;\">Các chương trình thực tập cho sinh viên đại học</span></li>\r\n				<li><span style=\"font-size:18px;\">Chương trình làm công ăn lương.</span></li>\r\n			</ul>\r\n			</td>\r\n			<td valign=\"top\" width=\"192\">\r\n			<p></p>\r\n			</td>\r\n			<td rowspan=\"4\" valign=\"top\" width=\"90\">\r\n			<p><span style=\"font-size:18px;\">Thời hạn trên mẫu DS 2019 công thêm 30 ngày trước khi chương trình bắt đầu và 30 ngày sau khi chương trình kết thúc.</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td scope=\"row\" valign=\"top\" width=\"115\">\r\n			<p><span style=\"font-size:18px;\">Người đi làm công (Au-pair).</span></p>\r\n			</td>\r\n			<td valign=\"top\" width=\"54\">\r\n			<p><span style=\"font-size:18px;\">J1</span></p>\r\n			</td>\r\n			<td valign=\"top\" width=\"192\">\r\n			<p></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td scope=\"row\" valign=\"top\" width=\"115\">\r\n			<p><span style=\"font-size:18px;\">Thực tập sinh và người được đào tạo.</span></p>\r\n			</td>\r\n			<td valign=\"top\" width=\"54\">\r\n			<p><span style=\"font-size:18px;\">J1</span></p>\r\n			</td>\r\n			<td valign=\"top\" width=\"192\">\r\n			<p><span style=\"font-size:18px;\">Bạn sẽ không thay vị trí hoặc làm gia tăng số lượng nhân viên do việc chiếm một vị trí mà lẽ ra thuộc về một nhân viên bình thường khác.</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td scope=\"row\" valign=\"top\" width=\"115\">\r\n			<p><span style=\"font-size:18px;\">Người phụ thuộc (vợ/ chồng hoặc con dưới 21 tuổi).</span></p>\r\n			</td>\r\n			<td valign=\"top\" width=\"54\">\r\n			<p><span style=\"font-size:18px;\">J2</span></p>\r\n			</td>\r\n			<td valign=\"top\" width=\"216\">\r\n			<p><span style=\"font-size:18px;\">Bạn sẽ sống cùng người thụ hưởng chương trình.</span></p>\r\n			</td>\r\n			<td valign=\"top\" width=\"192\">\r\n			<p><span style=\"font-size:18px;\">Không được phép làm việc ở Hoa Kỳ<sup>(*)</sup></span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"font-size:18px;\"><a href=\"http://vietnamese.vietnam.usembassy.gov/visa_category3.html#\" target=\"_self\" title=\"Quay về đầu trang\"> </a></span></p>\r\n\r\n<p><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\"><strong><a name=\"important\"></a>Quan trọng</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Bạn phải có mẫu DS-2019 khi đến phỏng vấn theo hẹn.  Chúng tôi không thể cấp visa cho tới khi bạn có thể nộp mẫu DS-2019.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Mẫu DS-2019 không phải là visa. Nếu bạn đến Hoa Kỳ với mẫu DS-2019 mà không có visa, bạn sẽ không được phép nhập cảnh vào Hoa Kỳ mà sẽ phải trở lại nước mà bạn đã xuất phát.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Để xin được loại visa này, bạn cần phải chứng minh:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Có nguồn tài chính đầy đủ để chi trả cho mọi chi phí của thời gian lưu trú và chuyến quay trở về.</span></li>\r\n	<li><span style=\"font-size:18px;\">Có khả năng tiếng Anh phù hợp.</span></li>\r\n	<li><span style=\"font-size:18px;\">Có ràng buộc chặt chẽ với nước nơi mình nộp đơn xin visa đi Hoa Kỳ.</span></li>\r\n	<li><span style=\"font-size:18px;\">Có bằng chứng chứng tỏ rằng mình sẽ rời khỏi Hoa Kỳ khi kết thúc chương trình.</span></li>\r\n</ul>\r\n\r\n <img alt=\"\" src=\"public/images/visa-j1-quan-trong.jpg\" style=\"width: 700px; height: 466px;\" />\r\n\r\n<p><strong><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\"><a name=\"docr\"></a>Giấy tờ cần có</span></span></strong></p>\r\n\r\n<p><span style=\"font-size:18px;\">Mỗi đương đơn cần phải nộp:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Hộ chiếu còn hạn sử dụng.</span></li>\r\n	<li><span style=\"font-size:18px;\">Trang xác nhận của Đơn xin visa điện tử (DS-160).</span></li>\r\n	<li><span style=\"font-size:18px;\">Ảnh cỡ 5cm x 5cm nền màu trắng mới chụp trong vòng sáu tháng.</span></li>\r\n	<li><span style=\"font-size:18px;\">Bằng chứng nộp phí xét đơn; Đương đơn có học bổng Fulbright, học bổng của Quỹ giáo dục Việt Nam (VEF) và thân nhân của họ không phải nộp phí xét đơn.</span></li>\r\n	<li><span style=\"font-size:18px;\">Bằng chứng nộp phí SEVIS; Đương đơn có học bổng Fulbright, học bổng của Quỹ giáo dục Việt Nam (VEF) không phải nộp phí xét SEVIS.</span></li>\r\n	<li><span style=\"font-size:18px;\">Mẫu DS-2019 do tổ chức bảo trợ đã được Chính phủ Hoa Kỳ phê chuẩn cấp cho từng đương đơn.</span></li>\r\n	<li><span style=\"font-size:18px;\">Giấy tờ hậu thuẫn cho đơn xin visa của bạn.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px;\">Những người phụ thuộc xin visa không cùng lúc với đương đơn chính cần phải có giấy tờ sau:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Bản sao mẫu DS-2019 của đương đơn chính mang visa J1.</span></li>\r\n	<li><span style=\"font-size:18px;\">Bản sao visa J1 của đương đơn chính.</span></li>\r\n	<li><span style=\"font-size:18px;\">Hồ sơ kết hôn hoặc khai sinh để chứng tỏ mối quan hệ với đương đơn chính.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px;\">Thực tập sinh, những người tham gia khóa đào tạo và sinh viên thực tập cần cung cấp:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Mẫu DS-7002 – Kế hoạch sắp xếp việc Đào tạo/Thực tập.</span></li>\r\n	<li><span style=\"font-size:18px;\">Kế hoạch đào tạo cụ thể.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px;\"><a href=\"http://vietnamese.vietnam.usembassy.gov/visa_category3.html#\" target=\"_self\" title=\"Quay về đầu trang\"> </a></span></p>\r\n\r\n<p><span style=\"font-size:24px;\"><span style=\"color:#0000FF;\"><strong><a name=\"2years\"></a>Hiểu rõ về quy định “Hai năm”</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Là người đã từng tham gia chương trình trao đổi khách, bạn có thể sẽ không được cấp visa định cư, visa hôn phu/hôn thê, visa làm việc ngắn hạn hoặc đi làm đại diện cho công ty ở Hoa Kỳ cho đến khi bạn đã trở về và sống ở nơi cư trú chính thức của bạn trong vòng ít nhất là hai năm sau khi chương trình trao đổi khách của bạn kết thúc nếu một trong những điều kiện sau áp dụng với bạn:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Chương trình trao đổi khách mà bạn tham gia là do chính phủ Hoa Kỳ hoặc chính phủ nước mà bạn là công dân hoặc cư trú trước đó tài trợ.</span></li>\r\n	<li><span style=\"font-size:18px;\">Bạn là công dân hoặc thường trú dân của một nước mà Bộ trưởng Ngoại giao Hoa Kỳ đã phê chuẩn rằng sẽ cần đến những người làm việc trong lĩnh vực đòi hỏi kỹ năng đặc biệt và đó là lý do tại sao bạn cần tham gia vào chương trình trao đổi khách này.</span></li>\r\n	<li><span style=\"font-size:18px;\">Bạn là bác sĩ đến Hoa Kỳ với mục đích học tập hoặc được đào tạo thêm về nghành y (trừ những chương trình liên quan đến nghiên cứu giảng dạy và tư vấn). </span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px;\"><a href=\"http://vietnamese.vietnam.usembassy.gov/visa_category3.html#\" target=\"_self\" title=\"Quay về đầu trang\"> </a></span></p>\r\n\r\n<p><strong><span style=\"font-size:24px;\"><span style=\"color:#0000FF;\"><a name=\"spouses\"></a>Những người phụ thuộc</span></span></strong></p>\r\n\r\n<p><span style=\"font-size:18px;\">Vợ chồng và/hoặc con cái nếu không dự định sống ở Hoa Kỳ cùng với đương đơn chính mà chỉ muốn đi thăm ngắn hạn có thể xin visa du lịch loại B2.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">(*)  Vợ chồng của khách trao đổi không được phép làm việc ở Hoa Kỳ bằng visa J2 trừ khi có giấy phép của Sở Di trú và Nhập tịch Hoa Kỳ (USCIS).  Đơn xin làm việc chỉ có thể được nộp sau khi người phụ thuộc mang visa J2 đã sang tới Hoa Kỳ và sẽ được xem xét tuỳ thuộc vào chính sách hiện hữu tại thời điểm đó.</span></p>\r\n', 'public/Images/visa-j1-lao-dong-my.jpg', 'visa-j1', 'Hoàng Hương Lan'),
(24, 'Tôi Thành Triệu Phú Ở Mỹ Thế Nào ', '21', '', '', '2018-05-16', 1, '<p><span style=\"font-size:18px;\">Từ lâu tôi vẫn luôn mong làm thế nào góp một phần khả năng cho cộng đồng Việt Nam ở Mỹ đựợc thành công, để quê hương Việt Nam được giàu mạnh. Tôi lấy hết can đảm để viết lên đây những gì đã trải qua trên đất Mỹ, giúp tôi có được ngày hôm nay.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Tôi sang Mỹ năm 22 tuổi và đã sống ở Mỹ hơn 22 năm. 10 năm đầu không biết có phải là khó khăn không, nhưng tôi thường dậy vào lúc 3h sáng và tối thì khoảng 7h mới về nhà. Sáng sớm tôi đi thả báo, rồi đi học và làm những việc khác suốt 10 năm. Không phải cuộc sống ở Mỹ quá khó khăn nên tôi phải làm vậy. Ở đây đi học được lĩnh tiền phụ cấp và làm thêm khoảng 15 tiếng mỗi tuần thì đã sống thoải mái. Nhưng vì không muốn xin tiền trường (Financial Aid) và ấp ủ tham vọng làm giàu quá lớn, tôi sẵn sàng chịu vất vả để hy vọng dư một ít tiền.<br />\r\n<br />\r\n<img alt=\"\" src=\"public/images/lam-them-o-my.jpg\" style=\"width: 450px; height: 299px;\" /></span><br />\r\n<br />\r\n </p>\r\n\r\n<p><span style=\"font-size:18px;\">Thành công đầu tiên và niềm vui lớn nhất đầu tiên sau 10 năm trên đất Mỹ là tấm bằng kỹ sư điện toán. Ngày tôi ra trường mọi người trong gia đình ai cũng rất mừng. Nhưng niềm vui nhanh chóng biến thành nỗi thất vọng. Tôi sớm nhận ra với mức lương 50.000 USD một năm chưa trừ thuế ở vùng Silicon Valley thì tôi chỉ làm công như vậy suốt đời và tham vọng làm giàu khó thành hiện thực. Tuy vậy tôi vẫn giữ việc làm vì vẫn chưa có cách nào khác. Một năm sau khi ra trường, tôi bắt đầu tìm nhà để mua. Sự thất vọng càng rõ ràng hơn vì tôi vẫn chưa dành được 10% để mua căn nhà khoảng 300.000 USD ở Silicon Valley. Dù có mua được đi nữa thì với mức lương đó cũng chỉ vừa đủ để trả tiền ngân hàng hàng tháng và một cuộc sống sẽ rất chật vật.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Suy nghĩ để tìm một hướng khác cho cuộc sống lớn dần lên trong tôi mỗi ngày. Trong một lần về thành phố Sacramemto chơi, thủ phủ của California cách Silicon Valley khoảng hai tiếng và cách thành phố San Francisco khoảng 1h30 phút lái xe, tôi thấy giá nhà ở đây quá chênh lệch và quá rẻ. Giá nhà khởi đầu từ 10.000 USD cho đến vài trăm nghìn đôla. Một hướng đi mới và một tia sáng mới cho những ý tưởng trong đầu đã đốt lên trong tôi. Khoảng vài tuần sau đó tôi đã nhanh chóng rút ra 10.000 USD từ thẻ tín dụng để mua một căn nhà gần như tệ nhất và ở khu xấu nhất tại đây.<br />\r\n<br />\r\n<img alt=\"\" src=\"public/images/Sacramento.jpg\" style=\"width: 1710px; height: 544px;\" /></span><br />\r\n </p>\r\n\r\n<p><span style=\"font-size:18px;\">Sau khi mua xong, từ thứ hai đến thứ sáu tôi đi làm ở San Jose, chiều thứ sáu tôi đi thẳng về đây để sửa nhà và đến tối chủ nhật thì về lại San Jose để chuẩn bị cho thứ hai đi làm. Tôi chưa bao giờ sửa nhà nên không biết gì cả, nhưng vì không có nhiều tiền nên tôi cố gắng tự làm lấy. Những chỗ nào hư nếu thấy khó khăn một chút thì tôi vẽ lại trước lúc tháo ra. Cứ như vậy sau hai tháng làm những ngày cuối tuần và tốn thêm khoảng 3.000 USD tiền mua vật liệu, căn nhà của tôi đã ở được. Vì đang làm ở San Jose nên tôi quyết định cho thuê.<br />\r\n<br />\r\n<img alt=\"\" src=\"public/images/dau-tu-my.jpg\" style=\"width: 960px; height: 638px;\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Tôi đăng báo và cho thuê 500 USD mỗi tháng. Rất vui là người Mexico đến xem nhà đầu tiên thì đã thuê nó. Như vậy tôi mua căn nhà 10.000 USD, tiền sửa chữa 3.000 USD, tổng cộng là 13.000 USD. Tôi trả thẻ tín dụng 200 USD, những chi phí khác như thuế và bảo hiểm 100 USD mỗi tháng nữa là 300 USD. Tôi còn dư lại 200 USD để cộng thêm vào lương thu nhập hàng tháng của tôi. Niềm vui chưa dừng lại ở đó vì sau khi tôi gọi Bank of America đến định giá căn nhà của tôi sau khi sửa xong và cho mướn là 35.000 USD. Họ cho tôi rút ra 25.000 USD.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">À, Bank of America họ cũng đâu khờ lắm đâu, vì căn nhà tôi mua 10.000 USD. Nếu gọi người thầu đến sửa thì cũng đã mất từ 15.000 - 25.000 USD. Cộng thêm công việc tôi đang làm là 50.000 USD một năm, số tiền họ cho tôi rút ra từ căn nhà là một sự đầu tư rất tốt đối với họ qua tôi. Tôi cảm thấy rất mừng vì số tiền tôi đem về sau hai tháng quá lớn so với số tiền dành dụm được sau 10 năm ở trường và một năm đi làm. Như vậy tiền nhà tôi cho mướn vừa đủ để trả tiền hàng tháng của thẻ tín dụng. Tiền Bank of America cho rút ra, tôi dùng để mua thêm hai căn nhà nữa và cứ cách đó thì sau hai năm tôi đã có 10 căn nhà như vậy.<br />\r\n<br />\r\n<img alt=\"\" src=\"public/images/lam-viec-my.jpg\" style=\"width: 1000px; height: 563px;\" /></span><br />\r\n </p>\r\n\r\n<p><span style=\"font-size:18px;\">Cuộc sống của tôi đã thay đổi từ đó. Sau khi tôi mua xong 10 căn thì giá nhà ở đây bắt đầu đi lên. Tôi mạnh dạn quyết định nghỉ làm ở công ty măc dù công ty tôi đang làm rất tốt và chúng tôi đang thực hiện hợp đồng cho quốc phòng. Ngày tôi viết giấy xin nghỉ, sếp của tôi nói công ty có thể cho tôi nghỉ ba tháng và nếu muốn thì quay lại nhưng tôi quyết tâm bỏ hẳn để phát triển công việc đầu tư nhà cửa. Tôi thầm nói rằng dù có thất bại thì tôi cũng chọn con đường khác chứ không thể làm công ty chật vật trong cuộc sống như vậy.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Sau đó, tôi bắt đầu bán bớt một vài căn nhà, mua đất tự xây và mướn thêm người xây nhà lớn hơn để bán từng cái một. Giai đoạn xây dựng ở đây tôi cũng chưa có kinh ngiệm nhưng cứ vừa làm vừa chạy đến ở những nơi họ đang làm nhà để hỏi và lấy kinh nghiệm. Cứ như vậy, mua đất xây và bán, vốn tôi lớn dần lên. Tôi bắt đầu đầu tư vào những trung tâm thương mại cỡ nhỏ. Sau năm năm từ 1999 đến cuối năm 2004 thì vốn tôi đã hơn 2 triệu USD. Vào năm 2004 khi giá nhà ở đây đang lên vùn vụt, tôi bắt đầu tính toán thấy được sư mạo hiểm và không đầu tư thêm ở đây nữa. Trong thời điểm đó không biết làm gì để phát triển thêm, và cũng rảnh rỗi nên tôi quyết định về Việt Nam chơi. Khi về Việt Nam, tôi phát hiện thị trường địa ốc ở đây đang có tiềm năng rất lớn nên tôi đã bán bớt ở Mỹ và đầu tư cùng với thân nhân của tôi ở Việt Nam.<br />\r\n<br />\r\n<img alt=\"\" src=\"public/images/co-hoi-lam-viec-my.jpg\" style=\"width: 720px; height: 440px;\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Sau lần khủng hoảng kinh tế vừa qua, dù đã tính toán và chuẩn bị trước, nhưng tôi vẫn phải chia sẻ sự mất mát cùng với những người đầu tư địa ốc ở Mỹ. Tuy nhiên, những gì còn lại qua sự chuẩn bị đủ cho tôi cảm thấy vui, hạnh phúc và đầu tư lại với một chiến lược mới hơn, lớn hơn, tiềm năng hơn. Rất nhiều người ở đây đang vật lộn với cơn khủng hoảng vừa qua, nhưng tôi cám ơn nó đã cho tôi cơ hội để đầu tư vào những gì mà trước đó không thể làm.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Cuộc sống ở Mỹ là cơ hội và đúng ra không vất vả như tôi đã trải qua, nhưng tôi chấp nhận vất vả vì tham vọng làm giàu. Tôi không cảm thấy hối tiếc vì 10 năm ấy, vì đó là những kiến thức vô giá tôi đã học được, trở thành hành trang mãi cho tôi trong cuộc sống.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\"><strong><font color=\"#4f4f4f\">Các bạn cũng có thể bắt đầu như tôi 10 năm trước</font></strong>, mua những căn nhà rất nhỏ và rất rẻ. Hoặc bạn bắt đầu mua một miếng đất rất nhỏ hay một miếng đất rất lớn, nhưng xây một căn nhà rất nhỏ cỡ vài chục nghìn USD để vừa ở và vừa đầu tư, chứ không cần phải vay ngân hàng để mua một căn nhà đắt giá rồi quần quật đi làm trả tiền hàng tháng.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Trong bất cứ đầu tư nào, bạn luôn cần phải tỉnh táo để biết lúc nào nên dừng và giới hạn theo khả năng quản lý và dòng vốn của bạn. Bạn cần phải tính toán chi tiêu và đầu tư cho hợp lý. Nếu bạn mạo hiểm quá so với số vốn và số tiền bạn có thì dù có bao nhiêu tiền bạn cũng sẽ cảm thấy rất túng thiếu. Nếu mạo hiểm để vay mượn nhiều quá trong bất cứ đâu tư nào so với số vốn mình có, thì nó cũng không khác gì đang chơi cổ phiếu hay đánh bạc.<br />\r\n<br />\r\n<img alt=\"von dau tu\" src=\"public/images/von-dau-tu.jpeg\" style=\"width: 600px; height: 400px;\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Thành công không thể có khi tiền bạc của bạn quá nhiều nhưng sức khỏe của bạn quá kém. Cân bằng giữa sức khỏe và tiền bạc sẽ cho bạn sự vui vẻ tự tin để tỉnh táo cho những quyết định trong công việc. Mỗi buổi sáng tôi chỉ cần 10 phút tắm hơi và buổi chiều 40 phút chạy máy, cộng với ăn uống cẩn thận đã cho tôi cân bằng được sức khỏe.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Có người thành công và người thất bại trên đất Mỹ, nhưng đối với tôi nó là một thiên đường. Khi đến đây, tôi không có gì ngoài hai bàn tay trắng và một ít kiến thức vì tôi đã bỏ học từ khi còn tiểu học ở Việt Nam. Tôi thầm cám ơn xã hội Mỹ đã tạo cơ hội cho tôi đạt được một kiến thức ở trường để làm một hành trang vô giá theo tôi mãi trong cuộc sống, dù nó là một cái giá mà tôi đã phải trả bằng sự vất vả suốt 10 năm. Cũng từ nơi này mà dù ít hay nhiều tôi đã góp phần giúp chị em tôi ở Việt Nam. Số tiền tôi có để cùng với thân nhân đầu tư ở Việt Nam, dù không phải là quá lớn, nhưng nó cũng là một phần trong số gần 3-4 tỷ USD khi đó người Việt ở nước ngoài đã cùng nhau gửi về Việt Nam hàng năm góp phần xây dựng quê hương.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Thành công của tôi trên đất Mỹ so với rất nhiều người Việt Nam ở đây thì quá nhỏ bé. Nhưng tôi muốn chia sẻ kinh nghiệm khởi đầu từ số vốn nhỏ bé cùng với việc tôi làm nhiều người Việt đều có thể làm theo trong thời điểm khủng hoảng này. Hãy thay đổi suy nghĩ và nhìn khủng hoảng kinh tế như là một sự điều chỉnh để tạo cơ hội làm giàu cho bạn. Mỗi lần khủng hoảng là một cơ hội để bạn làm giàu, nếu khủng hoảng tiếp lần hai thì bạn sẽ có đến hai cơ hội để làm giàu. Nhưng nếu bạn không thay đổi suy nghĩ và biết cách để tận dụng cơ hội này thì nếu khủng hoảng tiếp lần hai xảy đến, bạn sẽ đón nhận thêm nữa một sự khó khăn chồng chất lên khó khăn hiện tại của bạn.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Những dòng tâm sự này nếu không nghĩ đến góp phần xây dựng cộng đồng Việt Nam ở Mỹ, và không nghĩ đến góp phần xây dựng quê hương Việt Nam ngày một tốt đẹp hơn thì tôi đã không đủ can đảm để bỏ thời gian viết ra. Hy vọng những trải nghiệm của tôi có thể định hướng cho một vài người có tham vọng như tôi.<br />\r\n<br />\r\nChúc các bạn thành công.</span></p>\r\n', 'public/Images/ADLAI%20E._STEVENSON_HIGH_SCHOOL_DISTRICT.png', 'toi-thanh-trieu-phu-o-my-the-nao', 'Hoàng Hương Lan'),
(25, 'Định cư EB-5 Nước cờ đầu tư thông minh và dài hạn', '21', '', '', '2018-05-16', 1, '<span style=\"font-size:18px;\">Một nước Mỹ năng động với kinh tế phát triển, phúc lợi xã hội tốt và hệ thống giáo dục hiện đại, cơ hội đầu tư mở rộng… luôn là điểm đến hấp dẫn với nhiều người dân nhập cư trên Thế giới, đặc biệt là Việt Nam. Những năm gần đây, việc định cư Mỹ theo diện đầu tư EB-5 đã mở ra con đường mới cho các nhà đầu tư và gia đình người Việt một cơ hội phát triển kinh doanh và sinh sống trên tầm quốc tế. Vì thế, không sai khi nói rằng định cư ở Mỹ bằng con đường đầu tư EB-5 được xem là một nước cờ đầu tư thông minh và dài hạn.</span> <img alt=\"\" src=\"public/images/dau-tu-eb5.jpg\" style=\"width: 665px; height: 401px;\" /><br />\r\n<br />\r\n<br />\r\n \r\n<p><strong><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\">Định cư Mỹ theo diện đầu tư EB-5: Cơ hội thời toàn cầu hoá</span></span></strong></p>\r\n\r\n<p><span style=\"font-size:18px;\">Chương trình đầu tư định cư Mỹ EB-5: Đầu tư 500.000 USD trong vòng 5 năm được hoàn lại dành cho các nhà đầu tư có khả năng tài chính trên 1 triệu USD, cả gia đình nhà đầu tư được cấp thẻ xanh Mỹ sau 1,5 đến 2 năm. Nhận định về xu hướng nhập cư Mỹ theo chương trình đầu tư EB-5, mới đây, tại hội nghị lớn nhất thế giới về EB-5 tại Las Vegas Hoa Kỳ, các chuyên gia đã chỉ ra những cơ hội lớn của các nhà đầu tư vào chương trình này trong suốt những năm qua.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Theo đó, khi tham gia vào chương trình EB-5, nhà đầu tư cùng gia đình sẽ được nhận thẻ xanh và hưởng đầy đủ quyền lợi của một thường trú nhân Hoa Kỳ, cụ thể: có song song hai quốc tịch, được sống và làm việc ở một đất nước văn minh, phúc lợi tốt nhất thế giới, được mở rộng phát triển kinh doanh tầm quốc tế. Quan trọng hơn, con cái các nhà đầu tư, những tài sản quý giá nhất của các ông bố bà mẹ sẽ được hưởng nền giáo dục hiện đại, chất lượng, được miễn phí học tập từ mẫu giáo đến hết trung học phổ thông tại các trường công lập tại Mỹ, mức học phí đại học, cao học rất thấp. So ra với chi phí du học thì việc đầu tư Định cư EB-5 có chi phí thấp hơn nhiều với những lợi ích to lớn.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Do đó thấy rõ, con đường định cư nước Mỹ thông qua chương trình EB-5 không phải là việc đầu tư kiếm lợi nhuận, mà là đầu tư dài hạn, có chiều sâu, là một bước “đi tắt đón đầu” giúp nhà đầu tư thêm “thuận buồm xuôi gió“ để con cái và thế hệ sau của mình được chạm vào những ước mơ và con đường tương lai tươi sáng.<br />\r\n<br />\r\n<img alt=\"\" src=\"public/images/lao-dong-eb3-my.jpg\" style=\"width: 740px; height: 487px;\" /></span></p>\r\n\r\n<p><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\"><strong>Đầu tư định cư Mỹ EB-5: Điều nên cẩn trọng</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Để có visa Mỹ EB-5 thành công, các nhà đầu tư cũng cần lưu ý, cơ hội nào cũng sẽ phải đi kèm cùng thử thách. Và trong trường hợp định cư bằng con đường đầu tư EB-5, các nhà đầu tư phải luôn cẩn trọng, chọn cho mình nhà tư vấn định cư có kinh nghiệm, có khả năng và có chất lượng cao để hồ sơ được chấp thuận thành công, và đặc biệt, tìm dự án đầu tư khả quan, minh bạch, khả thi về tiến độ và được hoàn vốn nhanh chóng.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Với kinh nghiệm là một đơn vị Tư vấn đầu tư định cư Mỹ EB-5 uy tín hàng đầu tại Việt Nam có tỷ lệ hồ sơ thành công là 100% và là đại diện duy nhất Việt Nam được mời đến phát biểu ở Đại hội lớn nhất về EB-5 tại Las Vegas (Mỹ) vừa qua, Bà Dương Cam Ly – Giám đốc Công ty tư vấn định cư  chia sẻ: “Những dự án EB-5 mà các nhà đầu tư lựa chọn phải đảm bảo: Chủ đầu tư là thương hiệu uy tín hàng đầu ở Mỹ, dự án nằm trong vùng trọng điểm của Chính phủ Mỹ, có tài sản cố định hiện hữu, hoạt động lợi nhuận hiệu quả và công việc thặng dư sẵn sàng… để đáp ứng được hai mục tiêu quan trọng là Thẻ Xanh và Bảo toàn vốn an toàn”.</span></p>\r\n\r\n<p><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\"><strong>Các cơ hội khác cho các nhà đầu tư EB-5</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Ngoài cơ hội có thẻ xanh Mỹ mà chương trình EB-5 đã mở ra cho nhiều nhà đầu tư Việt, các chuyên gia còn bày tỏ quan điểm: đồng tiền thông minh là đồng tiền đầu tư vào Mỹ. Lý do là tại Mỹ, các nhà đầu tư EB-5 có thể tìm thấy nhiều cơ hội kinh doanh và phát triển với sự bảo vệ và hậu thuẩn vững vàng. Mà với dòng chuyển động không ngừng của nền kinh tế Mỹ như hiện nay, các chủ doanh nghiệp Việt có thừa khả năng, cơ hội và vị thế phát triển ở một thị trường đầy năng độngnhư Mỹ.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Hiện nay, dự án EB-5 hàng đầu Thế giới đang được ImmiCa giới thiệu độc quyền tại Việt Nam là khu thượng lưu Hudson Yards New York – Tòa tháp Manhattan Tower. Đây là dự án ấn tượng được phát triển bởi tập đoàn bất động sản lớn nhất Hoa Kỳ Related và chỉ huy động thêm 10% vốn từ các nhà đầu tư EB-5 để mang lại lượng công việc cũng như hiệu quả kinh tế vượt trội. Đặc biệt, Hudson Yards còn được ví von là “người khổng lồ” của Thành phố New York, vì toàn bộ dự án thành phố Hudson Yards sẽ được kiến tạo là một thành phố thu nhỏ, có đầy đủ các hệ thống cơ sở hạ tầng cho nhu cầu cuộc sống mới, hiện đại, đẳng cấp và mang tầm quốc tế. Một cơ hội được hợp tác với một người khổng lồ của nước Mỹ, mang lại cho các nhà đầu tư một sự hào hứng và không kém phần tự hào. Đặc biệt, một sự kiện nổi bật về dự án này dành cho những nhà đầu tư quan tâm đến chương trình sẽ diễn ra tại Việt Nam ngày 26/02/2016, buổi tọa đàm “Đầu tư Mỹ nhận thẻ xanh – Xây khu thượng lưu trong lòng New York”. Đây là một sự kiện đặc biệt với sự có mặt của Luật sư di trú hãng Luật Greenberg Traurig và đại diện chủ dự án – Tập đoàn Related.<br />\r\n<img alt=\"\" src=\"public/images/eb5-dau-tu-tai-my.jpg\" style=\"width: 781px; height: 412px;\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Vì vậy, định cư bằng con đường đầu tư Mỹ EB-5 vẫn đang là một phương thức có lợi thế nổi bật đem lại nhiều lợi ích và hạn chế thấp nhất rủi ro cho các nhà đầu tư. Chưa kể, sau 5 năm, số vốn đầu tư ban đầu sẽ nhanh chóng được hoàn về cho chủ nhân và đó cũng là thời điểm cả gia đình nhà đầu tư chính thức trở thành những công dân nước Mỹ, kinh doanh gia đình trên đà thuận lợi, con cái học hành thành công,cuộc sống môi trường tốt đẹp… Vẹn cả đôi đường, âu cũng là một nước cờ hay!</span></p>\r\n\r\n<p><span style=\"font-size:18px;\"><strong>Thùy Linh</strong></span></p>\r\n', 'public/Images/eb5-dau-tu-tai-my.jpg', 'dinh-cu-eb-5-nuoc-co-dau-tu-thong-minh-va-dai-han', 'Hoàng Hương Lan'),
(26, 'Đầu tư EB5  cơ hội và thách thức cho nhà đầu tư Việt Nam', '21', '', '', '2018-05-16', 1, '<div style=\"text-align: center;\"><span style=\"font-size:36px;\"><span style=\"color:#FF0000;\">Đầu tư EB5 – cơ hội và thách thức cho nhà đầu tư Việt Nam</span></span><br />\r\n </div>\r\n\r\n<p><span style=\"font-size:18px;\">Chương trình đầu tư EB-5 ra đời không chỉ kích thích sự phát triển của nền kinh tế Hoa Kỳ mà còn mang đến nhiều cơ hội không thể ngờ tới cho nhà đầu tư. Đó cũng chính là lý do chương trình EB-5 ngày càng nhận được rất nhiều sự quan tâm từ nhà đầu tư Việt Nam. Tuy nhiên, bên cạnh những cơ hội thì nhà đầu tư EB-5 cũng không thể tránh khỏi những thách thức khi đầu tư vào chương trình này.</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px;\"><img alt=\"\" src=\"public/images/co-hoi-dau-tu-eb5.jpg\" style=\"width: 768px; height: 402px;\" /><br />\r\n<br />\r\n<em>Đầu tư EB5 – cơ hội và thách thức cho nhà đầu tư Việt Nam</em></span></p>\r\n\r\n<span style=\"color:#0000FF;\"><span style=\"font-size:24px;\"><strong>Vậy những cơ hội tuyệt vời đó là gì?</strong></span></span>\r\n\r\n<p><span style=\"font-size:18px;\">Chính vì chương trình đầu tư EB-5 đang là một trong những chương trình định cư hấp dẫn nhất hiện nay với số tiền đầu tư $500.000 USD đối với mỗi nhà đầu tư.  Đây là cơ hội định cư Mỹ và hưởng những quyền lợi thường trú nhân cho cả gia đình, với thời gian xét duyệt hồ sơ được đánh giá là nhanh nhất hiện nay. Hơn nữa, nhà đầu tư và gia đình (vợ/chồng và con cái dưới 21 tuổi chưa lập gia đình) cũng được hưởng những quyền lợi đặc biệt khác như quyền tự do sinh sống tại bất cứ nơi nào trên nước Mỹ, nhập cảnh không cần Visa vào hơn 185 quốc gia trên toàn thế giới, thuận lợi cho việc phát triển kinh doanh của nhà đầu tư, cũng như con cái được hưởng một trong những nền giáo dục thuộc hàng bậc nhất trên thế giới</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px;\"><img alt=\"\" src=\"public/images/co-hoi-dau-tu-eb5-2.jpg\" style=\"width: 768px; height: 402px;\" /><br />\r\n<br />\r\n<em>Nhà đầu tư và gia đình cũng được hưởng những quyền lợi đặc biệt khác như quyền tự do sinh sống tại bất cứ nơi nào trên nước Mỹ</em></span></p>\r\n\r\n<p></p>\r\n\r\n<h2><span style=\"font-size:24px;\"><span style=\"color:#0000FF;\"><strong>Nhà đầu tư gặp phải những thách thức gì khi đầu tư vào chương trình EB-5?</strong></span></span></h2>\r\n \r\n\r\n<h3><span style=\"font-size:18px;\"><strong>Chi phí đầu tư tăng từ 500.000 USD lên 1,35 triệu USD</strong></span></h3>\r\n \r\n\r\n<p><span style=\"font-size:18px;\">Theo nguồn tin từ Hiệp Hội Đầu tư Hoa Kỳ (IIUSA) thì sau ngày 8 tháng 12 năm nay, mức đầu tư vào chương trình EB-5 có khả năng tăng từ 500.000 USD lên 1,35 triệu USD. Đây được xem như là một thách thức đối với nhà đầu tư trên thế giới, bao gồm Việt Nam bởi theo như dự đoán từ các chuyên gia trong lĩnh vực di trú thì nguồn thông tin này có thể trở thành sự thật. Vì vậy, cơ hội quyết định đầu tư EB-5 dành cho nhà đầu tư còn chưa đầy 2 tháng nữa. Nhà đầu tư nên nhanh chóng đưa ra quyết định kịp thời để hưởng các quyền lợi của một thường trú nhân Hoa Kỳ với chi phí thấp 500.000 USD.</span></p>\r\n\r\n<figure id=\"attachment_4462\"><img alt=\"\" src=\"public/images/co-hoi-dau-tu-eb5-3.jpg\" style=\"width: 768px; height: 402px;\" /><br />\r\n \r\n<figcaption><span style=\"font-size:18px;\">    <em>  Sau ngày 8 tháng 12 năm nay, mức đầu tư vào chương trình EB-5 có khả năng tăng từ 500.000 USD lên 1,35 triệu USD</em></span></figcaption>\r\n</figure>\r\n\r\n<h3><br />\r\n<span style=\"font-size:18px;\"><strong>Lượng Visa EB-5 của Việt Nam có nguy cơ quá tải vào năm 2018.</strong></span></h3>\r\n \r\n\r\n<p><span style=\"font-size:18px;\">Từ số liệu thống kê mới nhất cho thấy nhu cầu về Visa EB-5 của Việt Nam đứng thứ 2 trong số lượng nhà đầu tư tham gia chương trình này. Nổi bật nhất là thị trường Trung Quốc, nước luôn đứng vị trí số 1 với lượng hồ sơ tồn đọng nhiều nhất trong những năm qua, đồng nghĩa với việc nhà đầu tư Trung Quốc đang bị giới hạn 10 năm nhận thẻ xanh. Trong tương lai không xa, Việt Nam sẽ trở thành Trung Quốc thứ hai về số lượng hồ sơ EB-5 bị tồn đọng, thời gian chờ không phải là 2 năm mà có thể là 3,4 năm hoặc 5 năm. Đây là một vấn đề hết sức quan ngại đối với nhà đầu tư Việt Nam. Cho nên, đầu tư đúng lúc, đúng thời điểm không chỉ giúp nhà đầu tư tiết kiệm chi phí mà còn cơ hội trở thành thường trú nhân cũng sẽ nhanh hơn.</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px;\"><img alt=\"\" src=\"public/images/co-hoi-dau-tu-eb-5-4.jpg\" style=\"width: 768px; height: 402px;\" /><br />\r\n<em>Từ số liệu thống kê mới nhất cho thấy nhu cầu về Visa EB-5 của Việt Nam đứng thứ 2 trong số lượng nhà đầu tư tham gia chương trình này</em></span></p>\r\n\r\n<p></p>\r\n\r\n<h3><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\"><strong>Lựa chọn đầu tư nhầm dự án EB-5</strong></span></span></h3>\r\n\r\n<p><span style=\"font-size:18px;\">Đây không còn là một vấn đề quá xa lạ đối với nhiều nhà đầu tư Việt Nam nói riêng và nhà đầu tư EB-5 nói chung. Chưa có kế hoạch thẩm định dự án bài bản trước khi quyết định đầu tư là sai lầm của không ít nhà đầu tư. Thậm chí, sau khi đã thực hiện đầu tư, có nhà đầu tư còn không biết dự án tiến triển như thế nào và hoạt động ra sao, nguồn vốn đầu tư của họ sẽ đi về đâu, dự án lấy doanh thu từ đâu để hoàn vốn, v.v… Do đó, việc chọn một đội ngũ tư vấn uy tín, chuyên nghiệp và giàu kinh nghiệm trong việc thẩm định một dự án EB-5 an toàn, minh bạch để đồng hành cùng nhà đầu tư trên con đường định cư cho cả gia đình là rất quan trọng.</span><br />\r\n </p>\r\n\r\n<p><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\"><strong>Nhà đầu tư có thể tham khảo một số dự án </strong></span></span><br />\r\n </p>\r\n\r\n<p><span style=\"font-size:18px;\">Từ những cơ hội và thách thức nêu trên, nhà đầu tư hoàn toàn nắm rõ những gì mình đang có và đang gặp phải trong quá trình thực hiện hồ sơ định cư cho cả gia đình. Hiện nay, đa số nhà đầu tư trên thế giới, trong đó có Việt Nam rất am hiểu về thị trường EB-5. Nên việc lựa chọn đơn vị tư vấn giàu kinh nghiệm là điều kiện tiên quyết để cùng nhà đầu tư đề ra những phương án an toàn trong từng bước chuẩn bị hồ sơ, chọn lựa dự án nhằm hạn chế tối đa những rủi ro không đáng có khi quyết định đầu tư định cư Mỹ, nhanh chóng mang lại thẻ xanh cho cả gia đình.</span></p>\r\n', 'public/Images/co-hoi-dau-tu-eb5-2.jpg', 'dau-tu-eb5-co-hoi-va-thach-thuc-cho-nha-dau-tu-viet-nam', 'Hoàng Hương Lan'),
(27, 'ĐẦU TƯ MỸ L1 ĐỂ THÁCH THỨC MỞ ĐƯỜNG CHO NHỮNG TẦM CAO MỚI.', '22', '', '', '2018-05-16', 1, '<p><span style=\"font-size:18px;\">Mỹ là một trong những thị trường phát triển và năng động bậc nhất thế giới. Bên cạnh đó kinh tế, chính trị, văn hóa, nghệ thuật của Mỹ ảnh hưởng rất nhiều đến trật tự thế giới. Không nói khoa trương khi nhắc đến nước Mỹ là nhắc đến một quốc gia đầy mơ ước của bao thế hệ doanh nhân thành đạt trên khắp năm châu và dĩ nhiên rằng được làm việc, được kinh doanh trên đất Mỹ là điều tuyệt vời nhất đối với bất cứ nhà đầu tư ngoại quốc nào.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Đã hàng thập kỉ trôi qua, Mỹ vẫn đứng sừng sững như một cánh cổng lớn mà ở đó, ai đủ tiềm năng, đủ tầm vóc và đủ chí hướng mới có thể mở nó ra, nhìn thấy và vươn đến những tầm cao mới.<br />\r\n<br />\r\n<img alt=\"\" src=\"public/images/l1a.jpg\" style=\"width: 999px; height: 633px;\" /></span><br />\r\n </p>\r\n\r\n<p><span style=\"font-size:18px;\">Thử đặt ra một câu hỏi, Mỹ có gì mà tuyệt vời đến vậy?</span><br />\r\n </p>\r\n\r\n<p><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\"><strong>Trên hết, Mỹ là quốc gia sở hữu luật doanh nghiệp chặt chẽ và gần như là tiêu chuẩn của thế giới.</strong></span></span><br />\r\n </p>\r\n\r\n<p><span style=\"font-size:18px;\">Hãy tìm đến những nhà đầu tư ngoại quốc trên đất Mỹ, hay chỉ cần tìm đến STAN STANART VIETNAM, khách hàng sẽ được thông suốt về hệ thống thuế cũng như hệ thống pháp lý doanh nghiệp tuyệt vời ở đây. Theo thời gian, khách hàng sẽ cảm nhận được những điều hay mà khi kinh doanh, đầu tư ở Việt Nam không thể có được. Không phải ngẫu nhiên mà Mỹ được mệnh danh là miền đất hứa cho các công ty, bỡi lẽ ở đây khách hàng luôn được tạo điều kiện thuận lợi hàng đầu cho những dự án của mình.</span><br />\r\n </p>\r\n\r\n<p><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\"><strong>Tiếp đến, Mỹ là bàn đạp quốc tế vững chắc nhất.</strong></span></span><br />\r\n </p>\r\n\r\n<p><span style=\"font-size:18px;\">Mỹ được biết đến như một thước đo tiêu chuẩn của hầu hết những sản phẩm trên thị trường quốc tế và một công ty tại Mỹ chắc chắn luôn được đặt lên hàng đầu so với những nền kinh tế khác. Bởi lẽ đó, nếu như những sản phẩm, những dự án của bạn đã vượt qua tiêu chuẩn Mỹ và được thực hiện trên đất Mỹ, thì không còn gì phải lo lắng nữa, nó sẽ được chào đón và xem trọng ở bất cứ đâu trên thế giới.</span><br />\r\n </p>\r\n\r\n<p><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\"><strong>Thêm nữa, trở thành thường trú nhân Mỹ là cách tốt nhất để phá tan rào cản quốc tế.</strong></span></span><br />\r\n </p>\r\n\r\n<p><span style=\"font-size:18px;\">Nói về hình ảnh, nếu bạn trở thành một doanh nhân trên đất Mỹ, chắc chắn bạn sẽ trở thành ‘ngôi sao’ được chào đón tại các nước.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Không chỉ dừng ở đó, hẳn bạn đã từng phải ngốn biết bao thời gian cho việc làm Visa và chứng minh hàng tá giấy tờ mỗi khi muốn đến một quốc gia nào đó trên thế giới để tìm kiếm cơ hội kinh doanh. Nếu vậy thì trở thành doanh nhân và thường trú nhân Mỹ, bạn có thể đến 172 nước trên thế giới mà không cần Visa, nghĩ thử xem, tiết kiệm được một khoản lớn thời gian đấy!</span><br />\r\n </p>\r\n\r\n<p><span style=\"font-size:24px;\"><span style=\"color:#0000FF;\"><strong>Một điều nữa, là doanh nhân Mỹ bạn sẽ được bảo vệ tối đa trong các phi vụ kinh doanh quốc tế.</strong></span></span><br />\r\n </p>\r\n\r\n<p><span style=\"font-size:18px;\">Doanh nghiệp Mỹ được bảo vệ hơn cả trong các phi vụ tranh chấp quốc tế vì Mỹ là nước nổi tiếng được biết đến là anh cả trong việc xây dựng các đạo luật cũng như hiệp định kinh doanh quốc tế. Cùng lúc đó, Mỹ cũng là nơi được các luật sư kinh nghiệm và tài giỏi nhất thế giới ‘chọn mặt gửi vàng’ để sinh sống và tìm kiếm cơ hội. Chính vì lẽ đó, để kiếm một luật sư kinh nghiệm bảo vệ bạn trên thương trường quốc tế ở Mỹ không phải là khó.</span><br />\r\n </p>\r\n\r\n<p><span style=\"font-size:18px;\">Và hơn hết, việc trở thành doanh nhân Mỹ sẽ mang cho bạn những trải nghiệm chân thực nhất về một vùng đất hứa cho những nhà đầu tư trên toàn thế giới.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Để làm điều đó thuận tiện và nhanh chóng hơn, hãy đến với chúng tôi – STAN STANART VIETNAM – với đội ngũ chuyên viên và luật sư nhiều năm kinh nghiệm trong lĩnh vực đầu tư Mỹ. Cùng với tôn chỉ THÔNG SUỐT – MINH BẠCH – HIỆU QUẢ, chúng tôi cam kết sẽ mang đến cho khách hàng những sự tư vấn tận tình và hiệu quả nhất.</span><br />\r\n </p>\r\n', 'public/Images/l1a.jpg', 'dau-tu-my-l1-de-thach-thuc-mo-duong-cho-nhung-tam-cao-moi', 'Hoàng Hương Lan'),
(28, 'Nhiều người Việt đỗ xô định cư ở TEXAS', '22', '', '', '2018-05-16', 1, '<p><span style=\"font-size:18px;\">Người Việt <em>Định cư ở Mỹ</em> từ California đang có xu hướng đổ xô sang định cư ở Houston, Texas do giá nhà cửa rẻ hơn và nhiều cơ hội việc làm hơn.<br />\r\n<br />\r\n<img alt=\"\" src=\"public/images/texas.jpg\" style=\"width: 1024px; height: 669px;\" /><br />\r\n<br />\r\nNhiều người Việt bị hấp dẫn bởi giá nhà cửa và sinh hoạt rẻ cùng với nếp sống văn hoá nhộn nhịp tại thành phố Houston, bang Texas đã kéo nhau tới định cư tại đô thị ven biển này. Từ trước đến nay, quận Cam thuộc bang California vốn vẫn là nơi có cộng đồng người Việt đông đảo nhất ở <em>nước Mỹ</em>. Tuy nhiên, thời điểm này, với giá cả đắt đỏ và ít cơ hội làm ăn, quận Cam đang bị mất dần ưu thế để nhường chỗ cho Houston, thủ phủ của bang Texas giàu có nổi tiếng nhờ dầu hoả này.</span></p>\r\n \r\n\r\n<p><span style=\"font-size:18px;\">Lan Nguyễn đã từng mơ ước được sở hữu một ngôi nhà từ khi cô rời Việt Nam sang<em>định cư Mỹ</em> tại vùng Nam Califonia 11 năm trước đây. Nhưng hai vợ chồng cô chưa bao giờ dành dụm đủ tiền để mua nhà, vì hầu hết số tiền kiếm được chỉ đủ để trang trải cho một căn hộ chật chội ở khu vực Garden Grove. Giờ đây, vợ chồng cô đã chuyển tới một khu vực ngoại ô của thành phố Houston nằm trên bờ vịnh. Họ mua được quyền sở hữu một căn nhà 4 buồng ngủ có cả bể bơi với giá 200.000 USD.</span></p>\r\n \r\n\r\n<p><span style=\"font-size:18px;\">Lan Nguyễn là một trong nhiều <em>người Mỹ gốc Việt</em> đã tới Houston vì giá sinh hoạt thấp, các cơ hội làm ăn tốt hơn. <em>Cộng đồng người Việt</em> ở đây dần đông đảo hơn. Lan Nguyễn nói: “Lúc đầu chúng tôi nghĩ rằng California là nơi lý tưởng nhất. Quả là thật buồn khi phải rời một nơi ở mà chúng tôi đã hiểu biết tường tận nhưng ở đây chúng tôi có được một ngôi nhà đầy tiện nghi và tuyệt vời.”</span></p>\r\n \r\n\r\n<p><span style=\"font-size:18px;\">Những nhà kinh doanh gốc Việt tại California cũng mở rộng hoặc dịch chuyển hoạt động kinh doanh của họ sang Houston, cố gắng tranh thủ khai thác cộng đồng người Việt đang gia tăng tại đây, trong khi thị trường nơi đây chưa bị cạnh tranh khốc liệt như ở Little Saigon đông đúc.</span></p>\r\n \r\n\r\n<p><span style=\"font-size:18px;\">Sự dịch chuyển của những người Việt gốc Mỹ đã dẫn đến những thay đổi ở bang California. Khi nền kinh tế gặp khó khăn thì nhiều người trong số họ ra đi. Một nghiên cứu hàng năm của Bộ Tài chính bang California cho biết số người rời California là 89.000 người trong năm tài chính 2007, nhiều hơn so với tất cả số người đến bất cứ nơi nào trên nước Mỹ. Cũng theo các con số thống kê, cộng đồng người Việt ở Houston đã tăng lên tới 85.000 người, đứng hàng thứ ba trong số các thành phố ở Mỹ chỉ trong vòng 6 năm trở lại đây.</span></p>\r\n<img alt=\"\" src=\"public/images/nhap-cu-texas.jpg\" style=\"width: 418px; height: 630px;\" /> \r\n<p><span style=\"font-size:18px;\">Nhiều người Việt sống lâu năm ở Houston cho biết họ chứng kiến số lượng người Việt đến đây từ California trong 5 năm trở lại đây đã tăng vọt, chủ yếu do giá nhà cửa khá rẻ. Mặc dù sau cơn bão Katrina, nhiều người mất nhà cửa từ Lousiana đã tới đây nhưng con số định cư đến từ California vẫn đông hơn.</span></p>\r\n \r\n\r\n<p><span style=\"font-size:18px;\">Một nhà kinh doanh bất động sản cho biết ông ta bán đi căn nhà trị giá hàng triệu USD của mình tại California để mua được 7 căn nhà ở Houston, mỗi căn có giá từ 170.000 đến 200.000 USD. Một người khác bán đi một căn nhà ở khu El Monte với giá 600.000 USD để mua một căn nhà tương đương ở Houston giá chỉ 175.000 USD. Số tiền còn lại, ông ta dùng để mở rộng hoạt động kinh doanh.</span></p>\r\n \r\n\r\n<p><span style=\"font-size:18px;\">Một quảng cáo trên một tờ báo tiếng Việt ở Little Saigon viết “Bạn có thể tiết kiệm được 5.000 USD khi mua một ngôi nhà ở đó.” Điều đáng chú ý là hầu hết những người ra đi khỏi California đều thuộc thế hệ thứ hai. Và như vậy tới đây chắc sẽ có thêm nhiều cặp vợ chồng trẻ nữa từ bỏ Little Saigon. Cộng đồng người Việt ở Houston chẳng mấy chốc sẽ lớn hơn ở Little Saigon</span></p>\r\n', 'public/Images/texas.jpg', 'nhieu-nguoi-viet-do-xo-dinh-cu-o-texas', 'Hoàng Hương Lan');
INSERT INTO `tt_tintuc` (`Ma`, `Ten`, `MaDanhMuc`, `KeyWord`, `Descr`, `NgayTao`, `TrangThai`, `NoiDung`, `Hinh`, `Alias`, `TacGia`) VALUES
(29, 'Người Việt đinh cư tại Mỹ và cuộc sống thế nào', '22', '', '', '2018-05-16', 1, '<p><span style=\"font-size:18px;\">Phần lớn những người Việt vào đầu năm 1975 đến 1985 khi đến Mỹ đều được hội đoàn hay nhà thờ đứng ra bảo lãnh và hỗ trợ, và sẽ được hưởng các chế độ cao, đặc biệt là các hộ gia đình có em nhỏ dưới 18 tuổi. Vào thời điểm đó họ đã được chính phủ lo mọi thứ nên không cần phải tìm hiêu, học hỏi hay lo lắng gì cả. Khoảng thời gian đó thì ai cũng sẽ hài lòng vì họ được đến từ một đất nước với nhu cầu vật chất thiếu thốn, nghèo nàn. Xung quanh đó thì người Việt ai cũng nghèo như nhau không cần phải so sánh.<br />\r\n<br />\r\n<img alt=\"\" src=\"public/images/cuoc-song-nguoi-viet-o-my.jpg\" style=\"width: 480px; height: 318px;\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Sau này các thành phần người Việt định cư Mỹ đều là đi theo qui chế di dân. Bảo lãnh con cái, a chị em vợ chồng hoặc là cha mẹ. Vì thế mày điều kiện vật chất được ổn định hơn. Bên cạnh đó cũng có những người kém hiểu biết về cuộc sống ở hải ngoại  họ sẽ không thể lường trước và tránh được những khó khắn dẫn đến việc dễ suy sụp cho cuộc sống sau này.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\"><strong>Để dễ dàng hơn cho cuộc sống tại Mỹ thì người Viêt cần chuẩn bị những gì?</strong></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Để hợp thức hóa các thủ tục cho cuộc sống ở Mỹ thì bạn cần phải mất vài ngày để hoàn tất các giấy tờ như: thẻ căn cước, thẻ y tế…..</span></li>\r\n	<li><span style=\"font-size:18px;\">Để hạn chế được sự tốn kém thì lúc đầu bạn cần phải ở nhờ nhà người thân tạm một thời gian.</span></li>\r\n	<li><span style=\"font-size:18px;\">Bạn cần phải biết ngoại ngữ hoặc tranh thủ học tiếng anh. Ít nhất bạn cũng có thể giao tiếp được sơ sơ nếu như bạn có vốn mang theo và muốn đầu tư việc gì đó.</span></li>\r\n	<li><span style=\"font-size:18px;\">Đầu tiên là bạn phải có 1 chiếc xe và phải có bằng lái để thuận tiện cho sinh hoạt đi lại của bạn. ngày nào các bạn chưa trang bị cho mình 2 thứ này thì ngày đó các bạn còn gặp khá nhiều trở ngại trong vấn đề sinh hoạt đi lại.</span></li>\r\n	<li><span style=\"font-size:18px;\">Bạn cần phải làm bất cứ công việc gì để kiếm tiền nếu như bạn nằm trọng dạng người vô sản.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:18px;\">Từ nhiều năm trước thì việc định cư ở Mỹ là một quyết định hết sức khó khăn, không có sự lựa chọn nên họ phải kiên trì để vượt qa mọi khó khăn, trở ngại trước mắt. So với trước kia thì hiện nay các bạn có nhiều lợi thế hơn họ, bạn sẽ được người thân bên cạnh, hỗ trợ làm cho bạn bớt đi cảm giác lạc long, xa lạ.Không ai có thể thay thế bạn nếu như bạn có sự nổ lực và phấn đấu.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\"><strong>Người Việt sẽ được quyền lợi gì khi đinh cư Mỹ?</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Mọi người sẽ được hưởng chế độ: trường học gần nhà, ăn uốn miễn phí ở trường, sẽ được học các lớp đặc biệt để có nhiều kiến thức về tiếng anh, được đưa đón bằng xe bus ở tận nhà. Với điều kiện con họ dưới 18 tuổi.</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">Con bạn có thể vừa học vừa làm.Và được nhập học ở trường cao đẳng địa phương, được học đại học 4 năm để lấy bằng cử nhân nếu như con bạn trên 18 tuổi.</span></li>\r\n	<li><span style=\"font-size:18px;\">Con bạn sẽ được học tập miễn phí và được xin học bỗng của chính phủ với điều kiện khó khăn, thu nhập thấp.</span></li>\r\n	<li><span style=\"font-size:18px;\">Ngoài tiền học phí, sách vở do chính phủ giúp, miễn hoàn trả, nếu đủ điều kiện,để hỗ trợ cho việc sinh sống và toan thời gian học tập thì các bạn cũng có thể nộp đơn để vay tiền từ chính phủ.</span></li>\r\n	<li><span style=\"font-size:18px;\">Trẻ em dưới 18 tuổi, người già trên 65 tuổi được trợ cấp y tế toàn phần của chính phủ (medicaid), và còn có thể xin thêm được food stamps (trợ cấp thực phẩm).</span></li>\r\n	<li><span style=\"font-size:18px;\">Những người trên 18 tuổi hoặc dưới 65 tuổi, thu nhập thấp sẽ xin được trợ cấp y tế bán phần của chính phủ, như ở Texas có chương trình Gold card mà người Việt mình hay gọi là thẻ vàng. Mỗi tiểu bang có những chương trình và tên gọi khác nhau. (Những người mới nhập cư, hầu hết đủ điều kiện được hưởng vì chưa có thu nhập).</span></li>\r\n</ul>\r\n', 'public/Images/cuoc-song-nguoi-viet-o-my.jpg', 'nguoi-viet-dinh-cu-tai-my-va-cuoc-song-the-nao', 'Hoàng Hương Lan'),
(30, 'Kinh Nghiệm Phỏng Vấn Ở Lãnh Sự Quán Mỹ', '23', '', '', '2018-05-16', 1, '<p><span style=\"font-size:18px;\"><strong>Không ai có thể chắc chắn rằng mình sẽ được cấp visa bất chấp cảm giác hay thái độ của mình trong cuộc phỏng vấn. Nhân viên lãnh sự chỉ cấp visa khi nào người xin visa hội đủ những điều kiện về phương diện pháp lý. Tuy nhiên có một vài kinh nghiệm sau đây có thể giúp bạn chứng minh bạn đủ tư cách trong cuộc phỏng vấn:</strong></span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:18px;\"><img alt=\"\" src=\"http://localhost/van/public/images/phong-van-visa-my.jpg\" style=\"width: 711px; height: 497px;\" /></span><br />\r\n </p>\r\n\r\n<p><span style=\"font-size:18px;\">   1. Lúc trả lời câu hỏi, bạn nên tránh kể chuyện đời bạn hay kể chuyện gia đình của bạn cho nhân viên lãnh sự. Thật vậy, nhân viên lãnh sự rất bận và phải phỏng vấn nhiều người trong một ngày. Họ không muốn nghe bạn kể chuyện dài dòng cho một câu hỏi ngắn. Khi họ đặt câu hỏi là họ muốn xem bạn đủ tư cách để được cấp visa theo đòi hỏi của luật di trú Hoa Kỳ hay không.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">   2. Bám vào vấn đề và trả lời thẳng vào câu hỏi. Thỉnh thoảng, nhân viên lãnh sự đặt một câu hỏi chỉ cần trả lời «Có» hay «Không». Nếu gặp một câu hỏi như vậy, bạn nên trả lời thẳng câu hỏi thay vì trả lời vòng quanh không nhắm vào câu hỏi đặt ra. Nói một cách khác, bạn nên trả lời ngắn gọn, nhưng nhắm vào suy tư của nhân viên lãnh sự và thỏa mãn quan tâm của họ. Dĩ nhiên là nếu gặp một câu hỏi cần sự giải thích thì bạn giải thích cho họ, nhưng đi thẳng vào vấn đề.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">   3. Nghe hết câu hỏi của nhân viên lãnh sự trước khi trả lời. Thật vậy, nếu bạn trả lời trước khi nhân viên lãnh sự dứt lời thì có khi bạn không trả lời đúng câu hỏi mà họ đặt, và thay vì làm cho cuộc phỏng vấn được mau kết thúc, bạn lại làm kéo dài cuộc phỏng vấn.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">   4. Bạn phải chắc chắn hiểu câu hỏi. Nếu bạn không hiểu câu hỏi thì bạn lịch sự cho họ biết để họ có thể lập lại câu hỏi, dùng từ đơn giản hơn hay nhờ người thông dịch lại cho bạn. Nhân viên lãnh sự không muốn bạn trả lời một câu hỏi mà bạn không hiểu và họ sẽ không biết rằng bạn không hiểu câu hỏi nếu bạn không cho họ biết.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">   5. Không nên đoán câu hỏi. Nếu bạn không biết trả lời câu hỏi, hay bạn không nhớ thì bạn cho nhân viên lãnh sự biết. Họ không muốn bạn đoán nếu bạn không biết.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">   6. Nên nói sự thật. Có nhiều người đủ điều kiện để được cấp visa, nhưng họ lại nói dối nhân viên lãnh sự vì họ nghĩ rằng điều đó tốt cho trường hợp của họ. Khi nhân viên lãnh sự khám phá được, họ bị cấm vào Mỹ suốt đời. Nếu những người đó nói sự thật ngay từ đầu và giải thích trường hợp của họ thì họ đã được cấp visa. Nếu bạn nghi ngờ về trường hợp của bạn thì bạn nên nói sự thật và giải thích chuyện gì đã xảy ra.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">   7. Nên trình những giấy tờ gốc và xác thực. Những giấy tờ giả có sửa đổi bị xem là gian lận và là một trong những nguyên nhân đưa đến việc bị từ chối visa. Lãnh sự quán Hoa Kỳ, với một trong những Bộ phận kiểm soát gian lận lớn nhất và tinh vi nhất, có thể khám phá những giấy tờ giả mạo và sự gian lận của bạn.</span></p>\r\n<span style=\"font-size:18px;\"><em>Nguồn Visa Mỹ</em></span>', 'public/Images/phong-van-visa-my.jpg', 'kinh-nghiem-phong-van-o-lanh-su-quan-my', 'Hoàng Hương Lan'),
(31, 'Cuộc Sống Ở Mỹ Bắt Đầu Như Thế Nào', '23', '', '', '2018-05-16', 1, '<h3 style=\"text-align: center;\"><span style=\"color:#FF0000;\"><span style=\"font-size:36px;\">Ngôn ngữ và chiếc xe là chìa khoá mở đầu cho cuộc sống nơi xứ người, ngày nào còn chưa có hai thứ này thì bạn sẽ còn bế tắc…</span></span></h3>\r\n \r\n\r\n<p><span style=\"font-size:18px;\">Sau bài viết “<em>Người Việt sống khoẻ ở Houston</em>“, tôi nhận được nhiều thư hỏi thăm. Nhận thấy nhu cầu tìm hiểu về đời sống hiện tại ở Mỹ nói chung và Houston nói riêng rất cao, do đó dựa theo một số thắc mắc của bạn đọc, tôi cố gắng tìm tài liệu để viết tiếp bài này.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Tuy rằng nội dung bài viết cũng chẳng có gì cao xa, to lớn như những bí quyết làm giàu, chỉ là một phần kiến thức nhỏ nhoi, nhưng có lẽ cần thiết với một số người đã hay đang có ý định qua đất nước này sinh sống.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Những năm đầu (75-85), phần lớn người Việt Nam khi tới Mỹ đều được hội đoàn hay nhà thờ đứng ra bảo lãnh hỗ trợ, cũng như được hưởng quy chế khá cao, nhất là những gia đình có con em nhỏ dưới 18 tuổi. Khi đó họ chẳng cần phải lo lắng, cứ từ từ tìm hiểu, học hỏi để hội nhập, mọi thứ đã có chính phủ lo. Đến từ một đất nước nghèo, nhu cầu vật chất cuộc sống còn ở mức tối thiểu, nên ai cũng hài lòng với hiện tại, nhất là thời điểm đó, nhìn quanh người Việt ai cũng nghèo như nhau, chẳng ai giàu hơn ai để so sánh.</span></p>\r\n<img alt=\"\" src=\"public/images/dinh-cu-my.jpg\" style=\"width: 468px; height: 380px;\" />\r\n<p><span style=\"font-size:18px;\">Đời sống ở đâu cũng vậy, phải đi làm mới có thu nhập trang trải cuộc sống hàng ngày. Hơn nữa nhu cầu vật chất theo cuộc sống ngày càng lên cao.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Ngày mới qua, chỉ ao ước có nhu cầu sống tối thiểu. Nhưng khát vọng con người là thăng tiến, người Việt cũng không ngoại lệ, nên sau nhiều năm hội nhập, cuộc sống người Việt ở hải ngoại đã không còn cách biệt với người bản xứ, hay có thể có phần trội hơn. Vì người Việt vốn rất kiên trì chịu khó, cũng như bỏ công bỏ của nhiều để  cho học vấn con cái, nên sau nhiều năm, thành quả của họ có thể nhìn thấy một cách rõ ràng, phải nói là đáng ngưỡng mộ.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Còn thành phần mới qua <strong>định cư </strong> sau này, hầu hết là ra đi theo quy chế di dân, cá nhân bảo lãnh, như cha mẹ bảo lãnh cho con, anh em bảo lãnh cho nhau, chồng bảo lãnh vợ, do vậy điều kiện vật chất, thời gian hỗ trợ giúp đỡ có phần hạn chế hơn nhiều.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Đồng thời, vào thời điểm hiện tại, cuộc sống ở Việt Nam đã cải thiện hơn xưa. Có những bạn đang có cuộc sống tốt, có nhà (do cha mẹ để lại), có việc làm ổn định, hàng tháng (hay thỉnh thoảng) được thân nhân nước ngoài hỗ trợ một vài trăm đô la, có thể rất giàu hoặc không giàu, nhưng chí ít cũng có cuộc sống ung dung. Do đó có ước vọng rằng đi ra nước ngoài để có cuộc sống dễ dàng hơn, nhất là một cường quốc như nước Mỹ, và điển hình là thấy Việt kiều hải ngoại nhiều người thênh thang áo gấm về làng.<br />\r\n<br />\r\n<img alt=\"\" src=\"public/images/lam-viec-my-1.jpg\" style=\"width: 620px; height: 405px;\" /></span><br />\r\n </p>\r\n\r\n<p><span style=\"font-size:18px;\">Với suy nghĩ như vậy, nên không ít người qua tới đây, thiếu kiến thức tối thiểu của cuộc sống hải ngoại, khi đối diện với khó khăn không lường trước đã không thích ứng được, dẫn đến suy sụp tinh thần, bất đắc chí, than thân trách phận, có khi oán trách cả người đã bảo trợ họ sang, vì sao giàu thế mà không lo cho họ một cuộc sống như họ muốn. Thành ra cả người bảo trợ lẫn người được bảo trợ đã lâm vào cảnh khó khăn bứt rứt, tiến thoái lưỡng nan, sứt mẻ tình cảm.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Ở bài viết này, tôi xin đưa ra những bước bắt buộc phải có của cuộc sống mới, mà bạn cần phải vượt qua, nếu muốn tái <strong>định cư</strong> ở một đất nước xa lạ, mọi thứ từ ngôn ngữ cũng như cách sống hoàn toàn khác biệt.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Sau khi đặt chân đến xứ Mỹ, người thân (người bảo trợ) sẽ mất vài ngày đưa bạn đi làm giấy tờ, hợp thức hoá cần thiết cho cuộc sống ở Mỹ, như thẻ căn cước, thẻ y tế, xin nhập học cho bọn trẻ.</span></p>\r\n\r\n<h3><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\">Dưới đây là những quyền lợi bạn được hưởng từ chính phủ:</span></span></h3>\r\n\r\n<p><span style=\"font-size:18px;\">- Các cháu dưới 18 tuổi sẽ vào học trường tiểu học và trung học gần nhà. Có xe bus đưa rước đi về hai bận, ăn trưa miễn phí ở trường. Có thể được cho vào lớp đặc biệt thời gian đầu để củng cố Anh ngữ. (Nhớ đem theo đầy đủ học bạ ở bên Việt Nam, và cần dịch sang tiếng Anh)</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">- Các cháu trên 18 tuổi có thể ghi danh nhập học ở trường cao đẳng địa phương với tên gọi là Community College. Ở đó bạn có thể học tiếng Anh và chọn một nghề nào đó làm kế sinh sống trong tương lai. Tùy theo sở thích và trình độ, bạn có thể chuyển tiếp lên trường đại học 4 năm để lấy bằng cử nhân.<br />\r\n<br />\r\n<img alt=\"\" src=\"public/images/du-hoc-my.jpg\" style=\"width: 1125px; height: 752px;\" /></span><br />\r\n </p>\r\n\r\n<p><span style=\"font-size:18px;\">Nếu bạn không có thu nhập, hoặc thu nhập thấp, thì tất cả miễn phí. Hầu hết ai cũng được chính phủ tài trợ (xin học bổng), đây là quyền lợi chung cho mọi <strong>công dân Mỹ</strong>.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Ngoài tiền học phí, sách vở do chính phủ giúp, miễn hoàn trả, nếu đủ điều kiện, bạn có thể nộp đơn vay tiền từ chính phủ, giúp hỗ trợ sinh sống để có thể học toàn thời gian.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">- Con nít dưới 18 tuổi, người già trên 65 tuổi được trợ cấp y tế toàn phần của chính phủ (medicaid), và còn có thể xin thêm được food stamps (trợ cấp thực phẩm).</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">- Những người trên 18 tuổi hoặc dưới 65 tuổi, thu nhập thấp sẽ xin được trợ cấp y tế bán phần của chính phủ, như ở Texas có chương trình Gold card mà người Việt mình hay gọi là thẻ vàng. Mỗi tiểu bang có những chương trình và tên gọi khác nhau. (Những người mới nhập cư, hầu hết đủ điều kiện được hưởng vì chưa có thu nhập).</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Người bảo trợ, hoặc cho gia đình bạn ở tạm nhà họ một thời gian ngắn cho đỡ tốn kém, hoặc tìm mướn dùm bạn một căn nhà (mắc rẻ tùy theo khả năng) và từ đây sẽ bắt đầu hành trình gian khổ, tự túc tự cường của chính bạn.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Nếu bạn có vốn mang theo, có tiền sống một thời gian không đến nỗi quẫn bách, thì lúc này bạn nên tranh thủ học tiếng Anh, cho dù không cần nhiều, nhưng ít nhất nghe được, nói được chút ít để kiếm việc làm, vì chẳng lẽ họ mướn bạn vào làm, còn phải mướn thêm người thông dịch?</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Một điều cấp bách khác là phải học lái xe. Nhờ người thân kiếm mua một chiếc xe, mới cũ mắc rẻ tùy khả năng. Đây là chuyện bắt buộc (nếu không có tiền, bạn cũng phải thương lượng với người thân để hỏi mượn, sau này sẽ hoàn trả). Quỹ thời gian ở nước ngoài rất eo hẹp, không ai có thể đưa đón bạn mỗi ngày đi và về.<br />\r\n<br />\r\n<img alt=\"\" src=\"public/images/nuoc-my.jpg\" style=\"width: 600px; height: 375px;\" /></span><br />\r\n </p>\r\n\r\n<p><span style=\"font-size:18px;\">Ngôn ngữ và chiếc xe là chìa khoá mở đầu cho cuộc sống, ngày nào còn chưa có hai thứ này thì bạn sẽ còn bế tắc.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Trường hợp bạn không có tiền mang theo, nghĩa là vô sản, thì không thể chần chờ. Bạn phải chấp nhận bất cứ việc làm nào (đương nhiên là lương thiện), dù là những việc làm tay chân nặng nhọc, những việc không cần chất xám, để kiếm tiền trang trải cuộc sống gia đình.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Người bảo trợ, họ có thể tự nguyện giúp bạn thời gian đầu trong khả năng, nhưng họ không có bổn phận phải nuôi bạn và gia đình bạn. Hoặc dù họ có muốn, đôi khi cũng không đủ khả năng, vì số phí này không phải là con số nhỏ.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Còn việc làm ở xứ người trong trường hợp bạn là người mới ở đất nước này sẽ như thế nào? Cũng không khó hiểu, này nhé:</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Cứ tưởng tượng, một người mới nhập cư đi xin việc làm: không nói, không nghe được chủ muốn nói gì, kinh nghiệm nghề nghiệp không có, đôi khi cũng đã có tuổi, sức khoẻ không còn sung mãn… Tất cả là con số không, không khác đứa bé còn đang chập chững tập đi, thì bạn sẽ làm được những gì, ngoài những việc không ai muốn làm mới tới tay mình.</span></p>\r\n\r\n<p><strong><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\">Vậy thì sao? Có hai trường hợp:</span></span></strong></p>\r\n\r\n<h4><strong><span style=\"font-size:18px;\">1. Bạn là người năng động, tự tin:</span></strong></h4>\r\n\r\n<p><span style=\"font-size:18px;\">Tôi chấp nhận vì tôi biết, với tôi đây chỉ là tạm thời. Một thời gian nữa, khi quen cuộc sống ở đây, rành đường xá, tiếng Anh giỏi, tôi sẽ tìm một việc làm khá hơn, hợp với khả năng hơn.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Với tôi, đều quan trọng hơn cả là môi trường, điều kiện sống tốt cho tương lai, sự phát triển của các con. Tôi tin với khả năng của mình, tôi chấp nhận dấn thân.</span></p>\r\n\r\n<h4><strong><span style=\"font-size:18px;\">2. Bạn không chấp nhận được thực tại:</span></strong></h4>\r\n\r\n<p><span style=\"font-size:18px;\">- Lỡ ôm một ảo mộng thiên đường.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">- Từng nghe cuộc sống bên Mỹ vất vả, mà không hình dung được tới mức này.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">- Ở Việt Nam tôi sướng gấp mấy lần, công việc cũ nhàn hạ, có nhân viên, tội gì. Tiếng là qua Mỹ mà tôi phải làm những chuyện nặng nhọc, như làm tiệm phở, làm chợ, giữ con nít.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">- Nhìn chung quanh, thấy người qua trước giàu có, trong khi tôi phải làm việc tay chân như thế này, thật là mất mặt, biết vậy tôi không đi.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Hay thậm chí có bạn nằng nặc đòi quay về Việt Nam! Đó là những trường hợp tôi thấy đang xảy ra rất nhiều.<br />\r\n<br />\r\n<img alt=\"lam viec my\" src=\"public/images/lam-viec-o-my.jpg\" style=\"width: 297px; height: 353px;\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Mỗi lần quyết định đi hay ở đều không phải chuyện đơn giản. Ngoài chuyện tốn kém tiền bạc, thời giờ, còn có nỗ lực tình cảm của nhiều người thân. Trong khuôn khổ bài viết ngắn này, tôi chỉ có mục đích phần nào giúp cho bạn hiểu rõ con đường trước mặt, để có một quyết định sáng suốt nhất.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Những người Việt tha hương từ nhiều năm trước, họ không có chọn lựa nên dù thế nào cũng cắn răng dấn thân, kiên quyết đạp đổ mọi trở ngại. Nhờ vậy, có thể nói gian khổ họ trải qua, nhất là tinh thần, gấp vạn lần hoàn cảnh hiện tại của các bạn, nhưng họ đã vượt qua và thành công.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Còn hiện tại, bạn có lợi thế hơn nhiều. Bạn có người thân bên cạnh hướng dẫn trong cuộc sống mới, có chút tiền mang theo. Cộng đồng Việt Nam hải ngoại đã vững mạnh. Về tinh thần, bớt được nhiều cảm giác bơ vơ lạc lõng. Tuy nhiên, thời nào cũng có những việc bạn phải tự làm. Đó là nỗ lực phấn đấu, việc này không ai có thể làm thay thế bạn được.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Tôi hy vọng những thành quả giàu sang của người đi trước sẽ là động lực cho bạn dấn thân, chứ đừng lấy đó so sánh, phân bì để rồi thay vì dấn thân tiến bước, bạn trở thành thất chí, than thân trách phận, dùng ánh mắt cay đắng nhìn đời.<br />\r\n<br />\r\n<img alt=\"lam viec o my\" src=\"public/images/viec-lam-o-my-1.jpg\" style=\"width: 590px; height: 332px;\" /></span><br />\r\n </p>\r\n\r\n<p><span style=\"font-size:18px;\">Tôi đã chứng kiến không ít người là nạn nhân của nhân sinh quan sai lầm này. Hãy nhớ những người thành công hiện giờ, họ cũng phải trải qua một thời gian dài phấn đấu, không khác gì bạn bây giờ. Hay có khi còn khổ hơn bạn bây giờ nữa.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Còn nếu bạn đọc xong bài viết này, đã có chút khái niệm, thấy rằng hoàn cảnh mình ở Việt Nam tốt hơn, thì tôi khuyên bạn nên ở lại nước mình, đừng đi làm chi cho phí thì giờ, tốn tiền và tốn công người bảo trợ.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Nước Mỹ chưa bao giờ tự phong đất nước này là thiên đường, chỉ là từ những cảm nhận của một đại đa số nào đó, ưu ái mến tặng cho nó mà thôi. Thiên đường hay địa ngục là do từng trường hợp cá nhân, đừng thần thánh hoá quá, để rồi thất vọng.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Theo quan điểm của riêng tôi, đất Mỹ không là thiên đường một cách huyễn hoặc, xa xôi. Đất Mỹ cũng có ưu, có khuyết, và nhiều mặt tầm thường như mọi nước khác. Có đều tôi có thể khẳng định, nơi chốn này cho bạn và nhất là giới trẻ rất nhiều cơ hội để tiến thân. Chỉ là bạn có chịu nắm lấy hay không mà thôi!</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Tôi biết bài viết này chỉ đúng trong một phạm vi nhỏ nào đó, không phản ánh được tất cả. Nếu không phải trong trường hợp mình, cứ xem như đọc một chuyện tầm phào. Cám ơn bạn.</span></p>\r\n', 'public/Images/dinh-cu-my.jpg', 'cuoc-song-o-my-bat-dau-nhu-the-nao', 'Hoàng Hương Lan'),
(32, 'Nhập cư dựa trên việc làm ', '23', '', '', '2018-05-16', 1, '<span style=\"font-size:18px;\">Bạn có thể hội đủ điều kiện cho hạng mục ưu tiên thị thực nhập cư này nếu bạn là một công nhân lành nghề, chuyên nghiệp hoặc nhân viên khác.</span>\r\n<ul>\r\n	<li><span style=\"font-size:18px;\">\"Công nhân lành nghề\" là những người có công việc đòi hỏi tối thiểu 2 năm đào tạo hoặc kinh nghiệm làm việc, không phải là bản chất tạm thời hoặc theo mùa</span></li>\r\n	<li><span style=\"font-size:18px;\">“Chuyên gia” là những người có yêu cầu công việc ít nhất bằng bằng cử nhân Mỹ hoặc bằng tiếng nước ngoài và là thành viên của ngành nghề</span></li>\r\n	<li><span style=\"font-size:18px;\">Danh mục phụ \"công nhân khác\" dành cho những người thực hiện lao động không có kỹ năng, yêu cầu đào tạo hoặc kinh nghiệm dưới 2 năm, không phải là bản chất tạm thời hoặc theo mùa.</span></li>\r\n</ul>\r\n<br />\r\n<span style=\"font-size:18px;\"><img alt=\"lao dong my\" src=\"public/images/lao-dong-my.jpg\" style=\"width: 600px; height: 281px;\" /></span><br />\r\n \r\n<div style=\"text-align: center;\"><strong><span style=\"font-size:24px;\"><span style=\"color:#0000FF;\">Đủ tiêu chuẩn</span></span></strong><br />\r\n </div>\r\n\r\n<table border=\"1\">\r\n	<thead>\r\n		<tr>\r\n			<th>\r\n			<p><span style=\"font-size:24px;\"><span style=\"color:#B22222;\"><strong>Tiểu danh mục</strong></span></span></p>\r\n			</th>\r\n			<th>\r\n			<p><span style=\"color:#B22222;\"><span style=\"font-size:24px;\"><strong>Chứng cớ</strong></span></span></p>\r\n			</th>\r\n			<th>\r\n			<p><span style=\"color:#B22222;\"><span style=\"font-size:24px;\"><strong>Chứng nhận</strong></span></span></p>\r\n			</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align: center;\"><span style=\"font-size:18px;\">Công nhân lành nghề</span></p>\r\n			</td>\r\n			<td>\r\n			<ul>\r\n				<li style=\"text-align: center;\"><span style=\"font-size:18px;\">Bạn phải có khả năng chứng minh ít nhất 2 năm kinh nghiệm hoặc đào tạo công việc</span></li>\r\n				<li style=\"text-align: center;\"><span style=\"font-size:18px;\">Bạn phải thực hiện công việc mà nhân viên đủ điều kiện không có sẵn ở Hoa Kỳ</span></li>\r\n			</ul>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: center;\"><span style=\"font-size:18px;\">Chứng nhận lao động và yêu cầu công việc cố định, toàn thời gian <em>.</em></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align: center;\"><span style=\"font-size:18px;\">Chuyên gia</span></p>\r\n			</td>\r\n			<td>\r\n			<ul>\r\n				<li style=\"text-align: center;\"><span style=\"font-size:18px;\">Bạn phải có khả năng chứng minh rằng bạn có bằng cử nhân Mỹ hoặc bằng cấp ngoại ngữ tương đương, và rằng bằng cử nhân là yêu cầu bình thường để tham gia vào nghề nghiệp</span></li>\r\n				<li style=\"text-align: center;\"><span style=\"font-size:18px;\">Bạn phải thực hiện công việc mà nhân viên đủ điều kiện không có sẵn ở Hoa Kỳ</span></li>\r\n				<li style=\"text-align: center;\"><span style=\"font-size:18px;\">Giáo dục và kinh nghiệm có thể không được thay thế cho bằng cử nhân</span></li>\r\n			</ul>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: center;\"><span style=\"font-size:18px;\">Chứng nhận lao động và yêu cầu công việc cố định, toàn thời gian <em>.</em></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style=\"text-align: center;\"><span style=\"font-size:18px;\">Công nhân không có kỹ năng (Công nhân khác)</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: center;\"><span style=\"font-size:18px;\">Bạn phải có khả năng, vào thời điểm nộp đơn yêu cầu bạn, thực hiện lao động không có kỹ năng (yêu cầu đào tạo hoặc kinh nghiệm dưới 2 năm), đó không phải là bản chất tạm thời hoặc theo mùa, mà người lao động đủ điều kiện không có sẵn Hoa Kỳ.</span></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"text-align: center;\"><span style=\"font-size:18px;\">Chứng nhận lao động và yêu cầu công việc cố định, toàn thời gian <em>.</em></span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"font-size:18px;\"><strong><span style=\"color:#FF0000;\">Lưu ý:</span></strong> Mặc dù các yêu cầu đủ điều kiện cho phân loại tùy chọn thứ ba ít nghiêm ngặt hơn, bạn nên lưu ý rằng tồn đọng dài tồn tại đối với thị thực trong danh mục \"công nhân khác\". </span></p>\r\n\r\n<div><span style=\"font-size:24px;\"><span style=\"color:#0000FF;\"><strong>Bộ Lao động Hoa Kỳ - Chứng nhận Lao động</strong></span></span></div>\r\n\r\n<p style=\"margin-left: 40px;\"><span style=\"font-size:18px;\">Các kiến ​​nghị ưu tiên thứ ba nói chung phải có kèm theo giấy chứng nhận lao động cá nhân đã được phê duyệt từ Bộ Lao động theo Mẫu ETA-9089. Trong một số trường hợp, bản kiến ​​nghị có thể được nộp cho Cơ quan Di trú và Nhập tịch Hoa Kỳ (USCIS) với ETA-9089 không được chứng minh để xem xét như Bảng A, Nhóm I.</span></p>\r\n\r\n<div><strong><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\">Quy trình đăng ký</span></span></strong></div>\r\n\r\n<p style=\"margin-left: 40px;\"><span style=\"font-size:18px;\">Chủ nhân của bạn (người khởi kiện) phải nộp Mẫu đơn I-140, Đơn yêu cầu Người lao động nước ngoài. Là một phần của quy trình nộp đơn, chủ lao động của bạn phải có khả năng chứng minh khả năng trả lương được cung cấp theo ngày ưu tiên visa của bạn. Chủ nhân của bạn có thể sử dụng một báo cáo hàng năm, tờ khai thuế thu nhập liên bang, hoặc báo cáo tài chính được kiểm toán để chứng minh khả năng trả lương của bạn.</span></p>\r\n\r\n<div><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\"><strong>Gia đình của người có Visa EB-3</strong></span></span></div>\r\n\r\n<p style=\"margin-left: 40px;\"><span style=\"font-size:18px;\">Vợ / chồng của bạn có thể được nhận vào Hoa Kỳ ở E34 (vợ / chồng của một \"công nhân lành nghề\" hoặc \"chuyên nghiệp\") hoặc EW4 (vợ / chồng của một \"công nhân khác\"). Trong quá trình quý vị và vợ / chồng của quý vị đang nộp đơn xin thường trú nhân (tình trạng là chủ thẻ xanh), vợ / chồng của quý vị hội đủ điều kiện để nộp đơn xin Employment Authorization Document (EAD). Con nhỏ của bạn (dưới 21 tuổi) có thể được nhận là E35 (con của “công nhân lành nghề” hoặc “chuyên nghiệp”) hoặc EW5 (con của “công nhân khác”).</span></p>\r\n', 'public/Images/lao-dong-my.jpg', 'nhap-cu-dua-tren-viec-lam', 'Hoàng Hương Lan'),
(33, 'Tham khảo điều kiện xin visa đi Mỹ diện J1', '24', '', '', '2018-05-16', 1, '<p><span style=\"font-size:18px;\">Mỹ có nhiều thị thực dành cho người nước ngoài muốn tạm trú. Để cho con em mình có một cuộc sống tốt hơn, một tương lai tươi sáng hơn thì dĩ nhiên du học là con đường đơn giản hơn cả.Trong số đó có 3 chính sách dành cho du học sinh là F1, M1 và J1. Tuy nhiên nếu như muốn <strong>xin visa đi Mỹ </strong>theo diện J1 để trao đổi sinh viên thì cần đáp ứng các điều kiện ra sao?</span></p>\r\n\r\n<p><br />\r\n<br />\r\n<span style=\"font-size:18px;\"><img alt=\"\" src=\"public/images/visa-j1-dinh-cu-my.jpg\" style=\"width: 1000px; height: 523px;\" /></span><br />\r\n </p>\r\n\r\n<h2><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\"><strong>Đôi nét về thị thực trao đổi sinh viên J1</strong></span></span></h2>\r\n\r\n<p><span style=\"font-size:18px;\">Thực tế thì visa J1 là thị thực để trao đổi khách mời mà đối tượng ở đây là sinh viên đang theo học tại các chương trình bên ngoài của mỹ nhưng có mong muốn được tham gia vào các chương trình trao đổi sinh viên quốc tế với các trường cao đẳng hay đại học của quốc gia này. Như vậy có thể nói hình thức visa này được ban hành để thúc đẩy trao đổi văn hóa cũng như giúp sinh viên các nước học hỏi và nâng cao được kỹ năng chuyên môn nhờ tham gia các chương trình đào tạo do Mỹ công nhận. Đối tượng được <strong>xin visa đi Mỹ </strong>theo diện J1 bao gồm:</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">+ Học sinh, sinh viên từ trung học trở lên.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">+ Thực tập sinh các ngành kinh doanh.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">+ Giáo viên tiểu học và trung học.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">+ Người tham gia vào chương trình huấn luyện ngành hàng không.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">+ Giáo sư đại học.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">+ Học giả nghiên cứu.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">+ Chuyên viên hay thực tập ngành Y tế tham gia khóa huấn luyện tại Mỹ.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">+ Chuyên gia các lĩnh vực khác nhau.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">+ Khách mời có mục đích là du lịch, tham quan, trình bày kiến thức và kỹ năng đặc biệt, tư vấn, nghiên cứu, đào tạo, trao đổi hay tham gia các chương trình trao đổi văn hóa.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">+ Hướng dẫn viên.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">+ Những người làm công việc như giúp việc và chăm sóc giáo dục.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\"><img alt=\"\" src=\"public/images/j1-visa.png\" style=\"width: 660px; height: 279px;\" /></span></p>\r\n\r\n<h2><span style=\"color:#0000FF;\"><span style=\"font-size:24px;\"><strong>Điều kiện xin cấp visa J1</strong></span></span></h2>\r\n\r\n<p><span style=\"font-size:18px;\">Nếu như người tham gia thuộc vào một trong những đối tượng mà chính sách cấp thị thực cho người trao đổi J1 thì có thể <strong>xin visa đi Mỹ</strong>. Cụ thể thì người tham gia chính sách này sẽ phải đưa ra các bằng chứng về việc trao đổi học tập, kỹ năng là có thật. Nói rõ hơn thì ứng viên cũng là một trong các đối tượng trên sẽ phải có được sự tài trợ từ một tổ chức được chứng nhận là nhà tài trợ visa J-1 bởi Bộ Ngoại giao Mỹ. Nếu như có được tài trợ này thì ứng viên mới được nộp đơn xin cấp visa theo diện J1.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Nhà tài trợ ở đây được yêu cần không phải là công ty, doanh nghiệp mà bản thân ứng viên sở hữu visa làm việc hay có hoạt động tương tự như du học sinh, thực tập sinh tại đây. Nhà tài trợ sẽ phải là một tổ chức có nhiệm vụ nhằm truyền đạt những yêu cầu của Bộ Ngoại Giao Mỹ đến cho người nộp đơn sau đó là phê duyệt đơn xin tài trợ mày. Hiện nay tại Mỹ có đến hàng trăm nhà tài trợ những không có nghĩa là tài trợ thế nào đều do họ quyết định. Số lượng visa J1 sẽ được ấn định trước đó đối với mỗi nhà tài trợ.<br />\r\n<br />\r\n<img alt=\"j1\" src=\"public/images/visa--j1.jpg\" style=\"width: 674px; height: 546px;\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Có thể nói rõ hơn như sau: có một số nhà tài trợ sẽ chỉ chuyên cung cấp cho một đối tượng nhất định ví dụ như chương trình dành cho Giúp việc và chăm sóc giáo dục hay có nhà tài trợ chỉ chuyên về Chương trình thực tập sinh… Theo đó nhà tài trợ sẽ cấp cho ứng viên một mẫu đơn đặt biệt gọi là đơn DS-2019. Những người được chấp thuận sẽ điền sau đó là nộp đơn này để xin cấp visa J1 cho Lãnh sự quán. Tuy nhiên không phải bất cứ ai cũng có thể nộp đơn mà có một số trường hợp ứng viên sẽ được yêu cầu phải rời khỏi Mỹ trong thời hạn 2 năm của visa. Tuy nhiên có một số trường hợp thì yêu cầu này sẽ được miễn.</span></p>\r\n', 'public/Images/visa-j1-dinh-cu-my.jpg', 'tham-khao-dieu-kien-xin-visa-di-my-dien-j1', 'Hoàng Hương Lan'),
(34, 'Từ khách truy cập Exchange đến chủ sở hữu doanh nghiệp', '24', '', '', '2018-05-16', 1, '<p><span style=\"font-size:18px;\">Tác giả Kristin Chasan, Điều phối viên Truyền thông, Văn hóa Homestay Quốc tế (CHI) </span><br />\r\n </p>\r\n<img alt=\"\" src=\"public/images/visa-usa.png\" style=\"width: 602px; height: 602px;\" />\r\n<p><span style=\"font-size:18px;\"><em>Darya đặt ra với một số chiếc gối tùy chỉnh tuyệt đẹp của cô được lấy cảm hứng từ thời gian của cô ở Hoa Kỳ như một người tham gia J-1.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Năm 2010, Darya Modestova mới 19 tuổi. Cô vừa mới tốt nghiệp trung học, và đổ mồ hôi qua một mùa hè nóng bỏng ở quê nhà Tashkent, thủ đô Uzbekistan. Cô ấy không có kinh nghiệm làm việc, nhưng cô ấy có tham vọng và muốn làm điều gì đó sẽ giúp cô ấy thành công trong cuộc sống. Vì vậy, cô ấy đã tạo một bản lý lịch và đăng nó trực tuyến. Thông qua guile tuyệt đối, cô kiếm được ba cuộc phỏng vấn, và cuối cùng được thuê bởi Resource Interexchange Consulting (RIC) làm Giám đốc điều hành của họ. Khoảnh khắc này là một bước ngoặt trong cuộc đời của cô, bởi vì nó đã ở RIC, nơi cô đã học về Chương trình J-1 Exchange Visitor Program.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Cô biết rằng cơ hội sống và làm việc ở Mỹ là điều mà cô không thể bỏ qua, vì vậy mùa hè năm sau Darya đã lên máy bay đến Boston để có một trải nghiệm hoàn toàn mới sẽ mãi mãi thay đổi cô. Darya đã dành mùa hè năm 2011 sống và làm việc tại Cape Cod, Massachusetts như một phần của chương trình Du lịch và Làm việc Mùa hè của cô. Cô đã có một mùa hè tuyệt vời, và còn lại với một số kỹ năng mới có giá trị. Cô đã học cách đối xử với người khác trong một môi trường chuyên nghiệp, cách làm việc chăm chỉ và cách giúp một doanh nghiệp thành công. Cô cũng có được sự tự tin rằng cô có thể thành công khi cô đưa tâm trí của mình vào thứ gì đó. Để nói điều này thay đổi cuộc sống của cô sẽ là một cách nói.</span></p>\r\n<br />\r\n<img alt=\"\" src=\"public/images/visa-j1-usa.png\" style=\"width: 768px; height: 614px;\" /><br />\r\n \r\n<p><span style=\"font-size:18px;\"><em>Một số thiết kế gối độc đáo và thú vị của Darya lấy cảm hứng từ các chương trình truyền hình Mỹ như Bạn bè.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Sống ở Cape Cod là một trải nghiệm tuyệt vời với tình bạn, giao lưu văn hóa và làm việc vất vả. Darya nói, “Mùa hè đó đã thay đổi cuộc đời tôi. Nó mở ra tâm trí của tôi và tôi đã học được rằng tất cả mọi thứ tôi đã biết trước đây về cuộc sống không phải là cách nó được. Có rất nhiều cách suy nghĩ và sống khác nhau, không có tiêu chuẩn hay quy tắc, không có gì phải sợ. Tôi đã học được cách thể hiện bản thân và không sợ bị phán xét - đó là điều quan trọng nhất tôi học được trong vài năm qua. ”</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Vào mùa thu, cô trở về Uzbekistan, một người khác, và nhanh chóng đưa những kỹ năng mới này vào sử dụng tốt. Cô đã sử dụng số tiền cô kiếm được trong mùa hè để giúp trả tiền học đại học. Và cô đã sử dụng sự tự tin mới mẻ của mình để bắt đầu công việc kinh doanh của riêng mình - một công ty gối đặt tên là Korporaciya Podushek. Darya nảy ra ý tưởng đó theo một cách khác thường. Trong khi ở Mỹ, cô đã xem rất nhiều chương trình của Mỹ như  <em>Bạn bè</em>  và  <em>Cách tôi gặp mẹ của bạn</em>  như một cách để cải thiện tiếng Anh của cô, và cô nhận thấy rằng các bộ thường được làm đầy với gối trang trí dễ thương. Đây không phải là thứ mà Darya từng thấy ở nhà ở Uzbekistan - và cô thấy cơ hội của mình để tạo ra một thị trường.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Nếu không có kiến ​​thức về cách làm gối, cô ấy phải làm việc. Thấy nó như một thách thức, cô tự dạy mình. Bắt đầu với những món quà dành cho bạn bè và gia đình, công việc kinh doanh đơn lẻ của cô ấy dần dần phát triển. Lúc đầu, cô ấy bán 10 chiếc gối mỗi tháng cho những người mà cô ấy biết. Bây giờ công ty của cô đang bán 500 đến 900 gối mỗi tháng trên toàn thế giới. Cô ấy thậm chí còn phát triển đủ để thuê một đội ngũ nhân viên làm việc chăm chỉ, những người mà cô ấy được đào tạo và tự quản lý.</span></p>\r\n<br />\r\n<img alt=\"\" src=\"public/images/visa-j1-usa-2.png\" style=\"width: 768px; height: 511px;\" /><br />\r\n \r\n<p><span style=\"font-size:18px;\"><em>Darya với người quản lý công việc và du lịch của mình tại Patio American Grill ở St, Provincetown, MA.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Nếu chúng ta dừng lại ở đây, nó đã là một câu chuyện thành công. Nhưng Darya vẫn có tinh thần của chương trình J-1 bên trong cô, và muốn tiếp tục học hỏi và trải nghiệm những điều mới mẻ. Vì vậy, cô đã thử thách mới và trở về Hoa Kỳ vào tháng 4 năm 2016 trong chương trình Thực tập. Cô lo lắng về việc rời khỏi vùng thoải mái của mình và bắt đầu lại từ đầu, nhưng với kinh nghiệm quá khứ của cô như một người hướng dẫn, cô cũng biết nó sẽ rất tuyệt vời. Darya nói rằng chuyến bay thứ hai tới Mỹ đã thay đổi cuộc sống của cô một lần nữa. Rời khỏi mọi thứ cô tạo ra ở nhà khiến cô thực sự tìm thấy chính mình. Một năm sau, cô ấy là một người mới: mạnh mẽ hơn và dũng cảm hơn.<br />\r\n<br />\r\n<img alt=\"\" src=\"public/images/Western-Australia-University.jpg\" style=\"width: 712px; height: 470px;\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Darya rời California với tình bạn, những kỷ niệm và những câu chuyện sẽ ở lại với cô mãi mãi. Cô ấy nói \"Tôi càng đi nhiều, tôi càng thấy mình - tôi biết mình muốn gì. California đặc biệt cho tôi thấy tầm quan trọng của việc có thể mơ ước. Ở đây tôi 26 tuổi, ngay sau khi thực tập kéo dài một năm của tôi. Tôi dám mơ ước, đầy những kế hoạch và mục tiêu cần đạt được, những người bạn tin vào tôi và truyền cảm hứng cho tôi, và ước mơ - những giấc mơ mà tôi biết sẽ trở thành sự thật sớm đủ.</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Để trở thành một thành viên quốc tế thành công, chúng tôi cần phải dũng cảm, sẵn sàng để mất tất cả mọi thứ chúng tôi có, chúng tôi đã tạo ra, bởi vì tất cả những người tham gia trở về nhà hoàn toàn người khác nhau. Chúng tôi đang tìm kiếm những trải nghiệm và kiến ​​thức mới, nhưng chúng tôi tìm thấy chính mình - và đó là cảm giác tuyệt vời nhất để có thể tìm thấy chính mình. ”</span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Darya trở về nhà với một doanh nghiệp thành công và nhờ vào các kỹ năng đã đạt được trong chương trình thực tập của cô, một giấc mơ mới thú vị về việc tạo ra một doanh nghiệp xây dựng thương hiệu.</span></p>\r\n', 'public/Images/visa-usa.png', 'tu-khach-truy-cap-exchange-den-chu-so-huu-doanh-nghiep', 'Hoàng Hương Lan'),
(35, 'Một cựu sinh viên J1 đang thay đổi thế nào trong cộng đồng của mình', '24', '', '', '2018-05-16', 1, '<p><span style=\"font-size:18px;\">Chào mọi người! Tôi là Alaa Mahmoud đến từ Ai Cập. Tôi hiện đang theo học với tư cách là sinh viên y khoa năm thứ tư tại Đại học Kênh đào Suez, Ai Cập. Vào năm 2006, tôi đã tham gia vào chương trình Du Lịch Làm Việc Mùa Hè J-1 và tham dự Hội Nghị Lãnh Đạo Công Bằng của Hội Đồng Giáo Dục Quốc Tế (CIEE) 2016 (CLS16). Những kinh nghiệm này đã ảnh hưởng đến tôi như một người và khiến tôi trở thành một nhà sản xuất thay đổi.</span></p>\r\n<img alt=\"\" src=\"public/images/visa-j1-1.jpg\" style=\"width: 580px; height: 206px;\" /><br />\r\n \r\n<p><span style=\"font-size:18px;\">Trẻ em tụ tập quanh gia sư của mình trong các buổi học khác nhau trong trại xanh của trẻ em. <em>Trái: Kỹ năng đàm thoại tiếng Anh. | Quyền: Phiên sơ cứu.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Sau khi hoàn thành chương trình J-1 của tôi và tham gia CLS16 nơi tôi có đặc quyền gặp 62 lãnh đạo trẻ từ khắp nơi trên thế giới, tôi đã được truyền cảm hứng để khởi động một tổ chức liên quan đến các vấn đề sức khỏe cộng đồng và môi trường. Trong khi tham dự hội nghị, tôi đã có được những kỹ năng đã tạo cho tôi động lực để tạo ra ExEgypt ( <strong><u>E</u></strong> xchanging & <strong><u>E</u></strong> mpowering <strong><u>G</u></strong> lobal <strong><u>Y</u></strong> outh <strong><u>P</u></strong> otentials & <strong><u>T</u></strong> alents ), một sáng kiến ​​liên quan đến trẻ nhỏ để giúp tạo ra các nhà lãnh đạo trẻ.</span></p>\r\n<img alt=\"\" src=\"public/images/visa-j1-2.jpg\" style=\"width: 768px; height: 315px;\" /><br />\r\n \r\n<p><span style=\"font-size:18px;\"><em>Trái: Hình ảnh lớn của logo ExEgypt. Quyền: Alaa, chủ tịch ExEgypt cầm logo ExEgypt.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Kể từ khi tôi trở về Ai Cập, tôi bắt đầu suy nghĩ với ba đồng nghiệp của mình về cách xây dựng một thứ gì đó có tác động tốt và tạo sự khác biệt — không chỉ trong cộng đồng của chúng ta mà trên toàn thế giới. Vì vậy, chúng tôi đã tìm ra rằng xã hội có nghĩa là tất cả mọi thứ. Đó là lý do tại sao chúng tôi bắt đầu, cách chúng tôi đạt được và người mà chúng tôi muốn ảnh hưởng. Thực hành của chúng tôi hướng đến mọi con người trong xã hội, bắt đầu với trẻ em và kết thúc với người lớn. Chúng tôi mong muốn tăng diện tích xanh, nâng cao nhận thức về ô nhiễm và sức khỏe cộng đồng, và mang đến cho cuộc sống ý tưởng tái chế và nhấn mạnh tầm quan trọng của nó. Chúng tôi đã trình bày ý tưởng này cho các quản trị viên của trường và họ đã ủng hộ chúng tôi, đưa ra một số gợi ý và cho chúng tôi động lực để bắt đầu thực hiện dự án đó trong trường đại học và trong thành phố của chúng tôi.</span></p>\r\n<img alt=\"\" src=\"public/images/visa-j1-3.jpg\" style=\"width: 768px; height: 512px;\" />\r\n<p><span style=\"font-size:18px;\"><em>Trẻ em cho thấy công việc của mình sau khi học những điều cơ bản của việc trồng bởi các gia sư chuyên nghiệp của ExEgypt.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Rất may, nhiều giáo sư đã đề nghị tình nguyện với chúng tôi và làm giám thị của dự án, để đảm bảo rằng nó đã đi theo kế hoạch của chúng tôi. Bạn bè và tôi hoàn toàn chịu trách nhiệm cho trại trẻ em xanh đầu tiên của chúng tôi và chúng tôi đã tổ chức nó bằng tiền riêng của chúng tôi bởi vì chúng tôi tin tưởng vào từng bước duy nhất mà chúng tôi đã thực hiện. Sau khi tác động lớn của trại đầu tiên, nhiều người bắt đầu hỏi về chương trình của chúng tôi và làm thế nào họ có thể giúp chúng tôi, bằng cách đóng góp hoặc tự nguyện. Một câu chuyện cảm động là chúng tôi nhận được một tin nhắn từ một trong những phụ huynh cảm ơn chúng tôi về những gì chúng tôi đã làm với con cái họ, và họ bắt đầu trở nên độc lập hơn và theo lối sống lành mạnh hơn vì trại của chúng tôi.</span></p>\r\n<img alt=\"\" src=\"public/images/visa-j1-4.jpg\" style=\"width: 768px; height: 351px;\" />\r\n<p><span style=\"font-size:18px;\"><em>Trại xây dựng năng lực thứ hai của ExEgypt, nơi tất cả bạn bè và tình nguyện viên <br />\r\ngặp nhau để tìm hiểu thêm về ExEgypt và các hoạt động khác nhau của nó.</em></span></p>\r\n\r\n<p><span style=\"font-size:18px;\">Các hoạt động của ExEgypt bao gồm tổ chức các trại giáo dục cho trẻ em để nâng cao kiến ​​thức về các chủ đề cơ bản như lối sống lành mạnh, sơ cứu và giữ môi trường sạch sẽ bằng cách trồng và tái chế. ExEgypt khuyến khích sinh viên đại học tình nguyện trong các dịch vụ cộng đồng, tổ chức các trại và các sự kiện, và nâng cao nhận thức về các chủ đề có mối quan tâm toàn cầu và phải được chú ý, chẳng hạn như bình đẳng giới và hâm nóng toàn cầu. ExEgypt cũng tập trung vào việc tiến hành các cuộc hội thảo của các giảng viên chuyên nghiệp về các kỹ năng quan trọng — chủ yếu là các kỹ năng lãnh đạo và cách trở thành những người thay đổi. Chúng tôi cũng tổ chức các trường theo mùa vào mùa đông và mùa hè cho sinh viên quốc tế, thực hiện một chương trình y khoa khoa học và một chương trình xã hội cho họ thấy xung quanh Ai Cập.</span></p>\r\n\r\n<p><img alt=\"\" src=\"public/images/visa-j1-5.jpg\" style=\"width: 768px; height: 338px;\" /></p>\r\n\r\n<p><span style=\"font-size:18px;\">ExEgypt nhằm mục đích là hiệp hội có ảnh hưởng nhất liên quan đến các vấn đề môi trường và sức khỏe cộng đồng. Điều này có thể được đo lường bằng cách nhìn thấy tác động của chúng tôi đối với các hành vi của các thế hệ sắp tới. Chúng tôi cũng có kế hoạch để lại một dấu vân tay đáng kể về môi trường bằng cách khôi phục lại nhiều khu vực xanh hơn và sống trong một môi trường lành mạnh hơn<br />\r\n<br />\r\nNguồn : j1visa.state.gov</span></p>\r\n', 'public/Images/visa-j1-3.jpg', 'mot-cuu-sinh-vien-j1-dang-thay-doi-the-nao-trong-cong-dong-cua-minh', 'Hoàng Hương Lan');
INSERT INTO `tt_tintuc` (`Ma`, `Ten`, `MaDanhMuc`, `KeyWord`, `Descr`, `NgayTao`, `TrangThai`, `NoiDung`, `Hinh`, `Alias`, `TacGia`) VALUES
(36, 'Câu hỏi thường gập khi xin Visa EB5', '25', '', '', '2018-05-16', 1, '<div class=\"panel panel-default\">\r\n    <div class=panel-heading>\r\n        <h4 class=\"panel-title font-alt\">\r\n            <a data-toggle=collapse data-parent=#accordion href=#support1 class=collapsed>1. Có nên kết hôn giả để có thẻ xanh Mỹ nhanh và không quá tốn kém?</a>\r\n        </h4>\r\n    </div>\r\n    <div id=support1 class=\"panel-collapse collapse bg-duhoc\">\r\n        <div class=\"panel-body\">\r\n           \r\n           \r\n                <strong>Theo luật, bạn có thể bị tù không quá năm năm hoặc bị phạt $ 250.000, hoặc bị cả hai khi thực hiện hành vi kết hôn giả để trốn tránh các quy định của luật nhập cư. Ngoài ra, bạn có thể bị phạt lên đến $ 10.000 và bị tù năm năm, hoặc bị cả hai vì cố ý làm sai lệch hoặc che giấu sự thật hoặc sử dụng bất kỳ tài liệu sai trong trình đơn này. </strong>\r\n           \r\n        </div>\r\n    </div>\r\n</div>\r\n<div class=\"panel panel-default\">\r\n    <div class=panel-heading>\r\n        <h4 class=\"panel-title font-alt\">\r\n            <a data-toggle=collapse data-parent=#accordion href=#support2 class=collapsed>2. Tôi có thể trở thành công dân Mỹ mà vẫn duy trì được việc kinh doanh ở Việt Nam?</a>\r\n        </h4>\r\n    </div>\r\n    <div id=support2 class=\"panel-collapse collapse bg-duhoc\">\r\n        <div class=\"panel-body\">\r\n           \r\n            \r\n                <strong>Đúng. </strong>\r\n           \r\n        </div>\r\n    </div>\r\n</div>\r\n<div class=\"panel panel-default\">\r\n    <div class=panel-heading>\r\n        <h4 class=\"panel-title font-alt\">\r\n            <a data-toggle=collapse data-parent=#accordion href=#support3 class=collapsed>3. Các con của tôi có thể trở thành công dân Mỹ và được hưởng đầy đủ quyền lợi như công dân bản xứ. Ví dụ như học miễn phí từ tiểu học đến hết phổ thông trung học?</a>\r\n        </h4>\r\n    </div>\r\n    <div id=support3 class=\"panel-collapse collapse bg-duhoc\">\r\n        <div class=\"panel-body\">\r\n           \r\n            \r\n                <strong>Đúng. Con quý vị được hưởng mọi quyền lợi như người bản xứ. </strong>\r\n           \r\n        </div>\r\n    </div>\r\n</div>\r\n<div class=\"panel panel-default\">\r\n    <div class=panel-heading>\r\n        <h4 class=\"panel-title font-alt\">\r\n            <a data-toggle=collapse data-parent=#accordion href=#support4 class=collapsed>4. Tôi có thể có quốc tịch Mỹ mà không phải bỏ quốc tịch Việt Nam?</a>\r\n        </h4>\r\n    </div>\r\n    <div id=support4 class=\"panel-collapse collapse bg-duhoc\">\r\n        <div class=\"panel-body\">\r\n           \r\n            \r\n                <strong>Đúng. </strong>\r\n           \r\n        </div>\r\n    </div>\r\n</div>\r\n<div class=\"panel panel-default\">\r\n    <div class=panel-heading>\r\n        <h4 class=\"panel-title font-alt\">\r\n            <a data-toggle=collapse data-parent=#accordion href=#support5 class=collapsed>5. Tôi có thể xin định cư Mỹ và trở thành công dân hợp pháp của họ mà không cần người thân bảo lãnh?</a>\r\n        </h4>\r\n    </div>\r\n    <div id=support5 class=\"panel-collapse collapse bg-duhoc\">\r\n        <div class=\"panel-body\">\r\n           \r\n           \r\n                <strong>Đúng. Ngoài hình thức bảo lãnh thân nhân quý vi có để định cư ở Mỹ thông qua hình thức đầu tư qua các chương trình đầu tư EB-5, đầu tư L-1.</strong>\r\n          \r\n        </div>\r\n    </div>\r\n</div>\r\n<div class=\"panel panel-default\">\r\n    <div class=panel-heading>\r\n        <h4 class=\"panel-title font-alt\">\r\n            <a data-toggle=collapse data-parent=#accordion href=#support6 class=collapsed>6. Nếu đương đơn bắt buộc phải mua thương mại trước rồi mới tiến hành làm hồ sơ xin L-1 visa, không may bị bác đơn vì tiền đã mua thương mại rồi thì biết phải làm sao?</a>\r\n        </h4>\r\n    </div>\r\n    <div id=support6 class=\"panel-collapse collapse bg-duhoc\">\r\n        <div class=\"panel-body\">\r\n           \r\n           \r\n                <strong>Luật sư chúng tôi sẽ giúp thân chủ thảo hợp đồng mua thương mại đặt điều kiện rằng người mua chỉ thanh toán tiền cho người bán khi người mua nhận visa tại Việt Nam. Nếu đơn bị bác hợp đồng mua bán sẽ trở nên vô hiệu, người bán phải trả lại tiền đặt cọc cho người mua.\r\n(Note: ví dụ business giá 500 ngàn USD thì tiền cọc khoảng 20-50 ngàn USD tuỳ thương lượng.)</strong>\r\n          \r\n        </div>\r\n    </div>\r\n</div>\r\n<div class=\"panel panel-default\">\r\n    <div class=panel-heading>\r\n        <h4 class=\"panel-title font-alt\">\r\n            <a data-toggle=collapse data-parent=#accordion href=#support7 class=collapsed>7. Quyền lợi của người giữ thẻ xanh được những gì?</a>\r\n        </h4>\r\n    </div>\r\n    <div id=support7 class=\"panel-collapse collapse bg-duhoc\">\r\n        <div class=\"panel-body\">\r\n           \r\n            \r\n                <strong>•       Ra vào Mỹ không cần xin visa; Sinh sống, làm việc tại bất kỳ nơi nào tại Mỹ;   Con cái được học hệ thống trường công miễn phí; Cơ hội việc làm ngang với người bản xứ</strong>\r\n            \r\n        </div>\r\n    </div>\r\n</div>\r\n<div class=\"panel panel-default\">\r\n    <div class=panel-heading>\r\n        <h4 class=\"panel-title font-alt\">\r\n            <a data-toggle=collapse data-parent=#accordion href=#support8 class=collapsed>8. Quyền lợi của người giữ thẻ xanh có điều kiện và không điều kiện có khác nhau không?</a>\r\n        </h4>\r\n    </div>\r\n    <div id=support8 class=\"panel-collapse collapse bg-duhoc\">\r\n        <div class=\"panel-body\">\r\n           \r\n           \r\n                <strong>Không</strong>\r\n           \r\n        </div>\r\n    </div>\r\n</div>', '', 'cau-hoi-thuong-gap-khi-xin-visa-eb5', 'Hoàng Hương Lan'),
(37, 'Câu hỏi thường gập L1-A', '26', '', '', '2018-05-16', 1, '<div class=\"panel panel-default\">\r\n    <div class=panel-heading>\r\n        <h4 class=\"panel-title font-alt\">\r\n            <a data-toggle=collapse data-parent=#accordion href=#support1 class=collapsed>1. Tôi có thể thay đổi địa điểm làm việc của tôi khi tôi ở trong tình trạng L-1 không?</a>\r\n        </h4>\r\n    </div>\r\n    <div id=support1 class=\"panel-collapse collapse bg-duhoc\">\r\n        <div class=\"panel-body\">\r\n           \r\n           \r\n                <strong>Có, nếu bạn đang đảm nhận một vị trí có các nhiệm vụ tương tự với cùng một chủ nhân. Nhưng bạn phải nộp đơn sửa đổi và thông báo cho Dịch vụ Di trú và Nhập tịch Hoa Kỳ (USCIS) về thay đổi này. </strong>\r\n           \r\n        </div>\r\n    </div>\r\n</div>\r\n<div class=\"panel panel-default\">\r\n    <div class=panel-heading>\r\n        <h4 class=\"panel-title font-alt\">\r\n            <a data-toggle=collapse data-parent=#accordion href=#support2 class=collapsed>2.Tôi có thể thay đổi công việc và làm việc cho một chi nhánh khác của Hoa Kỳ của cùng một tập đoàn nước ngoài không?</a>\r\n        </h4>\r\n    </div>\r\n    <div id=support2 class=\"panel-collapse collapse bg-duhoc\">\r\n        <div class=\"panel-body\">\r\n           \r\n            \r\n                <strong>Không. Nếu bạn định làm việc cho một công ty khác, đơn vị liên kết khác cần nộp đơn yêu cầu visa L-1 độc lập cho bạn. Bạn không thể sử dụng L-1 ban đầu của bạn để làm việc cho công ty khác, mặc dù nó là một công ty liên quan. </strong>\r\n           \r\n        </div>\r\n    </div>\r\n</div>\r\n<div class=\"panel panel-default\">\r\n    <div class=panel-heading>\r\n        <h4 class=\"panel-title font-alt\">\r\n            <a data-toggle=collapse data-parent=#accordion href=#support3 class=collapsed>3. Thị thực L-1 của tôi đã được chấp thuận! Tôi vui mừng nhưng cũng muốn bắt đầu suy nghĩ trước. Thời gian tối đa tôi có thể làm việc ở Hoa Kỳ dưới tình trạng L-1 là bao lâu?</a>\r\n        </h4>\r\n    </div>\r\n    <div id=support3 class=\"panel-collapse collapse bg-duhoc\">\r\n        <div class=\"panel-body\">\r\n           \r\n            \r\n                <strong>Tốt cho bạn, và nó cũng tuyệt vời như bạn đang nhìn lâu. Câu trả lời cho câu hỏi của bạn phụ thuộc vào loại thị thực L-1 đã được phê duyệt: L-1A hoặc L-1B. Đối với thị thực L-1A, thời hạn tối đa là bảy năm; cho L-1B, đó là năm năm. Sự chấp thuận ban đầu thường là ba năm hoặc ít hơn, tuy nhiên, thời hạn ban đầu của một thị thực L-1A được sử dụng để thành lập văn phòng mới chỉ là một năm. vì vậy bạn có thể cần phải xin gia hạn trước khi visa hiện tại của bạn hết hạn. </strong>\r\n           \r\n        </div>\r\n    </div>\r\n</div>\r\n<div class=\"panel panel-default\">\r\n    <div class=panel-heading>\r\n        <h4 class=\"panel-title font-alt\">\r\n            <a data-toggle=collapse data-parent=#accordion href=#support4 class=collapsed>4. Công ty tôi đang xem xét visa L-1A cho tôi để làm việc tại chi nhánh Hoa Kỳ của chúng tôi, và tôi đã được yêu cầu cung cấp bằng chứng về trình độ học vấn của mình. Tôi lo lắng vì tôi thực sự không bao giờ đi học đại học. Tôi vẫn có đủ điều kiện để được thị thực L-1A? Việc thiếu bằng cử nhân làm cho trường hợp của tôi yếu đi?</a>\r\n        </h4>\r\n    </div>\r\n    <div id=support4 class=\"panel-collapse collapse bg-duhoc\">\r\n        <div class=\"panel-body\">\r\n           \r\n            \r\n                <strong> Bạn vẫn có thể hội đủ điều kiện cho L-1 mà không có bằng cử nhân, miễn là bạn đáp ứng các yêu cầu của loại . Không có yêu cầu giáo dục tối thiểu đối với L-1A. Thật vậy, giáo dục chỉ là một trong nhiều yếu tố mà USCIS sẽ cân nhắc khi xác định xem liệu bạn có là người quản lý hay không là người quản lý, đó là vị trí đủ điều kiện cho nhân viên của L-1A . </strong>\r\n           \r\n        </div>\r\n    </div>\r\n</div>\r\n<div class=\"panel panel-default\">\r\n    <div class=panel-heading>\r\n        <h4 class=\"panel-title font-alt\">\r\n            <a data-toggle=collapse data-parent=#accordion href=#support5 class=collapsed>5. Công ty tôi đang mở một chi nhánh mới ở Mỹ, và hội đồng quản trị muốn gửi tôi đến đó để giám sát hoạt động của chúng tôi. Chúng tôi đang xem xét kiến ​​nghị xin thị thực L-1A, nhưng dường như khó có thể chứng minh rằng vị trí tương lai của tôi có đủ điều kiện vì hiện tại chúng tôi chỉ có một nhân viên tại chi nhánh mới. Liệu tôi vẫn có thể hội đủ điều kiện như một người hưởng lợi L-1A?</a>\r\n        </h4>\r\n    </div>\r\n    <div id=support5 class=\"panel-collapse collapse bg-duhoc\">\r\n        <div class=\"panel-body\">\r\n           \r\n           \r\n                <strong>Vâng. Trường hợp của bạn thuộc loại “văn phòng mới”, có một bộ quy tắc cụ thể.</strong>\r\n          \r\n        </div>\r\n    </div>\r\n</div>', '', 'cau-hoi-thuong-gap-l1-a', 'Hoàng Hương Lan'),
(38, 'Câu hỏi thường gặp EB3', '27', '', '', '2018-05-16', 1, '<div class=\"panel panel-default\">\r\n<div class=\"panel-heading\">\r\n<h4 class=\"panel-title font-alt\"><a class=\"collapsed\" data-parent=\"#accordion\" data-toggle=\"collapse\" href=\"#support1\">1. Có phải tất cả thành viên trong gia đình đương đơn đều được cấp thị thực Thường trú nhân Hoa Kỳ không ? Và nếu có thì cấp như thế nào?</a></h4>\r\n</div>\r\n\r\n<div class=\"panel-collapse collapse bg-duhoc\" id=\"support1\">\r\n<div class=\"panel-body\"><strong>Sau khi đương đơn được cấp thị thực giấy phép lao động (work-permit visa) thì tất cả những thành viên trực hệ với đương đơn cũng sẽ được cấp thị thực Thường trú nhân (permanent resident visa). Nếu đương đơn có con trên 21 tuổi, thì những người con đó được xếp vào thành viên độc lập và trong trường hợp này thì phải nộp riêng một hồ sơ cho đương sự. </strong></div>\r\n</div>\r\n</div>\r\n\r\n<div class=\"panel panel-default\">\r\n<div class=\"panel-heading\">\r\n<h4 class=\"panel-title font-alt\"><a class=\"collapsed\" data-parent=\"#accordion\" data-toggle=\"collapse\" href=\"#support2\">2.Nhân công làm việc tại các công ty Hoa Kỳ sẽ nhận được những phúc lợi gì?</a></h4>\r\n</div>\r\n\r\n<div class=\"panel-collapse collapse bg-duhoc\" id=\"support2\">\r\n<div class=\"panel-body\"><strong>Những phúc lợi cho các nhân công là: Bảo hiểm sức khỏe, nhân thọ, tiếp tục được trả lương trong trường hợp bị thương tích không thể tiếp tục làm việc, trả lương cho các ngày nghỉ hè và ngày lễ, v.v. </strong></div>\r\n</div>\r\n</div>\r\n\r\n<div class=\"panel panel-default\">\r\n<div class=\"panel-heading\">\r\n<h4 class=\"panel-title font-alt\"><a class=\"collapsed\" data-parent=\"#accordion\" data-toggle=\"collapse\" href=\"#support3\">3.Người lao động diện EB3 cần có những điều kiện và tư cách cần thiết gì? </a></h4>\r\n</div>\r\n\r\n<div class=\"panel-collapse collapse bg-duhoc\" id=\"support3\">\r\n<div class=\"panel-body\"><strong>Những ứng viên tuyển dụng thích hợp phải có điều kiện thể chất phù hợp và trên 21 tuổi, và cũng có thể cần thêm một số điều kiện khác như kiến thức, năng khiếu, v.v., nhưng quan trọng là những ứng viên tuyển dụng thích hợp phải không từng phạm pháp và không mắc bệnh truyền nhiễm. Không là thành viên đảng cộng sản.</strong></div>\r\n</div>\r\n</div>\r\n\r\n<div class=\"panel panel-default\">\r\n<div class=\"panel-heading\">\r\n<h4 class=\"panel-title font-alt\"><a class=\"collapsed\" data-parent=\"#accordion\" data-toggle=\"collapse\" href=\"#support4\">4. Cần mang bao nhiêu tiền cho bước khởi đầu định cư tại Hoa Kỳ?</a></h4>\r\n</div>\r\n\r\n<div class=\"panel-collapse collapse bg-duhoc\" id=\"support4\">\r\n<div class=\"panel-body\"><strong>Nhìn chung, tiền lương tại công ty địa phương cũng đủ cho ứng viên trang trải trong cuộc sống hàng ngày, cho nên ứng viên không nhất thiết phải mang theo một số tiền lớn. Tuy nhiên, ứng viên cũng nên chuẩn bị một số tiền cần dùng trong trường họp khẩn cấp. </strong></div>\r\n</div>\r\n</div>\r\n\r\n<div class=\"panel panel-default\">\r\n<div class=\"panel-heading\">\r\n<h4 class=\"panel-title font-alt\"><a class=\"collapsed\" data-parent=\"#accordion\" data-toggle=\"collapse\" href=\"#support5\">5. Thị thực thường trú nhân Hoa Kỳ được cấp phát từ đâu?</a></h4>\r\n</div>\r\n\r\n<div class=\"panel-collapse collapse bg-duhoc\" id=\"support5\">\r\n<div class=\"panel-body\"><strong>Vấn đề này thường bị nhầm lẫn khá nhiều. Thị thực thường trú nhân Hoa Kỳ được cấp phát trực tiếp từ Lãnh sự quán hay Đại sứ quán Hoa Kỳ tại quốc gia đương đơn đang cư trú và cho cả thân nhân của đương đơn. Đây là thị thực chính thức (official visa) chứ không phải là thẻ tạm thời (amendment card) được cấp phát bởi các công ty môi giới di trú (Immigration brokerage firms)</strong></div>\r\n</div>\r\n</div>\r\n', '', 'cau-hoi-thuong-gap-eb3', 'Hoàng Hương Lan'),
(39, 'Câu hỏi thường gặp J1', '28', '', '', '2018-05-16', 1, '<div class=\"panel panel-default\">\r\n    <div class=panel-heading>\r\n        <h4 class=\"panel-title font-alt\">\r\n            <a data-toggle=collapse data-parent=#accordion href=#support1 class=collapsed>1.Điều kiện tham gia chương trình là gì? Trình độ&nbsp;tiếng Anh&nbsp;đòi hỏi bằng gì?</a>\r\n        </h4>\r\n    </div>\r\n    <div id=support1 class=\"panel-collapse collapse bg-duhoc\">\r\n        <div class=\"panel-body\">\r\n           \r\n           \r\n                <strong>Điều kiện tham gia là sinh viên sắp tốt nghiệp hoặc người đi làm, thuộc các ngành kinh tế, thương mại, công nghệ thông tin, đồ họa, xây dựng, kế toán, nhà hàng khách sạn, du lịch, nông nghiệp... Chi tiết cụ thể mời xem mục Đối tượng ở đầu trang. Nhìn chung, yếu tố tiên quyết để tham gia chương trình là trình độ tiếng Anh tốt&nbsp;</strong>\r\n           \r\n        </div>\r\n    </div>\r\n</div>\r\n<div class=\"panel panel-default\">\r\n    <div class=panel-heading>\r\n        <h4 class=\"panel-title font-alt\">\r\n            <a data-toggle=collapse data-parent=#accordion href=#support2 class=collapsed>2.Có được chọn công việc không, sẽ được công ty nào nhận vào làm? Có hợp đồng lao động không?</a>\r\n        </h4>\r\n    </div>\r\n    <div id=support2 class=\"panel-collapse collapse bg-duhoc\">\r\n        <div class=\"panel-body\">\r\n           \r\n            \r\n                <strong>Do đây không phải là chương trình tuyển dụng nên không có danh sách các vị trí cần tuyển, mà chương trình sẽ tiến hành xếp chỗ thực tập đào tạo dựa trên ngành học và kinh nghiệm làm việc của người tham gia. </strong>\r\n           \r\n        </div>\r\n    </div>\r\n</div>\r\n<div class=\"panel panel-default\">\r\n    <div class=panel-heading>\r\n        <h4 class=\"panel-title font-alt\">\r\n            <a data-toggle=collapse data-parent=#accordion href=#support3 class=collapsed>3.Thời gian tham gia chương trình có được chọn không?</a>\r\n        </h4>\r\n    </div>\r\n    <div id=support3 class=\"panel-collapse collapse bg-duhoc\">\r\n        <div class=\"panel-body\">\r\n           \r\n            \r\n                <strong>Được. Trong hợp đồng tham gia và trong hồ sơ đăng ký, người tham gia hãy nêu rõ thời điểm bắt đầu chương trình và độ dài chương trình mong muốn.</strong>\r\n           \r\n        </div>\r\n    </div>\r\n</div>\r\n<div class=\"panel panel-default\">\r\n    <div class=panel-heading>\r\n        <h4 class=\"panel-title font-alt\">\r\n            <a data-toggle=collapse data-parent=#accordion href=#support4 class=collapsed>4. Tỉ lệ đậu visa cao không?</a>\r\n        </h4>\r\n    </div>\r\n    <div id=support4 class=\"panel-collapse collapse bg-duhoc\">\r\n        <div class=\"panel-body\">\r\n           \r\n            \r\n                <strong> <p>\r\n                    Tỉ lệ đậu visa là 90 - 100%.\r\n                </p><p>\r\n                    Ngoài yếu tố người tham gia đủ điều kiện tham gia chương trình, nhân viên lãnh sự còn xét cấp visa dựa trên nhiều yếu tố khác như: trình độ tiếng Anh, quá trình công tác học tập, mối ràng buộc ở Việt Nam, có người thân ở Hoa Kỳ hay không, từng có hồ sơ định cư hay không, điều kiện tài chính... Do đó việc được cấp visa còn tùy thuộc nhiều vào điều kiện riêng của từng người. Nhưng nhìn chung, chương trình có tỉ lệ đậu visa cao so với du học, du lịch tự túc bởi đây là chương trình mang tính hệ thống, có Tổ chức bảo trợ, có công ty nhận\r\n                </p> </strong>\r\n           \r\n        </div>\r\n    </div>\r\n</div>\r\n<div class=\"panel panel-default\">\r\n    <div class=panel-heading>\r\n        <h4 class=\"panel-title font-alt\">\r\n            <a data-toggle=collapse data-parent=#accordion href=#support5 class=collapsed>5.Việc ăn ở bên đó thế nào?</a>\r\n        </h4>\r\n    </div>\r\n    <div id=support5 class=\"panel-collapse collapse bg-duhoc\">\r\n        <div class=\"panel-body\">\r\n           \r\n           \r\n                <strong>Sau khi được xếp chỗ thực tập, nếu người tham gia yêu cầu, Tổ chức bảo trợ sẽ tìm nơi ở với phí dịch vụ là 150 USD. Tiền thuê nhà hàng tháng và các chi phí sinh hoạt khác như đi lại, ăn uống, giặt giũ... sẽ do người tham gia tự chi trả. Thông thường, tiền thuê nhà là 350 - 600 USD tùy bang, tiền ăn uống, đi lại, điện thoại, nhu yếu phẩm... là 400 - 550 USD, tổng cộng khoảng 800 - 1.000 USD/tháng. Bang nào chi phí đắt đỏ thì thù lao cũng sẽ cao theo.\r\n<br>\r\nĐôi khi công ty nhận thực tập đào tạo có cung cấp nơi ở, có hoặc không có thu phí. Điều này, nếu có, sẽ được nêu rõ trong thông báo về chỗ thực tập đào tạo.\r\n                </strong>\r\n          \r\n        </div>\r\n    </div>\r\n</div>\r\n<div class=\"panel panel-default\">\r\n    <div class=panel-heading>\r\n        <h4 class=\"panel-title font-alt\">\r\n            <a data-toggle=collapse data-parent=#accordion href=#support6 class=collapsed>6. Chương trình có còn không?</a>\r\n        </h4>\r\n    </div>\r\n    <div id=support6 class=\"panel-collapse collapse bg-duhoc\">\r\n        <div class=\"panel-body\">\r\n           \r\n           \r\n                <strong>Có. Chương trình nhận hồ sơ quanh năm và không có mùa cao điểm hay thấp điểm.</strong>\r\n          \r\n        </div>\r\n    </div>\r\n</div>\r\n<div class=\"panel panel-default\">\r\n    <div class=panel-heading>\r\n        <h4 class=\"panel-title font-alt\">\r\n            <a data-toggle=collapse data-parent=#accordion href=#support7 class=collapsed>7. Rớt visa được hoàn bao nhiêu tiền đã đóng?</a>\r\n        </h4>\r\n    </div>\r\n    <div id=support7 class=\"panel-collapse collapse bg-duhoc\">\r\n        <div class=\"panel-body\">\r\n           \r\n            \r\n                <strong>Mỗi Tổ chức bảo trợ có mỗi chính sách hoàn tiền khác nhau. Liên hệ NTT để có thông tin chi tiết. </strong>\r\n            \r\n        </div>\r\n    </div>\r\n</div>', '', 'cau-hoi-thuong-gap-j1', 'Hoàng Hương Lan');

-- --------------------------------------------------------

--
-- Cấu trúc bảng cho bảng `t_banner`
--

